import * as vscode from "vscode";
import { Logger, LogLevel } from "./utils/Logger";
import { MemoryMapEditorProvider } from "./providers/MemoryMapEditorProvider";
import { IpCoreEditorProvider } from "./providers/IpCoreEditorProvider";
import {
  createIpCoreCommand,
  createMemoryMapCommand,
  createIpCoreWithMemoryMapCommand,
} from "./commands/FileCreationCommands";
import { registerGeneratorCommands } from "./commands/GenerateCommands";

/**
 * Register a command, ignoring "already exists" errors that occur when the
 * extension host restarts without a full deactivation cycle.
 */
function safeRegisterCommand(
  context: vscode.ExtensionContext,
  command: string,
  handler: (...args: any[]) => any,
): void {
  try {
    context.subscriptions.push(
      vscode.commands.registerCommand(command, handler),
    );
  } catch {
    // Command was already registered by a previous (stale) activation
  }
}

/**
 * Extension activation entry point
 */
export function activate(context: vscode.ExtensionContext): void {
  // Initialize logging
  Logger.initialize("FPGA Memory Map & IP Core Editor", LogLevel.INFO);
  const logger = new Logger("Extension");
  logger.info("Extension activating");

  // Register Memory Map custom editor provider
  try {
    context.subscriptions.push(
      vscode.window.registerCustomEditorProvider(
        "fpgaMemoryMap.editor",
        new MemoryMapEditorProvider(context),
        {
          webviewOptions: {
            retainContextWhenHidden: true,
          },
          supportsMultipleEditorsPerDocument: false,
        },
      ),
    );
    logger.info("Memory Map editor registered");
  } catch (e) {
    logger.warn("fpgaMemoryMap.editor already registered – skipping");
  }

  // Register IP Core custom editor provider
  try {
    context.subscriptions.push(
      vscode.window.registerCustomEditorProvider(
        "fpgaIpCore.editor",
        new IpCoreEditorProvider(context),
        {
          webviewOptions: {
            retainContextWhenHidden: true,
          },
          supportsMultipleEditorsPerDocument: false,
        },
      ),
    );
    logger.info("IP Core editor registered");
  } catch (e) {
    logger.warn("fpgaIpCore.editor already registered – skipping");
  }

  // Register File Creation Commands
  safeRegisterCommand(context, "fpga-ip-core.createIpCore", createIpCoreCommand);
  safeRegisterCommand(context, "fpga-ip-core.createMemoryMap", createMemoryMapCommand);
  safeRegisterCommand(context, "fpga-ip-core.createIpCoreWithMemoryMap", createIpCoreWithMemoryMapCommand);

  // Register VHDL Generator Commands
  registerGeneratorCommands(context);
  logger.info("Generator commands registered");

  logger.info("Extension activated successfully");
}

/**
 * Extension deactivation cleanup
 */
export function deactivate(): void {
  const logger = new Logger("Extension");
  logger.info("Extension deactivating");
  Logger.dispose();
}

import * as fs from 'fs/promises';
import * as path from 'path';
import * as yaml from 'js-yaml';
import * as vscode from 'vscode';
import { Logger } from '../utils/Logger';
import { BusLibraryService } from '../services/BusLibraryService';
import { TemplateLoader } from './TemplateLoader';
import { GenerateOptions, GenerateResult } from './types';

type BusDefinition = {
  ports?: Array<{
    name: string;
    width?: number;
    direction?: string;
    presence?: string;
  }>;
};

type BusDefinitions = Record<string, BusDefinition>;

export interface VlnvDef {
  vendor?: string;
  library?: string;
  name?: string;
  version?: string;
}

export interface BusInterfaceDef {
  name?: string;
  type?: string;
  mode?: string;
  physicalPrefix?: string;
  physical_prefix?: string;
  useOptionalPorts?: string[];
  use_optional_ports?: string[];
  portWidthOverrides?: Record<string, number>;
  port_width_overrides?: Record<string, number>;
  associatedClock?: string;
  associated_clock?: string;
  associatedReset?: string;
  associated_reset?: string;
  array?: {
    count?: number;
    indexStart?: number;
    index_start?: number;
    namingPattern?: string;
    naming_pattern?: string;
    physicalPrefixPattern?: string;
    physical_prefix_pattern?: string;
  };
  ports?: Array<Record<string, unknown>>;
  [key: string]: unknown;
}

export interface IpCoreData {
  vlnv?: VlnvDef;
  description?: string;
  parameters?: Array<{
    name?: string;
    value?: number | string;
    data_type?: string;
    dataType?: string;
  }>;
  ports?: Array<{
    name?: string;
    direction?: string;
    width?: number | string;
    presence?: string;
  }>;
  busInterfaces?: BusInterfaceDef[];
  bus_interfaces?: BusInterfaceDef[];
  clocks?: Array<{ name?: string }>;
  resets?: Array<{ name?: string; polarity?: string }>;
  memoryMaps?: Record<string, unknown> | Record<string, unknown>[];
  memory_maps?: Record<string, unknown> | Record<string, unknown>[];
  [key: string]: unknown;
}

export class IpCoreScaffolder {
  private readonly logger: Logger;
  private readonly templates: TemplateLoader;
  private readonly busLibraryService: BusLibraryService;
  private busDefinitions: BusDefinitions | null = null;

  private static readonly BUS_TYPE_MAP: Record<string, string> = {
    AXI4L: 'axil',
    AXI4LITE: 'axil',
    AXILITE: 'axil',
    AVALONMM: 'avmm',
    AVMM: 'avmm',
    AVALON_MM: 'avmm',
    'AVALON-MM': 'avmm',
  };

  constructor(logger: Logger, templates: TemplateLoader, context: vscode.ExtensionContext) {
    this.logger = logger;
    this.templates = templates;
    this.busLibraryService = new BusLibraryService(logger, context);
  }

  async generateAll(
    inputPath: string,
    outputDir: string,
    options: GenerateOptions = {}
  ): Promise<GenerateResult> {
    try {
      await this.ensureBusDefinitions();
      const ipCoreData = await this.loadIpCore(inputPath);
      const busType = this.getBusType(ipCoreData);
      const context = await this.buildTemplateContext(ipCoreData, busType, inputPath);
      const includeRegs = options.includeRegs !== false;
      const includeTestbench = options.includeTestbench === true;
      const vendor = options.vendor ?? 'both';
      const includeVhdl = options.includeVhdl !== false;

      const files: Record<string, string> = {};
      const name = String(ipCoreData?.vlnv?.name ?? 'ip_core').toLowerCase();

      if (includeVhdl) {
        files[`rtl/${name}_pkg.vhd`] = this.templates.render('package.vhdl.j2', context);
        files[`rtl/${name}.vhd`] = this.templates.render('top.vhdl.j2', context);
        files[`rtl/${name}_core.vhd`] = this.templates.render('core.vhdl.j2', context);
        files[`rtl/${name}_${busType}.vhd`] = this.templates.render(
          `bus_${busType}.vhdl.j2`,
          context
        );
      }

      if (includeRegs) {
        files[`rtl/${name}_regs.vhd`] = this.templates.render('register_file.vhdl.j2', context);
      }

      if (includeTestbench) {
        files[`tb/${name}_test.py`] = this.templates.render('cocotb_test.py.j2', context);
        files['tb/Makefile'] = this.templates.render('cocotb_makefile.j2', context);
      }

      if (vendor === 'intel' || vendor === 'both') {
        files[`intel/${name}_hw.tcl`] = this.templates.render('intel_hw_tcl.j2', context);
      }

      if (vendor === 'xilinx' || vendor === 'both') {
        files['xilinx/component.xml'] = this.templates.render('xilinx_component_xml.j2', context);
        const versionStr = String(ipCoreData?.vlnv?.version ?? '1.0').replace(/\./g, '_');
        files[`xilinx/xgui/${name}_v${versionStr}.tcl`] = this.templates.render(
          'xilinx_xgui.j2',
          context
        );
      }

      const written: Record<string, string> = {};
      await Promise.all(
        Object.entries(files).map(async ([relativePath, content]) => {
          const fullPath = path.join(outputDir, relativePath);
          await fs.mkdir(path.dirname(fullPath), { recursive: true });
          await fs.writeFile(fullPath, content, 'utf8');
          written[relativePath] = fullPath;
        })
      );

      this.logger.info('Generated VHDL files', {
        count: Object.keys(written).length,
        busType,
        outputDir,
      });

      return {
        success: true,
        files: written,
        count: Object.keys(written).length,
        busType,
      };
    } catch (error) {
      this.logger.error('VHDL generation failed', error as Error);
      return {
        success: false,
        error: error instanceof Error ? error.message : String(error),
      };
    }
  }

  private async ensureBusDefinitions(): Promise<void> {
    if (this.busDefinitions) {
      return;
    }
    const library = await this.busLibraryService.loadDefaultLibrary();
    this.busDefinitions = (library || {}) as BusDefinitions;
  }

  private async loadIpCore(inputPath: string): Promise<IpCoreData> {
    const content = await fs.readFile(inputPath, 'utf8');
    const parsed = yaml.load(content);
    if (!parsed || typeof parsed !== 'object') {
      throw new Error('Invalid IP core YAML');
    }
    return parsed as IpCoreData;
  }

  private getBusType(ipCore: IpCoreData): string {
    const busInterfaces = ipCore?.busInterfaces ?? ipCore?.bus_interfaces ?? [];

    for (const bus of busInterfaces) {
      const mode = this.getString(bus?.mode);
      if (mode === 'slave') {
        const rawType = this.getString(bus?.type).toUpperCase();
        return IpCoreScaffolder.BUS_TYPE_MAP[rawType] || 'axil';
      }
    }

    return 'axil';
  }

  private async buildTemplateContext(
    ipCore: IpCoreData,
    busType: string,
    inputPath: string
  ): Promise<Record<string, unknown>> {
    const name = String(ipCore?.vlnv?.name ?? 'ip_core').toLowerCase();
    const registers = await this.prepareRegisters(ipCore, inputPath);
    const swAccess = new Set(['read-write', 'write-only', 'rw', 'wo']);
    const hwAccess = new Set(['read-only', 'ro']);

    const swRegisters = registers.filter((reg) => swAccess.has(this.getString(reg.access)));
    const hwRegisters = registers.filter((reg) => hwAccess.has(this.getString(reg.access)));

    const clocks = ipCore?.clocks ?? [];
    const resets = ipCore?.resets ?? [];
    const clockPort = clocks[0]?.name ?? 'clk';
    const resetPort = resets[0]?.name ?? 'rst';
    const resetPolarity = this.getString(resets[0]?.polarity ?? 'activeHigh');
    const resetActiveHigh = resetPolarity.toLowerCase().includes('high');

    const expandedBusInterfaces = this.expandBusInterfaces(ipCore);
    const busPorts: Array<Record<string, unknown>> = [];
    const secondaryBusPorts: Array<Record<string, unknown>> = [];
    let busPrefix = 's_axi';

    if (expandedBusInterfaces.length > 0) {
      const primary = expandedBusInterfaces[0];
      busPrefix = this.normalizePrefix(primary.physical_prefix ?? primary.physicalPrefix ?? '');

      expandedBusInterfaces.forEach((iface, index) => {
        const busTypeKey = this.normalizeBusTypeKey(iface.type ?? '');
        const activePorts = this.getActiveBusPorts(
          busTypeKey,
          iface.use_optional_ports ?? [],
          iface.physical_prefix ?? '',
          iface.mode ?? '',
          iface.port_width_overrides ?? {}
        );
        iface.ports = activePorts;
        if (index === 0) {
          busPorts.push(...activePorts);
        } else {
          secondaryBusPorts.push(...activePorts);
        }
      });
    }

    return {
      entity_name: name,
      registers: registers,
      sw_registers: swRegisters,
      hw_registers: hwRegisters,
      generics: this.prepareGenerics(ipCore),
      user_ports: this.prepareUserPorts(ipCore),
      bus_type: busType,
      bus_ports: busPorts,
      secondary_bus_ports: secondaryBusPorts,
      expanded_bus_interfaces: expandedBusInterfaces,
      bus_prefix: expandedBusInterfaces.length > 0 ? busPrefix : 's_axi',
      data_width: 32,
      addr_width: 8,
      reg_width: 4,
      memory_maps: (await this.resolveMemoryMaps(ipCore, inputPath)) ?? [],
      clock_port: clockPort,
      reset_port: resetPort,
      reset_active_high: resetActiveHigh,
      memmap_relpath: `../../${name}.mm.yml`,
      vendor: ipCore?.vlnv?.vendor,
      library: ipCore?.vlnv?.library,
      version: ipCore?.vlnv?.version,
      description: ipCore?.description ?? '',
      author: ipCore?.vlnv?.vendor,
      display_name: String(ipCore?.vlnv?.name ?? 'ip_core')
        .replace(/_/g, ' ')
        .replace(/\b\w/g, (letter) => letter.toUpperCase()),
    };
  }

  private prepareGenerics(ipCore: IpCoreData): Array<Record<string, unknown>> {
    const params = ipCore?.parameters ?? [];
    return params.map((param) => ({
      name: param.name,
      type: this.getString(param.data_type ?? param.dataType),
      default_value: param.value,
    }));
  }

  private prepareUserPorts(ipCore: IpCoreData): Array<Record<string, unknown>> {
    const params = ipCore?.parameters ?? [];
    const paramDefaults = new Map<string, number>();
    params.forEach((param) => {
      if (param?.name && param?.value !== undefined) {
        paramDefaults.set(String(param.name), Number(param.value));
      }
    });

    const ports = ipCore?.ports ?? [];
    return ports.map((port) => {
      const direction = this.getString(port.direction).toLowerCase();
      const widthValue = port.width ?? 1;
      const isParameterized = typeof widthValue === 'string';
      if (isParameterized) {
        const defaultWidth = (paramDefaults.get(widthValue) ?? 32) - 1;
        return {
          name: String(port.name).toLowerCase(),
          direction,
          type: `std_logic_vector(${widthValue}-1 downto 0)`,
          width: null,
          width_expr: widthValue,
          is_parameterized: true,
          default_width: defaultWidth,
        };
      }

      const width = Number(widthValue);
      if (width === 1) {
        return {
          name: String(port.name).toLowerCase(),
          direction,
          type: 'std_logic',
          width: 1,
          width_expr: null,
          is_parameterized: false,
          default_width: null,
        };
      }

      return {
        name: String(port.name).toLowerCase(),
        direction,
        type: `std_logic_vector(${width - 1} downto 0)`,
        width,
        width_expr: null,
        is_parameterized: false,
        default_width: null,
      };
    });
  }

  private expandBusInterfaces(ipCore: IpCoreData): BusInterfaceDef[] {
    const busInterfaces = ipCore?.busInterfaces ?? ipCore?.bus_interfaces ?? [];
    if (!Array.isArray(busInterfaces)) {
      return [];
    }

    const expanded: BusInterfaceDef[] = [];
    for (const iface of busInterfaces) {
      const arrayDef = iface?.array;
      if (arrayDef) {
        const count = Number(arrayDef.count ?? 1);
        const start = Number(arrayDef.indexStart ?? arrayDef.index_start ?? 0);
        for (let i = 0; i < count; i += 1) {
          const idx = start + i;
          const namePattern =
            arrayDef.namingPattern ?? arrayDef.naming_pattern ?? `${String(iface.name)}_{index}`;
          const prefixPattern =
            arrayDef.physicalPrefixPattern ??
            arrayDef.physical_prefix_pattern ??
            `${String(iface.physicalPrefix ?? iface.physical_prefix ?? 's_axi_')}{index}_`;

          expanded.push({
            name: String(namePattern).replace('{index}', String(idx)),
            type: this.getString(iface.type),
            mode: this.getString(iface.mode).toLowerCase(),
            physical_prefix: String(prefixPattern).replace('{index}', String(idx)),
            use_optional_ports: iface.useOptionalPorts ?? iface.use_optional_ports ?? [],
            port_width_overrides: iface.portWidthOverrides ?? iface.port_width_overrides ?? {},
            associated_clock: iface.associatedClock ?? iface.associated_clock,
            associated_reset: iface.associatedReset ?? iface.associated_reset,
          });
        }
      } else {
        expanded.push({
          name: iface.name,
          type: this.getString(iface.type),
          mode: this.getString(iface.mode).toLowerCase(),
          physical_prefix: iface.physicalPrefix ?? iface.physical_prefix ?? 's_axi_',
          use_optional_ports: iface.useOptionalPorts ?? iface.use_optional_ports ?? [],
          port_width_overrides: iface.portWidthOverrides ?? iface.port_width_overrides ?? {},
          associated_clock: iface.associatedClock ?? iface.associated_clock,
          associated_reset: iface.associatedReset ?? iface.associated_reset,
        });
      }
    }

    return expanded;
  }

  private getActiveBusPorts(
    busTypeName: string,
    useOptionalPorts: string[],
    physicalPrefix: string,
    mode: string,
    portWidthOverrides: Record<string, number>
  ): Array<Record<string, unknown>> {
    const busDef = this.busDefinitions?.[busTypeName.toUpperCase()] ?? {};
    const ports = busDef.ports ?? [];
    const optionalSet = new Set(useOptionalPorts || []);
    const activePorts: Array<Record<string, unknown>> = [];

    ports.forEach((port) => {
      const logicalName = port.name;
      if (['ACLK', 'ARESETn', 'clk', 'reset'].includes(logicalName)) {
        return;
      }

      const presence = port.presence ?? 'required';
      const isRequired = presence === 'required';
      const isSelected = optionalSet.has(logicalName);
      if (!isRequired && !isSelected) {
        return;
      }

      let direction = port.direction ?? 'in';
      if (mode === 'slave') {
        direction = direction === 'out' ? 'in' : direction === 'in' ? 'out' : direction;
      }

      let width = port.width ?? 1;
      if (portWidthOverrides?.[logicalName] !== undefined) {
        width = portWidthOverrides[logicalName];
      }

      activePorts.push({
        logical_name: logicalName,
        name: `${physicalPrefix}${logicalName.toLowerCase()}`,
        direction,
        width,
        type: this.getVhdlPortType(Number(width), logicalName),
      });
    });

    return activePorts;
  }

  private getVhdlPortType(width: number, logicalName: string): string {
    if (['AWADDR', 'ARADDR', 'address'].includes(logicalName)) {
      return 'std_logic_vector(C_ADDR_WIDTH-1 downto 0)';
    }
    if (['WDATA', 'RDATA', 'writedata', 'readdata'].includes(logicalName)) {
      return 'std_logic_vector(C_DATA_WIDTH-1 downto 0)';
    }
    if (logicalName === 'WSTRB') {
      return 'std_logic_vector((C_DATA_WIDTH/8)-1 downto 0)';
    }
    if (width === 1) {
      return 'std_logic';
    }
    return `std_logic_vector(${width - 1} downto 0)`;
  }

  private normalizeBusTypeKey(typeName: string): string {
    let key = typeName.toUpperCase();
    if (['AXIL', 'AXI4-LITE', 'AXI4LITE'].includes(key)) {
      key = 'AXI4L';
    } else if (['AVMM', 'AVALON-MM'].includes(key)) {
      key = 'AVALON_MM';
    } else if (key === 'AXIS') {
      key = 'AXIS';
    } else if (key === 'AVALON_ST') {
      key = 'AVALON_ST';
    }
    return key;
  }

  private normalizePrefix(prefix: string): string {
    if (!prefix) {
      return 's_axi';
    }
    return prefix.endsWith('_') ? prefix.slice(0, -1) : prefix;
  }

  private async resolveMemoryMaps(
    ipCore: IpCoreData,
    inputPath: string
  ): Promise<Array<Record<string, unknown>>> {
    const memoryMaps = ipCore?.memoryMaps ?? ipCore?.memory_maps;
    if (!memoryMaps) {
      return [];
    }

    if (!Array.isArray(memoryMaps) && 'import' in memoryMaps) {
      const baseDir = path.dirname(inputPath);
      const importPath = path.resolve(baseDir, memoryMaps.import as string);
      const content = await fs.readFile(importPath, 'utf8');
      const parsed = yaml.load(content);
      if (Array.isArray(parsed)) {
        return parsed as Array<Record<string, unknown>>;
      }
      return parsed ? [parsed as Record<string, unknown>] : [];
    }

    return Array.isArray(memoryMaps) ? memoryMaps : [memoryMaps];
  }

  private async prepareRegisters(
    ipCore: IpCoreData,
    inputPath: string
  ): Promise<Array<Record<string, unknown>>> {
    const memoryMaps = await this.resolveMemoryMaps(ipCore, inputPath);
    const registers: Array<Record<string, unknown>> = [];

    const processRegister = (reg: Record<string, unknown>, baseOffset: number, prefix: string) => {
      const currentOffset =
        baseOffset + this.parseNumber(reg.address_offset ?? reg.addressOffset ?? reg.offset ?? 0);
      const regName = reg.name || 'REG';

      const nestedRegs = reg.registers || [];
      if (Array.isArray(nestedRegs) && nestedRegs.length > 0) {
        const count = Number(reg.count ?? 1) || 1;
        const stride = Number(reg.stride ?? 0) || 0;
        for (let i = 0; i < count; i += 1) {
          const instanceOffset = currentOffset + i * stride;
          const instancePrefix =
            count > 1 ? `${prefix}${String(regName)}_${i}_` : `${prefix}${String(regName)}_`;
          (nestedRegs as Array<Record<string, unknown>>).forEach((child) => {
            processRegister(child, instanceOffset, instancePrefix);
          });
        }
        return;
      }

      const fields = ((reg.fields as Array<Record<string, unknown>>) || []).map((field) => {
        let bitOffset = field.bit_offset ?? field.bitOffset ?? field.bit_range;
        let bitWidth = field.bit_width ?? field.bitWidth ?? field.bitWidth;

        if (bitOffset === undefined || bitWidth === undefined) {
          const parsedBits = this.parseBits(this.getString(field.bits));
          if (bitOffset === undefined) {
            bitOffset = parsedBits.offset;
          }
          if (bitWidth === undefined) {
            bitWidth = parsedBits.width;
          }
        }

        const access = this.getString(field.access || reg.access || 'read-write');
        const resetValue = field.reset_value ?? field.resetValue ?? field.reset ?? 0;

        return {
          name: field.name,
          offset: Number(bitOffset ?? 0),
          width: Number(bitWidth ?? 1),
          access: access.toLowerCase(),
          reset_value: resetValue,
          description: field.description || '',
        };
      });

      const regAccess = this.getString(reg.access || 'read-write');
      registers.push({
        name: `${prefix}${String(regName)}`,
        offset: currentOffset,
        access: regAccess.toLowerCase(),
        description: reg.description || '',
        fields,
      });
    };

    memoryMaps.forEach((map) => {
      const blocks =
        (map.addressBlocks as Array<Record<string, unknown>>) ||
        (map.address_blocks as Array<Record<string, unknown>>) ||
        [];
      blocks.forEach((block) => {
        const baseOffset = this.parseNumber(
          block.base_address ?? block.baseAddress ?? block.offset ?? 0
        );
        const regs = (block.registers as Array<Record<string, unknown>>) || [];
        regs.forEach((reg) => processRegister(reg, baseOffset, ''));
      });
    });

    return registers.sort((a, b) => (a.offset as number) - (b.offset as number));
  }

  private parseBits(bits: string): { offset: number; width: number } {
    if (!bits || typeof bits !== 'string') {
      return { offset: 0, width: 1 };
    }
    const range = bits.match(/\[(\d+):(\d+)\]/);
    if (range) {
      const high = Number(range[1]);
      const low = Number(range[2]);
      return { offset: Math.min(low, high), width: Math.abs(high - low) + 1 };
    }
    const single = bits.match(/\[(\d+)\]/);
    if (single) {
      const bit = Number(single[1]);
      return { offset: bit, width: 1 };
    }
    return { offset: 0, width: 1 };
  }

  private parseNumber(value: unknown): number {
    if (typeof value === 'number' && Number.isFinite(value)) {
      return value;
    }
    if (typeof value === 'string') {
      const parsed = Number.parseInt(value, 0);
      return Number.isFinite(parsed) ? parsed : 0;
    }
    return 0;
  }

  private getString(value: unknown): string {
    if (value === null || value === undefined) {
      return '';
    }
    if (typeof value === 'string') {
      return value;
    }
    if (typeof value === 'object' && value !== null && 'value' in value) {
      return String((value as Record<string, unknown>).value);
    }
    return String(value);
  }
}

import * as fs from "fs";
import * as path from "path";
import * as vscode from "vscode";
import * as nunjucks from "nunjucks";
import { Logger } from "../utils/Logger";

export class TemplateLoader {
  private readonly logger: Logger;
  private readonly env: nunjucks.Environment;
  private readonly templatesPath: string;

  constructor(logger: Logger, templatesPath?: string) {
    this.logger = logger;
    this.templatesPath = templatesPath || TemplateLoader.resolveTemplatesPath();

    this.env = new nunjucks.Environment(
      new nunjucks.FileSystemLoader(this.templatesPath, { noCache: true }),
      {
        autoescape: false,
        trimBlocks: true,
        lstripBlocks: true,
      },
    );

    this.env.addFilter("format", (format: string, value: unknown) => {
      if (typeof format !== "string") {
        return String(value ?? "");
      }
      const match = format.match(/%(-)?(0)?(\d+)?([sXx])/);
      if (!match) {
        return String(value ?? "");
      }
      const leftAlign = Boolean(match[1]);
      const zeroPad = Boolean(match[2]);
      const width = match[3] ? Number.parseInt(match[3], 10) : 0;
      const type = match[4];

      let rendered = "";
      if (type === "s") {
        rendered = String(value ?? "");
      } else if (type === "X" || type === "x") {
        const num = Number(value ?? 0);
        rendered = Number.isFinite(num) ? Math.trunc(num).toString(16) : "0";
        if (type === "X") {
          rendered = rendered.toUpperCase();
        }
      }

      if (width > 0 && rendered.length < width) {
        const padChar = zeroPad && !leftAlign ? "0" : " ";
        const padding = padChar.repeat(width - rendered.length);
        rendered = leftAlign
          ? `${rendered}${padding}`
          : `${padding}${rendered}`;
      }

      return rendered;
    });

    this.env.addFilter(
      "selectattr",
      (
        items: unknown,
        attribute: string,
        operator: string,
        compare: unknown,
      ) => {
        const list = Array.isArray(items) ? items : [];
        return list.filter((item) => {
          const value =
            item && typeof item === "object"
              ? (item as Record<string, unknown>)[attribute]
              : undefined;
          if (operator === "equalto") {
            return value === compare;
          }
          if (operator === "in") {
            return Array.isArray(compare)
              ? compare.includes(value as never)
              : false;
          }
          return false;
        });
      },
    );

    this.env.addFilter("list", (items: unknown) => {
      return Array.isArray(items) ? items : [];
    });

    this.logger.info(`Template loader using ${this.templatesPath}`);
  }

  static resolveTemplatesPath(): string {
    const workspaceRoot = vscode.workspace.workspaceFolders?.[0]?.uri.fsPath;
    const candidates = [path.join(__dirname, "templates")].filter(
      Boolean,
    ) ;

    for (const candidate of candidates) {
      if (fs.existsSync(candidate)) {
        return candidate;
      }
    }

    return workspaceRoot ? workspaceRoot : process.cwd();
  }

  getTemplatesPath(): string {
    return this.templatesPath;
  }

  render(templateName: string, context: Record<string, unknown>): string {
    return this.env.render(templateName, context);
  }
}

import * as fs from "fs/promises";
import * as path from "path";
import * as yaml from "js-yaml";

export interface ParsedPort {
  name: string;
  direction: string;
  type: string;
  width?: number | string;
}

export interface ParsedParameter {
  name: string;
  type: string;
  value?: string;
}

export interface ParseOptions {
  vendor?: string;
  library?: string;
  version?: string;
  detectBus?: boolean;
}

export interface ParseResult {
  entityName: string;
  yamlText: string;
  outputPath?: string;
}

export class VhdlParser {
  async parseFile(
    vhdlPath: string,
    options: ParseOptions = {},
  ): Promise<ParseResult> {
    const content = await fs.readFile(vhdlPath, "utf8");
    const cleaned = this.stripComments(content);

    const entityName = this.extractEntityName(cleaned);
    if (!entityName) {
      throw new Error("No VHDL entity found in file");
    }

    const parameters = this.extractParameters(cleaned);
    const ports = this.extractPorts(cleaned);

    const detectBus = options.detectBus !== false;
    const busDetection = detectBus ? this.detectBusInterfaces(ports) : null;

    const clockReset = this.classifyClocksResets(ports);

    const excludedNames = new Set<string>();
    if (busDetection) {
      busDetection.busPortNames.forEach((name) => excludedNames.add(name));
    }
    clockReset.clocks.forEach((clock) => excludedNames.add(clock.name));
    clockReset.resets.forEach((reset) => excludedNames.add(reset.name));

    const userPorts = ports.filter((port) => !excludedNames.has(port.name));

    const yamlData: Record<string, unknown> = {
      apiVersion: "ipcore/v1.0",
      vlnv: {
        vendor: options.vendor || "user",
        library: options.library || "ip",
        name: entityName,
        version: options.version || "1.0",
      },
      description: `Generated from ${path.basename(vhdlPath)}`,
    };

    if (clockReset.clocks.length > 0) {
      yamlData.clocks = clockReset.clocks.map((clock) => ({
        name: clock.name,
        description: "",
      }));
    }

    if (clockReset.resets.length > 0) {
      yamlData.resets = clockReset.resets.map((reset) => ({
        name: reset.name,
        polarity: reset.polarity,
        description: "",
      }));
    }

    if (userPorts.length > 0) {
      yamlData.ports = userPorts.map((port) => this.portToDict(port));
    }

    if (busDetection && busDetection.busInterfaces.length > 0) {
      yamlData.busInterfaces = busDetection.busInterfaces.map((bus) => ({
        name: bus.name,
        type: bus.type,
        mode: bus.mode,
        physicalPrefix: bus.physicalPrefix,
        description: "",
      }));
    }

    if (parameters.length > 0) {
      yamlData.parameters = parameters.map((param) => ({
        name: param.name,
        value: this.parseParameterValue(param.value),
        dataType: param.type.toLowerCase(),
      }));
    }

    yamlData.fileSets = [
      {
        name: "RTL_Sources",
        description: "RTL source files",
        files: [{ path: path.basename(vhdlPath), type: "vhdl" }],
      },
    ];

    const yamlText = yaml.dump(yamlData, {
      noRefs: true,
      sortKeys: false,
      lineWidth: -1,
      indent: 2,
    });

    return {
      entityName,
      yamlText,
    };
  }

  private stripComments(content: string): string {
    return content
      .split("\n")
      .map((line) => line.replace(/--.*$/, ""))
      .join("\n");
  }

  private extractEntityName(content: string): string | null {
    const match = content.match(/\bentity\s+(\w+)\s+is\b/i);
    return match ? match[1] : null;
  }

  private extractParameters(content: string): ParsedParameter[] {
    const body = this.extractBlockContent(content, "generic");
    if (!body) {
      return [];
    }

    const entries = this.splitEntries(body);
    const params: ParsedParameter[] = [];

    for (const entry of entries) {
      const cleaned = entry.replace(/\s+/g, " ").trim();
      if (!cleaned || !cleaned.includes(":")) {
        continue;
      }
      const [namesPart, typePart] = cleaned.split(":");
      const names = namesPart
        .split(",")
        .map((name) => name.trim())
        .filter(Boolean);
      const typeMatch = typePart.split(":=");
      const type = typeMatch[0].trim();
      const value = typeMatch[1]?.trim();

      names.forEach((name) => {
        params.push({ name, type, value });
      });
    }

    return params;
  }

  private extractPorts(content: string): ParsedPort[] {
    const body = this.extractBlockContent(content, "port");
    if (!body) {
      return [];
    }

    const entries = this.splitEntries(body);
    const ports: ParsedPort[] = [];

    for (const entry of entries) {
      const cleaned = entry.replace(/\s+/g, " ").trim();
      if (!cleaned || !cleaned.includes(":")) {
        continue;
      }
      const [namesPart, typePart] = cleaned.split(":");
      const names = namesPart
        .split(",")
        .map((name) => name.trim())
        .filter(Boolean);
      const dirMatch = typePart.trim().match(/^(in|out|inout)\s+(.+)$/i);
      if (!dirMatch) {
        continue;
      }
      const direction = dirMatch[1].toLowerCase();
      const type = dirMatch[2].trim();
      const width = this.extractWidthFromType(type);

      names.forEach((name) => {
        ports.push({ name, direction, type, width });
      });
    }

    return ports;
  }

  private extractWidthFromType(type: string): number | string | undefined {
    if (/\bstd_logic\b/i.test(type)) {
      if (/std_logic_vector/i.test(type)) {
        const rangeMatch = type.match(/\(([^)]+)\)/);
        if (!rangeMatch) {
          return undefined;
        }
        const range = rangeMatch[1];
        const numericMatch = range.match(/(\d+)\s+downto\s+(\d+)/i);
        if (numericMatch) {
          const high = Number(numericMatch[1]);
          const low = Number(numericMatch[2]);
          return Math.abs(high - low) + 1;
        }

        const paramMatch = range.match(/(\w+)\s*-\s*1\s+downto\s+0/i);
        if (paramMatch) {
          return paramMatch[1];
        }
      }
      return 1;
    }

    return undefined;
  }

  private portToDict(port: ParsedPort): Record<string, unknown> {
    let logicalName = port.name.toUpperCase();
    ["I_", "O_", "IO_"].forEach((prefix) => {
      if (logicalName.startsWith(prefix)) {
        logicalName = logicalName.slice(prefix.length);
      }
    });

    const result: Record<string, unknown> = {
      name: port.name,
      logicalName,
      direction: port.direction,
    };

    if (port.width !== undefined) {
      if (typeof port.width === "number") {
        if (port.width > 1) {
          result.width = port.width;
        }
      } else {
        result.width = port.width;
      }
    }

    return result;
  }

  private parseParameterValue(value?: string): unknown {
    if (value === undefined) {
      return undefined;
    }
    const trimmed = value.trim();
    if (!trimmed) {
      return undefined;
    }
    const numeric = Number(trimmed);
    if (Number.isFinite(numeric)) {
      return numeric;
    }
    return trimmed;
  }

  private classifyClocksResets(ports: ParsedPort[]): {
    clocks: Array<{ name: string }>;
    resets: Array<{ name: string; polarity: string }>;
  } {
    const clocks: Array<{ name: string }> = [];
    const resets: Array<{ name: string; polarity: string }> = [];

    ports.forEach((port) => {
      const name = port.name.toLowerCase();
      if (port.direction !== "in") {
        return;
      }
      if (/(^|_)(clk|clock|aclk)$/.test(name)) {
        clocks.push({ name: port.name });
        return;
      }
      if (/(^|_)(rst|reset|aresetn|reset_n|rst_n)$/.test(name)) {
        const activeLow =
          name.endsWith("n") ||
          name.includes("reset_n") ||
          name.includes("rst_n");
        resets.push({
          name: port.name,
          polarity: activeLow ? "activeLow" : "activeHigh",
        });
      }
    });

    return { clocks, resets };
  }

  private detectBusInterfaces(ports: ParsedPort[]): {
    busInterfaces: Array<{
      name: string;
      type: string;
      mode: string;
      physicalPrefix: string;
    }>;
    busPortNames: Set<string>;
  } {
    const lowerPorts = ports.map((port) => port.name.toLowerCase());
    const axiSignals = [
      "awaddr",
      "awvalid",
      "awready",
      "wdata",
      "wstrb",
      "wvalid",
      "wready",
      "bresp",
      "bvalid",
      "bready",
      "araddr",
      "arvalid",
      "arready",
      "rdata",
      "rresp",
      "rvalid",
      "rready",
    ];
    const avalonSignals = ["address", "read", "write", "writedata", "readdata"];

    const axiMatch = this.findBestPrefix(lowerPorts, axiSignals);
    const avalonMatch = this.findBestPrefix(lowerPorts, avalonSignals);

    let selected: {
      prefix: string;
      count: number;
      type: "AXI4L" | "AVALON_MM";
    } | null = null;

    if (axiMatch.count >= 4 || avalonMatch.count >= 3) {
      if (axiMatch.count >= avalonMatch.count) {
        selected = {
          prefix: axiMatch.prefix,
          count: axiMatch.count,
          type: "AXI4L",
        };
      } else {
        selected = {
          prefix: avalonMatch.prefix,
          count: avalonMatch.count,
          type: "AVALON_MM",
        };
      }
    }

    if (!selected || !selected.prefix) {
      return { busInterfaces: [], busPortNames: new Set() };
    }

    const physicalPrefix = selected.prefix;
    const busName = physicalPrefix.replace(/_+$/, "") || "bus";
    const busPortNames = new Set<string>();
    ports.forEach((port) => {
      if (port.name.toLowerCase().startsWith(physicalPrefix)) {
        busPortNames.add(port.name);
      }
    });

    return {
      busInterfaces: [
        {
          name: busName,
          type: selected.type,
          mode: "slave",
          physicalPrefix: physicalPrefix,
        },
      ],
      busPortNames,
    };
  }

  private findBestPrefix(
    ports: string[],
    signals: string[],
  ): { prefix: string; count: number } {
    const counts = new Map<string, number>();
    ports.forEach((name) => {
      signals.forEach((signal) => {
        if (name.endsWith(signal)) {
          const prefix = name.slice(0, name.length - signal.length);
          counts.set(prefix, (counts.get(prefix) ?? 0) + 1);
        }
      });
    });

    let bestPrefix = "";
    let bestCount = 0;
    counts.forEach((count, prefix) => {
      if (count > bestCount) {
        bestCount = count;
        bestPrefix = prefix;
      }
    });

    return { prefix: bestPrefix, count: bestCount };
  }

  private extractBlockContent(content: string, keyword: string): string | null {
    const match = content.match(new RegExp(`\\b${keyword}\\b`, "i"));
    if (!match || match.index === undefined) {
      return null;
    }

    const startIndex = content.indexOf("(", match.index);
    if (startIndex === -1) {
      return null;
    }

    let depth = 0;
    for (let i = startIndex; i < content.length; i += 1) {
      const char = content[i];
      if (char === "(") {
        depth += 1;
      } else if (char === ")") {
        depth -= 1;
        if (depth === 0) {
          return content.slice(startIndex + 1, i);
        }
      }
    }

    return null;
  }

  private splitEntries(body: string): string[] {
    const entries: string[] = [];
    let depth = 0;
    let current = "";

    for (let i = 0; i < body.length; i += 1) {
      const char = body[i];
      if (char === "(") {
        depth += 1;
      } else if (char === ")") {
        depth = Math.max(0, depth - 1);
      }

      if (char === ";" && depth === 0) {
        const trimmed = current.trim();
        if (trimmed) {
          entries.push(trimmed);
        }
        current = "";
      } else {
        current += char;
      }
    }

    const trimmed = current.trim();
    if (trimmed) {
      entries.push(trimmed);
    }

    return entries;
  }
}

import * as vscode from 'vscode';
import { Logger } from '../utils/Logger';
import { HtmlGenerator } from '../services/HtmlGenerator';
import { MessageHandler } from '../services/MessageHandler';
import { YamlValidator } from '../services/YamlValidator';
import { DocumentManager } from '../services/DocumentManager';

/**
 * Custom editor provider for FPGA memory map YAML files
 */
export class MemoryMapEditorProvider implements vscode.CustomTextEditorProvider {
  private readonly logger = new Logger('MemoryMapEditorProvider');
  private readonly htmlGenerator: HtmlGenerator;
  private readonly messageHandler: MessageHandler;
  private readonly documentManager: DocumentManager;

  constructor(private readonly context: vscode.ExtensionContext) {
    this.htmlGenerator = new HtmlGenerator(context);
    this.documentManager = new DocumentManager();
    const yamlValidator = new YamlValidator();
    this.messageHandler = new MessageHandler(yamlValidator, this.documentManager);

    this.logger.info('MemoryMapEditorProvider initialized');
  }

  /**
   * Resolve the custom text editor for a document
   */
  public resolveCustomTextEditor(
    document: vscode.TextDocument,
    webviewPanel: vscode.WebviewPanel,
    _token: vscode.CancellationToken
  ): void {
    this.logger.info('Resolving custom text editor for document', document.uri.toString());

    // Configure webview
    webviewPanel.webview.options = {
      enableScripts: true,
    };

    // Set HTML content
    webviewPanel.webview.html = this.htmlGenerator.generateHtml(webviewPanel.webview);

    let isReady = false;

    // Send update to webview when ready
    const updateWebview = () => {
      if (!isReady) {
        return;
      }
      this.messageHandler.sendUpdate(webviewPanel.webview, document);
    };

    // Listen for document changes
    const changeDocumentSubscription = vscode.workspace.onDidChangeTextDocument((e) => {
      if (e.document.uri.toString() === document.uri.toString()) {
        updateWebview();
      }
    });

    // Clean up subscriptions when webview is disposed
    webviewPanel.onDidDispose(() => {
      changeDocumentSubscription.dispose();
      this.logger.debug('Webview panel disposed');
    });

    // Handle messages from the webview
    webviewPanel.webview.onDidReceiveMessage((message) => {
      if (message.type === 'ready') {
        isReady = true;
        updateWebview();
        return;
      }
      void this.messageHandler.handleMessage(message, document);
    });

    // Queue initial content until webview signals readiness
    updateWebview();
  }
}

import * as path from "path";
import * as vscode from "vscode";
import * as yaml from "js-yaml";
import { Logger } from "../utils/Logger";

export class BusLibraryService {
  private readonly logger: Logger;
  private cachedDefaultLibrary: any | null = null;
  private readonly context: vscode.ExtensionContext;

  constructor(logger: Logger, context: vscode.ExtensionContext) {
    this.logger = logger;
    this.context = context;
  }

  async loadDefaultLibrary(): Promise<any> {
    if (this.cachedDefaultLibrary) {
      return this.cachedDefaultLibrary;
    }

    const builtInPath = path.join(
      this.context.extensionPath,
      "dist",
      "resources",
      "bus_definitions.yml",
    );

    // Provide only one path since webpack handles resources
    const candidates = [builtInPath];
    let loadedContent: string | null = null;
    let loadedPath: string | null = null;

    for (const candidate of candidates) {
      try {
        const uri = vscode.Uri.file(candidate);
        const fileData = await vscode.workspace.fs.readFile(uri);
        loadedContent = Buffer.from(fileData).toString("utf8");
        loadedPath = candidate;
        break;
      } catch (e) {
        // Check next candidate
      }
    }

    if (!loadedContent || !loadedPath) {
      this.logger.error("Default bus library not found in extension resources");
      return {};
    }

    try {
      const parsed = yaml.load(loadedContent);
      this.cachedDefaultLibrary = parsed ?? {};
      this.logger.info(`Loaded default bus library from ${loadedPath}`);
      return this.cachedDefaultLibrary;
    } catch (error) {
      this.logger.error(
        `Failed to parse default bus library from ${loadedPath}`,
        error as Error,
      );
      return {};
    }
  }

  clearCache(): void {
    this.cachedDefaultLibrary = null;
  }
}

import * as vscode from 'vscode';
import { Logger } from '../utils/Logger';
import { YamlValidator } from './YamlValidator';
import { DocumentManager } from './DocumentManager';

/**
 * Message types that can be sent from the webview
 */
export interface WebviewMessage {
  type: string;
  [key: string]: unknown;
}

/**
 * Update message from webview
 */
export interface UpdateMessage extends WebviewMessage {
  type: 'update';
  text: string;
}

/**
 * Command message from webview
 */
export interface CommandMessage extends WebviewMessage {
  type: 'command';
  command: string;
}

/**
 * Service responsible for handling messages between extension and webview
 */
export class MessageHandler {
  private readonly logger = new Logger('MessageHandler');

  constructor(
    private readonly yamlValidator: YamlValidator,
    private readonly documentManager: DocumentManager
  ) {}

  /**
   * Handle a message received from the webview
   */
  async handleMessage(message: WebviewMessage, document: vscode.TextDocument): Promise<void> {
    this.logger.debug('Received message from webview', message.type);

    switch (message.type) {
      case 'update':
        await this.handleUpdate(message as UpdateMessage, document);
        break;
      case 'command':
        await this.handleCommand(message as CommandMessage, document);
        break;
      default:
        this.logger.warn('Unknown message type', message.type);
    }
  }

  /**
   * Handle update message from webview
   */
  private async handleUpdate(message: UpdateMessage, document: vscode.TextDocument): Promise<void> {
    await this.documentManager.updateDocument(document, message.text);
  }

  /**
   * Handle command message from webview
   */
  private async handleCommand(
    message: CommandMessage,
    document: vscode.TextDocument
  ): Promise<void> {
    switch (message.command) {
      case 'save':
        await this.handleSaveCommand(document);
        break;
      case 'validate':
        await this.handleValidateCommand(document);
        break;
      case 'openFile':
        await this.handleOpenFileCommand(document, (message as any).path);
        break;
      default:
        this.logger.warn('Unknown command', message.command);
    }
  }

  /**
   * Handle openFile command
   */
  private async handleOpenFileCommand(
    document: vscode.TextDocument,
    filePath: string
  ): Promise<void> {
    try {
      // Resolve path relative to current document
      const currentDir = vscode.Uri.joinPath(document.uri, '..');
      const targetUri = vscode.Uri.joinPath(currentDir, filePath);

      // Open string paths or relative paths
      await vscode.commands.executeCommand('vscode.open', targetUri);
      this.logger.info('Opened file:', targetUri.toString());
    } catch (e) {
      this.logger.error('Failed to open file', e as Error);
      void vscode.window.showErrorMessage(`Failed to open file: ${filePath}`);
    }
  }

  /**
   * Handle save command
   */
  private async handleSaveCommand(document: vscode.TextDocument): Promise<void> {
    await document.save();
    this.logger.info('Document saved');
  }

  /**
   * Handle validate command
   */
  private async handleValidateCommand(document: vscode.TextDocument): Promise<void> {
    const result = this.yamlValidator.validate(document.getText());

    if (result.valid) {
      await vscode.window.showInformationMessage('YAML parsed successfully.');
      this.logger.info('YAML validation successful');
    } else {
      await vscode.window.showErrorMessage(`YAML parse error: ${result.error ?? 'Unknown error'}`);
      this.logger.warn('YAML validation failed', result.error);
    }
  }

  /**
   * Send an update message to the webview
   */
  sendUpdate(webview: vscode.Webview, document: vscode.TextDocument): void {
    void webview.postMessage({
      type: 'update',
      text: this.documentManager.getText(document),
      fileName: this.documentManager.getRelativePath(document.uri),
    });
    this.logger.debug('Sent update to webview');
  }
}

