import {
  repackBlocksForward,
  repackBlocksBackward,
} from "../../../webview/algorithms/AddressBlockRepacker";

describe("AddressBlockRepacker", () => {
  describe("repackBlocksForward", () => {
    it("should repack blocks forward maintaining sizes", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000, size: 0x1000 },
        { name: "Block2", base_address: 0x2000, size: 0x1000 },
        { name: "Block3", base_address: 0x5000, size: 0x2000 },
      ];

      const result = repackBlocksForward(blocks, 1);

      // Block 0 unchanged, blocks 1-2 repacked sequentially
      expect(result[0].base_address).toBe(0x0000);
      expect(result[1].base_address).toBe(0x1000); // After block 0
      expect(result[2].base_address).toBe(0x2000); // After block 1
    });

    it("should handle repacking from index 0", () => {
      const blocks = [
        { name: "Block1", base_address: 0x1000, size: 0x1000 },
        { name: "Block2", base_address: 0x5000, size: 0x2000 },
      ];

      const result = repackBlocksForward(blocks, 0);

      // All blocks repacked from address 0
      expect(result[0].base_address).toBe(0);
      expect(result[1].base_address).toBe(0x1000);
    });

    it("should handle blocks with varying sizes", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000, size: 0x100 },
        { name: "Block2", base_address: 0x1000, size: 0x2000 },
        { name: "Block3", base_address: 0x5000, size: 0x500 },
      ];

      const result = repackBlocksForward(blocks, 1);

      expect(result[0].base_address).toBe(0x0000);
      expect(result[1].base_address).toBe(0x100); // 0x000 + 0x100
      expect(result[2].base_address).toBe(0x2100); // 0x100 + 0x2000
    });

    it("should preserve original array", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000, size: 0x1000 },
        { name: "Block2", base_address: 0x2000, size: 0x1000 },
      ];

      const result = repackBlocksForward(blocks, 1);

      // Original should be unchanged
      expect(blocks[1].base_address).toBe(0x2000);
      expect(result[1].base_address).toBe(0x1000);
    });

    it("should handle single block", () => {
      const blocks = [{ name: "Block1", base_address: 0x5000, size: 0x1000 }];

      const result = repackBlocksForward(blocks, 0);

      expect(result[0].base_address).toBe(0);
    });

    it("should handle empty array", () => {
      const result = repackBlocksForward([], 0);
      expect(result).toEqual([]);
    });

    it("should handle type coercion for size property", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000, size: 0x1000 },
        { name: "Block2", base_address: 0x2000, size: undefined as any },
      ];

      const result = repackBlocksForward(blocks, 1);

      // Should use 0 for undefined size
      expect(result[1].base_address).toBe(0x1000);
    });
  });

  describe("repackBlocksBackward", () => {
    it("should repack blocks backward maintaining sizes", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000, size: 0x1000 },
        { name: "Block2", base_address: 0x2000, size: 0x1000 },
        { name: "Block3", base_address: 0x5000, size: 0x2000 },
      ];

      const result = repackBlocksBackward(blocks, 1);

      // Block 2 unchanged, blocks 0-1 repacked backward
      expect(result[2].base_address).toBe(0x5000);
      expect(result[1].base_address).toBe(0x4000); // 0x5000 - 0x1000
      expect(result[0].base_address).toBe(0x3000); // 0x4000 - 0x1000
    });

    it("should handle repacking to end when fromIndex is last", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000, size: 0x1000 },
        { name: "Block2", base_address: 0x1000, size: 0x2000 },
      ];

      const result = repackBlocksBackward(blocks, 1);

      // Last block stays in place
      expect(result[1].base_address).toBe(0x1000);
      expect(result[0].base_address).toBeGreaterThanOrEqual(0);
    });

    it("should clamp to address 0", () => {
      const blocks = [
        { name: "Block1", base_address: 0x1000, size: 0x5000 }, // Too large
        { name: "Block2", base_address: 0x2000, size: 0x1000 },
      ];

      const result = repackBlocksBackward(blocks, 0);

      // Should clamp block 0 to address 0
      expect(result[0].base_address).toBe(0);
    });

    it("should preserve original array", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000, size: 0x1000 },
        { name: "Block2", base_address: 0x5000, size: 0x2000 },
      ];

      const result = repackBlocksBackward(blocks, 0);

      // Original unchanged
      expect(blocks[0].base_address).toBe(0x0000);
      expect(result).not.toBe(blocks);
    });

    it("should handle empty array", () => {
      const result = repackBlocksBackward([], 0);
      expect(result).toEqual([]);
    });

    it("should handle single block", () => {
      const blocks = [{ name: "Block1", base_address: 0x5000, size: 0x1000 }];

      const result = repackBlocksBackward(blocks, 0);

      expect(result[0].base_address).toBe(0x5000);
    });
  });

  describe("Edge cases", () => {
    it("should handle zero-sized blocks", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000, size: 0 },
        { name: "Block2", base_address: 0x1000, size: 0x1000 },
      ];

      const forward = repackBlocksForward(blocks, 1);
      expect(forward[1].base_address).toBe(0); // After zero-sized block

      const backward = repackBlocksBackward(blocks, 0);
      expect(backward[0].base_address).toBeGreaterThanOrEqual(0);
    });

    it("should handle missing size property", () => {
      const blocks = [
        { name: "Block1", base_address: 0x0000 } as any,
        { name: "Block2", base_address: 0x1000, size: 0x1000 },
      ];

      const forward = repackBlocksForward(blocks, 0);
      expect(forward[0].base_address).toBe(0);
    });

    it("should handle very large addresses", () => {
      const blocks = [
        { name: "Block1", base_address: 0xffff0000, size: 0x1000 },
        { name: "Block2", base_address: 0xffff5000, size: 0x1000 },
      ];

      const result = repackBlocksForward(blocks, 1);
      expect(result[1].base_address).toBe(0xffff1000);
    });
  });
});

import * as vscode from "vscode";

/**
 * Log levels for extension logging
 */
export enum LogLevel {
  DEBUG = "DEBUG",
  INFO = "INFO",
  WARN = "WARN",
  ERROR = "ERROR",
}

/**
 * Logger service for structured logging throughout the extension
 */
export class Logger {
  private static outputChannel: vscode.OutputChannel | null = null;
  private static logLevel: LogLevel = LogLevel.INFO;

  constructor(private readonly context: string) {}

  /**
   * Initialize the logger with a VS Code output channel
   */
  static initialize(
    channelName: string,
    level: LogLevel = LogLevel.INFO,
  ): void {
    this.outputChannel = vscode.window.createOutputChannel(channelName);
    this.logLevel = level;
  }

  /**
   * Get the output channel (create if doesn't exist)
   */
  private static getChannel(): vscode.OutputChannel {
    if (!this.outputChannel) {
      this.outputChannel = vscode.window.createOutputChannel(
        "FPGA Memory Map Editor",
      );
    }
    return this.outputChannel;
  }

  /**
   * Set the global log level
   */
  static setLogLevel(level: LogLevel): void {
    this.logLevel = level;
  }

  /**
   * Write a message to the output channel
   */
  private log(level: LogLevel, message: string, ...args: unknown[]): void {
    const channel = Logger.getChannel();
    const timestamp = new Date().toISOString();
    const formattedArgs = args.map((arg) => this.formatArg(arg)).join(" ");
    const logMessage = `[${timestamp}] [${level}] [${this.context}] ${message}${formattedArgs ? " " + formattedArgs : ""}`;

    channel?.appendLine(logMessage);

    // Also log to console in development
    if (process.env.NODE_ENV === "development") {
      const consoleMethod = this.getConsoleMethod(level);
      consoleMethod(`[${this.context}]`, message, ...args);
    }
  }

  /**
   * Format an argument for logging
   */
  private formatArg(arg: unknown): string {
    if (arg === null) {
      return "null";
    }
    if (arg === undefined) {
      return "undefined";
    }
    if (arg instanceof Error) {
      return `Error: ${arg.message}\n${arg.stack ?? ""}`;
    }
    if (typeof arg === "object") {
      try {
        return JSON.stringify(arg, null, 2);
      } catch {
        return String(arg);
      }
    }
    return String(arg);
  }

  /**
   * Get the appropriate console method for a log level
   */
  private getConsoleMethod(level: LogLevel): (...args: unknown[]) => void {
    switch (level) {
      case LogLevel.DEBUG:
        return console.debug;
      case LogLevel.INFO:
        return console.info;
      case LogLevel.WARN:
        return console.warn;
      case LogLevel.ERROR:
        return console.error;
      default:
        return console.log;
    }
  }

  /**
   * Check if a log level should be logged based on current log level
   */
  private shouldLog(level: LogLevel): boolean {
    const levels = [
      LogLevel.DEBUG,
      LogLevel.INFO,
      LogLevel.WARN,
      LogLevel.ERROR,
    ];
    const currentIndex = levels.indexOf(Logger.logLevel);
    const messageIndex = levels.indexOf(level);
    return messageIndex >= currentIndex;
  }

  /**
   * Log a debug message
   */
  debug(message: string, ...args: unknown[]): void {
    if (this.shouldLog(LogLevel.DEBUG)) {
      this.log(LogLevel.DEBUG, message, ...args);
    }
  }

  /**
   * Log an info message
   */
  info(message: string, ...args: unknown[]): void {
    if (this.shouldLog(LogLevel.INFO)) {
      this.log(LogLevel.INFO, message, ...args);
    }
  }

  /**
   * Log a warning message
   */
  warn(message: string, ...args: unknown[]): void {
    if (this.shouldLog(LogLevel.WARN)) {
      this.log(LogLevel.WARN, message, ...args);
    }
  }

  /**
   * Log an error message
   */
  error(message: string, error?: Error, ...args: unknown[]): void {
    if (this.shouldLog(LogLevel.ERROR)) {
      const allArgs = error ? [error, ...args] : args;
      this.log(LogLevel.ERROR, message, ...allArgs);
    }
  }

  /**
   * Show the output channel
   */
  static show(): void {
    this.getChannel().show();
  }

  /**
   * Dispose of the output channel
   */
  static dispose(): void {
    this.outputChannel?.dispose();
    this.outputChannel = null;
  }
}

/**
 * Address block repacking algorithms for maintaining proper block layouts
 */

import type { AddressBlockRecord, RegisterRecord } from '../types/editor';

/**
 * Calculate block size based on registers and register arrays
 * For regular registers: 4 bytes per register
 * For register arrays: count * stride bytes
 */
function calculateBlockSize(block: AddressBlockRecord): number {
  const registers: RegisterRecord[] = block?.registers || [];
  if (registers.length === 0) {
    return typeof block?.size === "number" ? block.size : 4;
  }

  let totalSize = 0;
  for (const reg of registers) {
    if (reg.__kind === "array") {
      // Register array: size = count * stride
      const count = reg.count || 1;
      const stride = reg.stride || 4;
      totalSize += count * stride;
    } else {
      // Regular register: 4 bytes
      totalSize += 4;
    }
  }
  return totalSize;
}

/**
 * Repack address blocks forward (toward higher addresses) starting from the given index.
 * Maintains block sizes but shifts them to higher addresses.
 * Calculates block size based on registers and register arrays.
 * @param blocks Array of address blocks
 * @param fromIndex Starting index for repacking (inclusive)
 * @returns New array with repacked blocks
 */
export function repackBlocksForward(blocks: AddressBlockRecord[], fromIndex: number): AddressBlockRecord[] {
  const newBlocks = [...blocks];

  // Start from the block just before fromIndex to determine the starting position
  let nextBase = 0;
  if (fromIndex > 0) {
    const prevBlock = newBlocks[fromIndex - 1];
    const prevBase: number =
      typeof prevBlock.base_address === "number" ? prevBlock.base_address : 0;
    const prevSize = calculateBlockSize(prevBlock);
    nextBase = prevBase + prevSize;
  }

  for (let i = fromIndex; i < newBlocks.length; i++) {
    const block = newBlocks[i];
    const blockSize = calculateBlockSize(block);

    newBlocks[i] = {
      ...block,
      base_address: nextBase,
      size: blockSize,
    };
    nextBase += blockSize;
  }

  return newBlocks;
}

/**
 * Repack address blocks backward (toward lower addresses) starting from the given index going backwards.
 * Maintains block sizes but shifts them to lower addresses.
 * Calculates block size based on registers and register arrays.
 * @param blocks Array of address blocks
 * @param fromIndex Starting index for repacking (inclusive), goes backward to index 0
 * @returns New array with repacked blocks
 */
export function repackBlocksBackward(blocks: AddressBlockRecord[], fromIndex: number): AddressBlockRecord[] {
  const newBlocks = [...blocks];
  if (newBlocks.length === 0) {
    return [];
  }

  // Start from the block just after fromIndex to determine the starting position.
  // `nextEnd` tracks an inclusive end address; `Infinity` preserves the current
  // block base for the first processed element when repacking from the tail.
  let nextEnd: number =
    fromIndex < newBlocks.length - 1
      ? (newBlocks[fromIndex + 1].base_address ?? 0) - 1
      : Infinity;

  for (let i = fromIndex; i >= 0; i--) {
    const block = newBlocks[i];
    const size = calculateBlockSize(block);

    // Use inclusive-address arithmetic: base = end - size + 1.
    const base = nextEnd === Infinity ? (block.base_address ?? 0) : nextEnd - size + 1;
    newBlocks[i] = {
      ...block,
      base_address: Math.max(0, base),
      size,
    };
    nextEnd = base - 1;
  }

  return newBlocks;
}

import React, { useMemo } from 'react';
import { FIELD_COLORS, FIELD_COLOR_KEYS } from '../shared/colors';

interface AddressMapVisualizerProps {
  blocks: any[];
  hoveredBlockIndex?: number | null;
  setHoveredBlockIndex?: (idx: number | null) => void;
  onBlockClick?: (blockIndex: number) => void;
}

function getBlockColor(idx: number) {
  return FIELD_COLOR_KEYS[idx % FIELD_COLOR_KEYS.length];
}

function toHex(n: number): string {
  return `0x${Math.max(0, n).toString(16).toUpperCase()}`;
}

/**
 * Calculate block size based on registers and register arrays
 * For regular registers: 4 bytes per register
 * For register arrays: count * stride bytes
 * Falls back to explicit size if no registers
 */
function calculateBlockSize(block: any): number {
  const registers = block?.registers || [];
  if (registers.length === 0) {
    return block?.size ?? block?.range ?? 4;
  }

  let totalSize = 0;
  for (const reg of registers) {
    if (reg.__kind === 'array') {
      // Register array: size = count * stride
      const count = reg.count || 1;
      const stride = reg.stride || 4;
      totalSize += count * stride;
    } else {
      // Regular register: 4 bytes
      totalSize += 4;
    }
  }
  return totalSize;
}

const AddressMapVisualizer: React.FC<AddressMapVisualizerProps> = ({
  blocks,
  hoveredBlockIndex = null,
  setHoveredBlockIndex = (_idx: number | null) => undefined,
  onBlockClick,
}) => {
  // Group blocks by address ranges
  const groups = useMemo(() => {
    return blocks.map((block, idx) => {
      const base = block.base_address ?? block.offset ?? 0;
      const size = calculateBlockSize(block);
      return {
        idx,
        name: block.name || `Block ${idx}`,
        start: Number(base),
        end: Number(base) + Number(size) - 1,
        size,
        color: getBlockColor(idx),
        usage: block.usage || 'register',
      };
    });
  }, [blocks]);

  return (
    <div className="w-full">
      <div className="relative w-full flex items-start overflow-x-auto pb-2">
        {/* Address grid background */}
        {/* <div className="absolute inset-0 pointer-events-none fpga-bit-grid-bg bg-[size:32px_48px] rounded-lg" /> */}
        <div className="relative flex flex-row items-end gap-0 pl-4 pr-2 pt-12 pb-2 min-h-[64px] w-full">
          {groups.map((group, groupIdx) => {
            const isHovered = hoveredBlockIndex === group.idx;
            const separatorShadow = 'inset 0 0 0 1px var(--vscode-panel-border)';
            return (
              <div
                key={group.idx}
                className={`relative flex-1 flex flex-col items-center justify-end select-none min-w-[120px] ${isHovered ? 'z-10' : ''}`}
                style={{ cursor: onBlockClick ? 'pointer' : 'default' }}
                onMouseEnter={() => setHoveredBlockIndex(group.idx)}
                onMouseLeave={() => setHoveredBlockIndex(null)}
                onClick={() => onBlockClick?.(group.idx)}
              >
                <div
                  className="h-20 w-full overflow-hidden flex items-center justify-center px-2 rounded-md"
                  style={{
                    background: FIELD_COLORS[group.color],
                    opacity: 1,
                    transform: isHovered ? 'translateY(-2px)' : undefined,
                    filter: isHovered ? 'saturate(1.15) brightness(1.05)' : undefined,
                    boxShadow: isHovered
                      ? `${separatorShadow}, 0 0 0 2px var(--vscode-focusBorder), 0 10px 20px color-mix(in srgb, var(--vscode-foreground) 22%, transparent)`
                      : separatorShadow,
                  }}
                >
                  <div className="flex flex-col items-center gap-0.5">
                    <span className="text-lg select-none">
                      {group.usage === 'memory' ? '📦' : '📋'}
                    </span>
                    <span className="text-[10px] font-mono text-white/80 font-semibold select-none text-center leading-tight">
                      {group.usage === 'memory' ? 'MEM' : 'REG'}
                    </span>
                  </div>
                </div>
                <div
                  className={`absolute -top-12 px-2 py-0.5 rounded border shadow text-xs whitespace-nowrap pointer-events-none ${
                    groupIdx === 0 ? 'left-0' : 'left-1/2 -translate-x-1/2'
                  }`}
                  style={{
                    background: 'var(--vscode-editorWidget-background)',
                    color: 'var(--vscode-foreground)',
                    borderColor: 'var(--vscode-panel-border)',
                  }}
                >
                  <div className="font-bold">
                    {group.name}
                    <span className="ml-2 vscode-muted font-mono text-[11px]">
                      [{toHex(group.start)}:{toHex(group.end)}]
                    </span>
                  </div>
                  <div className="text-[11px] vscode-muted font-mono">
                    {group.size < 1024
                      ? `${group.size}B`
                      : group.size < 1048576
                        ? `${(group.size / 1024).toFixed(1)}KB`
                        : `${(group.size / 1048576).toFixed(1)}MB`}
                  </div>
                </div>
                <div className="flex w-full justify-center">
                  <div className="text-center text-[11px] vscode-muted font-mono mt-1">
                    {toHex(group.start)}
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

