export interface AddressBlockModel {
  name?: string;
  base_address?: number | string;
  offset?: number | string;
  description?: string;
  usage?: string;
  registers?: RegisterModel[];
  [key: string]: unknown;
}

const ACCESS_OPTIONS = [
  'read-only',
  'write-only',
  'read-write',
  'write-1-to-clear',
  'read-write-1-to-clear',
];

export interface BlockEditorProps {
  /** The address block object (has name, base_address, registers, etc.). */
  block: AddressBlockModel;
  selectionMeta?: {
    absoluteAddress?: number;
    relativeOffset?: number;
    focusDetails?: boolean;
  };
  onUpdate: YamlUpdateHandler;
  onNavigateToRegister?: (regIndex: number) => void;
}

// ---------------------------------------------------------------------------
// Component
// ---------------------------------------------------------------------------

/**
 * Renders and manages editing of a single address block's properties, including:
 * - Block header / description
 * - RegisterMapVisualizer
 * - Keyboard-navigable registers table with insert / delete / reorder support
 */
export function BlockEditor({
  block,
  selectionMeta,
  onUpdate,
  onNavigateToRegister,
}: BlockEditorProps) {
  const registers = block?.registers || [];
  const baseAddress = Number(block?.base_address ?? block?.offset ?? 0);

  const [selectedRegIndex, setSelectedRegIndex] = useState<number>(-1);
  const [hoveredRegIndex, setHoveredRegIndex] = useState<number | null>(null);
  const [regActiveCell, setRegActiveCell] = useState<RegActiveCell>({
    rowIndex: -1,
    key: 'name',
  });
  const [insertError, setInsertError] = useState<string | null>(null);

  const focusRef = useRef<HTMLDivElement | null>(null);
  const errorRef = useRef<HTMLDivElement | null>(null);

  const toHex = (n: number) => `0x${Math.max(0, n).toString(16).toUpperCase()}`;
  const getRegColor = (idx: number) => FIELD_COLOR_KEYS[idx % FIELD_COLOR_KEYS.length];

  // Auto-focus on explicit request.
  useEffect(() => {
    if (!selectionMeta?.focusDetails) {
      return;
    }
    const id = window.setTimeout(() => focusRef.current?.focus(), 0);
    return () => window.clearTimeout(id);
  }, [selectionMeta?.focusDetails, block?.name]);

  // Clamp selection when block changes.
  useEffect(() => {
    const regs = block?.registers || [];
    if (!Array.isArray(regs) || regs.length === 0) {
      setSelectedRegIndex(-1);
      setRegActiveCell({ rowIndex: -1, key: 'name' });
      return;
    }
    setSelectedRegIndex((prev) => {
      if (prev < 0) {
        return 0;
      }
      if (prev >= regs.length) {
        return regs.length - 1;
      }
      return prev;
    });
    setRegActiveCell((prev) => {
      const rowIndex = prev.rowIndex < 0 ? 0 : Math.min(regs.length - 1, prev.rowIndex);
      const key = REG_COLUMN_ORDER.includes(prev.key) ? prev.key : 'name';
      return { rowIndex, key };
    });
  }, [block?.name, (block?.registers || []).length]);

  // Escape: return focus back to the table.
  useEffect(() => {
    const onKeyDown = (e: KeyboardEvent) => {
      if (e.key !== 'Escape') {
        return;
      }
      const activeEl = document.activeElement as HTMLElement | null;
      if (!activeEl) {
        return;
      }
      const inRegs =
        !!focusRef.current && focusRef.current.contains(activeEl) && activeEl !== focusRef.current;
      if (!inRegs) {
        return;
      }
      e.preventDefault();
      e.stopPropagation();
      try {
        activeEl.blur?.();
      } catch {
        /* ignore */
      }
      window.setTimeout(() => focusRef.current?.focus(), 0);
    };
    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  }, []);

  // Keyboard shortcuts (o/O insert, arrow nav, F2/e edit, d delete, Shift+A/I array insert).
  useEffect(() => {
    const liveRegisters = block?.registers || [];

    const tryInsertReg = (after: boolean) => {
      setInsertError(null);
      type TargetArray = Parameters<typeof SpatialInsertionService.insertRegisterAfter>[0];
      const result = after
        ? SpatialInsertionService.insertRegisterAfter(
            liveRegisters as unknown as TargetArray,
            selectedRegIndex
          )
        : SpatialInsertionService.insertRegisterBefore(
            liveRegisters as unknown as TargetArray,
            selectedRegIndex
          );

      if (result.error) {
        setInsertError(result.error);
        return;
      }

      const newIdx = result.newIndex;
      onUpdate(['registers'], result.items as unknown[]);
      setSelectedRegIndex(newIdx);
      setHoveredRegIndex(newIdx);
      setRegActiveCell({ rowIndex: newIdx, key: 'name' });
      window.setTimeout(() => {
        document.querySelector(`tr[data-reg-idx="${newIdx}"]`)?.scrollIntoView({ block: 'center' });
      }, 100);
    };

    const onKeyDown = (e: KeyboardEvent) => {
      let keyLower = (e.key || '').toLowerCase();
      if (e.altKey && e.code) {
        if (e.code === 'KeyH') {
          keyLower = 'h';
        }
        if (e.code === 'KeyJ') {
          keyLower = 'j';
        }
        if (e.code === 'KeyK') {
          keyLower = 'k';
        }
        if (e.code === 'KeyL') {
          keyLower = 'l';
        }
      }
      const vimToArrow: Record<string, 'ArrowLeft' | 'ArrowDown' | 'ArrowUp' | 'ArrowRight'> = {
        h: 'ArrowLeft',
        j: 'ArrowDown',
        k: 'ArrowUp',
        l: 'ArrowRight',
      };
      const normalizedKey: string = vimToArrow[keyLower] ?? e.key;

      const isArrow = ['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(normalizedKey);
      const isEdit = normalizedKey === 'F2' || keyLower === 'e';
      const isDelete = keyLower === 'd' || e.key === 'Delete';
      const isInsertAfter = keyLower === 'o' && !e.shiftKey;
      const isInsertBefore = keyLower === 'o' && e.shiftKey;
      const isInsertArrayAfter = keyLower === 'a' && e.shiftKey;
      const isInsertArrayBefore = keyLower === 'i' && e.shiftKey;

      if (
        !isArrow &&
        !isEdit &&
        !isDelete &&
        !isInsertAfter &&
        !isInsertBefore &&
        !isInsertArrayAfter &&
        !isInsertArrayBefore
      ) {
        return;
      }
      if (e.ctrlKey || e.metaKey) {
        return;
      }

      const activeEl = document.activeElement as HTMLElement | null;
      const isInRegsArea =
        !!focusRef.current &&
        !!activeEl &&
        (activeEl === focusRef.current || focusRef.current.contains(activeEl));
      if (!isInRegsArea) {
        return;
      }

      const target = e.target as HTMLElement | null;
      const isTypingTarget = !!target?.closest(
        'input, textarea, select, [contenteditable="true"], vscode-text-field, vscode-text-area, vscode-dropdown'
      );
      if (isTypingTarget) {
        return;
      }

      const scrollToCell = (rowIndex: number, key: RegEditKey) => {
        window.setTimeout(() => {
          const row = document.querySelector(`tr[data-reg-idx="${rowIndex}"]`);
          row?.scrollIntoView({ block: 'nearest' });
          const cell = row?.querySelector(`td[data-col-key="${key}"]`) as HTMLElement | null;
          cell?.scrollIntoView({ block: 'nearest', inline: 'nearest' });
        }, 0);
      };

      const focusEditor = (rowIndex: number, key: RegEditKey) => {
        window.setTimeout(() => {
          const row = document.querySelector(`tr[data-reg-idx="${rowIndex}"]`);
          const editor = row?.querySelector(`[data-edit-key="${key}"]`) as HTMLElement | null;
          editor?.focus?.();
        }, 0);
      };

      const currentRow =
        regActiveCell.rowIndex >= 0
          ? regActiveCell.rowIndex
          : selectedRegIndex >= 0
            ? selectedRegIndex
            : 0;
      const currentKey: RegEditKey = REG_COLUMN_ORDER.includes(regActiveCell.key)
        ? regActiveCell.key
        : 'name';

      if (isEdit) {
        if (currentRow < 0 || currentRow >= liveRegisters.length) {
          return;
        }
        e.preventDefault();
        e.stopPropagation();
        setSelectedRegIndex(currentRow);
        setHoveredRegIndex(currentRow);
        setRegActiveCell({ rowIndex: currentRow, key: currentKey });
        focusEditor(currentRow, currentKey);
        return;
      }
      if (isInsertAfter || isInsertBefore) {
        e.preventDefault();
        e.stopPropagation();
        tryInsertReg(isInsertAfter);
        return;
      }
      if (isInsertArrayAfter || isInsertArrayBefore) {
        e.preventDefault();
        e.stopPropagation();
        let maxN = 0;
        for (const r of liveRegisters) {
          const match = r.name?.match(/^ARRAY_(\d+)$/i);
          if (match) {
            maxN = Math.max(maxN, parseInt(match[1], 10));
          }
        }
        const arrayName = `ARRAY_${maxN + 1}`;
        const selIdx = selectedRegIndex >= 0 ? selectedRegIndex : liveRegisters.length - 1;
        const selected = liveRegisters[selIdx];
        const selectedOffset = selected?.address_offset ?? selected?.offset ?? 0;
        let selectedSize = 4;
        if (selected?.__kind === 'array') {
          selectedSize = (selected.count || 1) * (selected.stride || 4);
        }
        const newArraySize = 8;
        const baseOffset = isInsertArrayAfter
          ? Number(selectedOffset) + Number(selectedSize)
          : selectedOffset;
        const newArray = {
          __kind: 'array',
          name: arrayName,
          address_offset: baseOffset,
          offset: baseOffset,
          count: 2,
          stride: 4,
          description: '',
          registers: [
            {
              name: 'reg0',
              offset: 0,
              address_offset: 0,
              access: 'read-write',
              description: '',
              fields: [{ name: 'data', bits: '[31:0]', access: 'read-write', description: '' }],
            },
          ],
        };
        let newRegs: RegisterModel[];
        let newIdx: number;
        if (isInsertArrayAfter) {
          newRegs = [
            ...liveRegisters.slice(0, selIdx + 1),
            newArray,
            ...liveRegisters.slice(selIdx + 1),
          ];
          newIdx = selIdx + 1;
        } else {
          newRegs = [
            ...liveRegisters.slice(0, selIdx),
            newArray,
            ...liveRegisters.slice(selIdx).map((r: RegisterModel) => ({
              ...r,
              offset: Number(r.offset ?? r.address_offset ?? 0) + newArraySize,
              address_offset: Number(r.address_offset ?? r.offset ?? 0) + newArraySize,
            })),
          ];
          newIdx = selIdx;
        }
        onUpdate(['registers'], newRegs as unknown[]);
        setSelectedRegIndex(newIdx);
        setHoveredRegIndex(newIdx);
        setRegActiveCell({ rowIndex: newIdx, key: 'name' });
        return;
      }
      if (isDelete) {
        if (currentRow < 0 || currentRow >= liveRegisters.length) {
          return;
        }
        e.preventDefault();
        e.stopPropagation();
        const newRegs = liveRegisters.filter((_: RegisterModel, i: number) => i !== currentRow);
        onUpdate(['registers'], newRegs as unknown[]);
        const nextRow = currentRow > 0 ? currentRow - 1 : newRegs.length > 0 ? 0 : -1;
        setSelectedRegIndex(nextRow);
        setHoveredRegIndex(nextRow);
        setRegActiveCell({ rowIndex: nextRow, key: currentKey });
        return;
      }

      e.preventDefault();
      e.stopPropagation();
      if (liveRegisters.length === 0) {
        return;
      }

      const isVertical = normalizedKey === 'ArrowUp' || normalizedKey === 'ArrowDown';
      const delta = normalizedKey === 'ArrowUp' || normalizedKey === 'ArrowLeft' ? -1 : 1;

      if (e.altKey && isVertical) {
        if (selectedRegIndex < 0) {
          return;
        }
        const next = selectedRegIndex + delta;
        if (next < 0 || next >= liveRegisters.length) {
          return;
        }
        const newRegs = [...liveRegisters];
        const temp = newRegs[selectedRegIndex];
        newRegs[selectedRegIndex] = newRegs[next];
        newRegs[next] = temp;
        newRegs.forEach((r, i) => {
          r.offset = i * 4;
          r.address_offset = i * 4;
        });
        onUpdate(['registers'], newRegs as unknown[]);
        setSelectedRegIndex(next);
        setHoveredRegIndex(next);
        setRegActiveCell((prev) => ({ rowIndex: next, key: prev.key }));
        scrollToCell(next, currentKey);
        return;
      }

      if (isVertical) {
        const nextRow = Math.max(0, Math.min(liveRegisters.length - 1, currentRow + delta));
        setSelectedRegIndex(nextRow);
        setHoveredRegIndex(nextRow);
        setRegActiveCell({ rowIndex: nextRow, key: currentKey });
        scrollToCell(nextRow, currentKey);
        return;
      }

      const currentCol = Math.max(0, REG_COLUMN_ORDER.indexOf(currentKey));
      const nextCol = Math.max(0, Math.min(REG_COLUMN_ORDER.length - 1, currentCol + delta));
      const nextKey = REG_COLUMN_ORDER[nextCol] ?? 'name';
      setSelectedRegIndex(currentRow);
      setHoveredRegIndex(currentRow);
      setRegActiveCell({ rowIndex: currentRow, key: nextKey });
      scrollToCell(currentRow, nextKey);
    };

    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  }, [block, selectedRegIndex, hoveredRegIndex, regActiveCell, onUpdate]);

  return (
    <div className="flex flex-col w-full h-full min-h-0">
      {/* Header + RegisterMapVisualizer */}
      <div className="vscode-surface border-b vscode-border p-8 flex flex-col gap-6 shrink-0 relative overflow-hidden">
        <div className="flex justify-between items-start relative z-10">
          <div>
            <h2 className="text-2xl font-bold font-mono tracking-tight">
              {block?.name || 'Address Block'}
            </h2>
            <p className="vscode-muted text-sm mt-1 max-w-2xl">
              {block?.description || `Base: ${toHex(baseAddress)}`} • {block?.usage || 'register'}
            </p>
          </div>
        </div>
        <div className="w-full relative z-10 mt-2 select-none">
          <RegisterMapVisualizer
            registers={registers}
            hoveredRegIndex={hoveredRegIndex}
            setHoveredRegIndex={setHoveredRegIndex}
            baseAddress={baseAddress}
            onReorderRegisters={(newRegs) => onUpdate(['registers'], newRegs as unknown[])}
            onRegisterClick={onNavigateToRegister}
          />
        </div>
      </div>

      {/* Registers table */}
      <div className="flex-1 flex overflow-hidden min-h-0">
        <div className="flex-1 vscode-surface min-h-0 flex flex-col">
          <div
            ref={focusRef}
            tabIndex={0}
            data-regs-table="true"
            className="flex-1 overflow-auto min-h-0 outline-none focus:outline-none"
          >
            {insertError ? (
              <div ref={errorRef} className="vscode-error px-4 py-2 text-xs">
                {insertError}
              </div>
            ) : null}
            <table className="w-full text-left border-collapse table-fixed">
              <colgroup>
                <col className="w-[30%] min-w-[200px]" />
                <col className="w-[20%] min-w-[120px]" />
                <col className="w-[15%] min-w-[100px]" />
                <col className="w-[35%]" />
              </colgroup>
              <thead className="vscode-surface-alt text-xs font-semibold vscode-muted uppercase tracking-wider sticky top-0 z-10 shadow-sm">
                <tr className="h-12">
                  <th className="px-6 py-3 border-b vscode-border align-middle">Name</th>
                  <th className="px-4 py-3 border-b vscode-border align-middle">Offset</th>
                  <th className="px-4 py-3 border-b vscode-border align-middle">Access</th>
                  <th className="px-6 py-3 border-b vscode-border align-middle">Description</th>
                </tr>
              </thead>
              <tbody className="divide-y vscode-border text-sm">
                {registers.map((reg: RegisterModel, idx: number) => {
                  const color = getRegColor(idx);
                  const offset = reg.address_offset ?? reg.offset ?? idx * 4;

                  return (
                    <tr
                      key={idx}
                      data-reg-idx={idx}
                      className={`group transition-colors border-l-4 border-transparent h-12 ${
                        idx === selectedRegIndex
                          ? 'vscode-focus-border vscode-row-selected'
                          : idx === hoveredRegIndex
                            ? 'vscode-focus-border vscode-row-hover'
                            : ''
                      }`}
                      onMouseEnter={() => setHoveredRegIndex(idx)}
                      onMouseLeave={() => setHoveredRegIndex(null)}
                      onClick={() => {
                        setSelectedRegIndex(idx);
                        setHoveredRegIndex(idx);
                        setRegActiveCell((prev) => ({ rowIndex: idx, key: prev.key }));
                      }}
                    >
                      {/* NAME */}
                      <td
                        data-col-key="name"
                        className={`px-6 py-2 font-medium align-middle ${
                          regActiveCell.rowIndex === idx && regActiveCell.key === 'name'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedRegIndex(idx);
                          setHoveredRegIndex(idx);
                          setRegActiveCell({ rowIndex: idx, key: 'name' });
                        }}
                      >
                        <div className="flex items-center gap-2">
                          <div
                            className="w-2.5 h-2.5 rounded-sm"
                            style={{ backgroundColor: FIELD_COLORS[color] || color }}
                          />
                          <VSCodeTextField
                            data-edit-key="name"
                            className="flex-1"
                            value={reg.name || ''}
                            onBlur={(e: Event | React.FormEvent<HTMLElement>) =>
                              onUpdate(
                                ['registers', idx, 'name'],
                                (e.target as HTMLInputElement).value
                              )
                            }
                          />
                        </div>
                      </td>
                      {/* OFFSET */}
                      <td
                        data-col-key="offset"
                        className={`px-4 py-2 font-mono vscode-muted align-middle ${
                          regActiveCell.rowIndex === idx && regActiveCell.key === 'offset'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedRegIndex(idx);
                          setHoveredRegIndex(idx);
                          setRegActiveCell({ rowIndex: idx, key: 'offset' });
                        }}
                      >
                        <VSCodeTextField
                          data-edit-key="offset"
                          className="w-full font-mono"
                          value={toHex(offset as number)}
                          onInput={(e: Event | React.FormEvent<HTMLElement>) => {
                            const val = Number.parseInt((e.target as HTMLInputElement).value, 0);
                            if (!Number.isNaN(val)) {
                              onUpdate(['registers', idx, 'offset'], val);
                            }
                          }}
                        />
                      </td>
                      {/* ACCESS */}
                      <td
                        data-col-key="access"
                        className={`px-4 py-2 align-middle ${
                          regActiveCell.rowIndex === idx && regActiveCell.key === 'access'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedRegIndex(idx);
                          setHoveredRegIndex(idx);
                          setRegActiveCell({ rowIndex: idx, key: 'access' });
                        }}
                      >
                        <VSCodeDropdown
                          data-edit-key="access"
                          className="w-full"
                          value={reg.access || 'read-write'}
                          onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                            onUpdate(
                              ['registers', idx, 'access'],
                              (e.target as HTMLInputElement).value
                            )
                          }
                        >
                          {ACCESS_OPTIONS.map((opt) => (
                            <VSCodeOption key={opt} value={opt}>
                              {opt}
                            </VSCodeOption>
                          ))}
                        </VSCodeDropdown>
                      </td>
                      {/* DESCRIPTION */}
                      <td
                        data-col-key="description"
                        className={`px-6 py-2 vscode-muted align-middle ${
                          regActiveCell.rowIndex === idx && regActiveCell.key === 'description'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedRegIndex(idx);
                          setHoveredRegIndex(idx);
                          setRegActiveCell({ rowIndex: idx, key: 'description' });
                        }}
                      >
                        <VSCodeTextArea
                          data-edit-key="description"
                          className="w-full"
                          rows={1}
                          value={reg.description || ''}
                          onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                            onUpdate(
                              ['registers', idx, 'description'],
                              (e.target as HTMLTextAreaElement).value
                            )
                          }
                        />
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <KeyboardShortcutsButton context="block" />
    </div>
  );
}

import React, { useEffect, useRef, useState } from 'react';
import type { YamlUpdateHandler } from '../../types/editor';
import { VSCodeTextField, VSCodeTextArea } from '@vscode/webview-ui-toolkit/react';
import { KeyboardShortcutsButton } from '../../shared/components';
import AddressMapVisualizer from '../AddressMapVisualizer';
import { FIELD_COLORS, FIELD_COLOR_KEYS } from '../../shared/colors';
import {
  SpatialInsertionService,
  AddressBlockRuntimeDef,
} from '../../services/SpatialInsertionService';

// ---------------------------------------------------------------------------
// Types
// ---------------------------------------------------------------------------

type BlockEditKey = 'name' | 'base' | 'size' | 'usage' | 'description';
type BlockActiveCell = { rowIndex: number; key: BlockEditKey };
const BLOCK_COLUMN_ORDER: BlockEditKey[] = ['name', 'base', 'size', 'usage', 'description'];

export interface MemoryMapBlockDef extends AddressBlockRuntimeDef {
  usage?: string;
  description?: string;
}

export interface MemoryMapEditorProps {
  /** The memory map object (has name, description, address_blocks / addressBlocks). */
  memoryMap: {
    name?: string;
    description?: string;
    address_blocks?: MemoryMapBlockDef[];
    addressBlocks?: MemoryMapBlockDef[];
    [k: string]: unknown;
  };
  selectionMeta?: {
    absoluteAddress?: number;
    relativeOffset?: number;
    focusDetails?: boolean;
  };
  onUpdate: YamlUpdateHandler;
  onNavigateToBlock?: (blockIndex: number) => void;
}

// ---------------------------------------------------------------------------
// Helpers
// ---------------------------------------------------------------------------

/**
 * Calculates block size in bytes based on its registers or explicit size field.
 */
function calculateBlockSize(block: MemoryMapBlockDef): number {
  const registers = block?.registers || [];
  if (registers.length === 0) {
    const sz = block?.size ?? block?.range ?? 4;
    return typeof sz === 'string' ? Number.parseInt(sz, 10) || 4 : sz;
  }
  let totalSize = 0;
  for (const reg of registers as Record<string, unknown>[]) {
    if (reg.__kind === 'array') {
      const count = (reg.count as number) || 1;
      const stride = (reg.stride as number) || 4;
      totalSize += count * stride;
    } else {
      totalSize += 4;
    }
  }
  return totalSize;
}

// ---------------------------------------------------------------------------
// Component
// ---------------------------------------------------------------------------

/**
 * Renders and manages editing of a memory map's address blocks,
 * including the AddressMapVisualizer and keyboard-navigable blocks table.
 */
export function MemoryMapEditor({
  memoryMap,
  selectionMeta,
  onUpdate,
  onNavigateToBlock,
}: MemoryMapEditorProps) {
  const blocks = memoryMap?.address_blocks || memoryMap?.addressBlocks || [];

  const [selectedBlockIndex, setSelectedBlockIndex] = useState<number>(-1);
  const [hoveredBlockIndex, setHoveredBlockIndex] = useState<number | null>(null);
  const [blockActiveCell, setBlockActiveCell] = useState<BlockActiveCell>({
    rowIndex: -1,
    key: 'name',
  });
  const [insertError, setInsertError] = useState<string | null>(null);

  const focusRef = useRef<HTMLDivElement | null>(null);

  // Auto-focus on explicit request.
  useEffect(() => {
    if (!selectionMeta?.focusDetails) {
      return;
    }
    const id = window.setTimeout(() => {
      focusRef.current?.focus();
    }, 0);
    return () => window.clearTimeout(id);
  }, [selectionMeta?.focusDetails, memoryMap?.name]);

  // Clamp selection when map changes.
  useEffect(() => {
    const currentBlocks = memoryMap?.address_blocks || memoryMap?.addressBlocks || [];
    if (!Array.isArray(currentBlocks) || currentBlocks.length === 0) {
      setSelectedBlockIndex(-1);
      setBlockActiveCell({ rowIndex: -1, key: 'name' });
      return;
    }
    setSelectedBlockIndex((prev) => {
      if (prev < 0) {
        return 0;
      }
      if (prev >= currentBlocks.length) {
        return currentBlocks.length - 1;
      }
      return prev;
    });
    setBlockActiveCell((prev) => {
      const rowIndex = prev.rowIndex < 0 ? 0 : Math.min(currentBlocks.length - 1, prev.rowIndex);
      const key = BLOCK_COLUMN_ORDER.includes(prev.key) ? prev.key : 'name';
      return { rowIndex, key };
    });
  }, [memoryMap?.name, (memoryMap?.address_blocks || memoryMap?.addressBlocks || []).length]);

  // Escape: return focus from inline editor back to the table.
  useEffect(() => {
    const onKeyDown = (e: KeyboardEvent) => {
      if (e.key !== 'Escape') {
        return;
      }
      const activeEl = document.activeElement as HTMLElement | null;
      if (!activeEl) {
        return;
      }
      const inBlocks =
        !!focusRef.current && focusRef.current.contains(activeEl) && activeEl !== focusRef.current;
      if (!inBlocks) {
        return;
      }
      e.preventDefault();
      e.stopPropagation();
      try {
        (activeEl as any).blur?.();
      } catch {
        // ignore
      }
      window.setTimeout(() => focusRef.current?.focus(), 0);
    };
    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  }, []);

  // Keyboard shortcuts.
  useEffect(() => {
    const liveBlocks = memoryMap?.address_blocks || memoryMap?.addressBlocks || [];

    const tryInsertBlock = (after: boolean) => {
      setInsertError(null);
      const result = after
        ? SpatialInsertionService.insertBlockAfter(liveBlocks, selectedBlockIndex)
        : SpatialInsertionService.insertBlockBefore(liveBlocks, selectedBlockIndex);

      if (result.error) {
        setInsertError(result.error);
        return;
      }

      const newIdx = result.newIndex;
      onUpdate(['addressBlocks'], result.items);
      setSelectedBlockIndex(newIdx);
      setHoveredBlockIndex(newIdx);
      setBlockActiveCell({ rowIndex: newIdx, key: 'name' });
      window.setTimeout(() => {
        document
          .querySelector(`tr[data-block-idx="${newIdx}"]`)
          ?.scrollIntoView({ block: 'center' });
      }, 100);
    };

    const onKeyDown = (e: KeyboardEvent) => {
      let keyLower = (e.key || '').toLowerCase();
      if (e.altKey && e.code) {
        if (e.code === 'KeyH') {
          keyLower = 'h';
        }
        if (e.code === 'KeyJ') {
          keyLower = 'j';
        }
        if (e.code === 'KeyK') {
          keyLower = 'k';
        }
        if (e.code === 'KeyL') {
          keyLower = 'l';
        }
      }
      const vimToArrow: Record<string, 'ArrowLeft' | 'ArrowDown' | 'ArrowUp' | 'ArrowRight'> = {
        h: 'ArrowLeft',
        j: 'ArrowDown',
        k: 'ArrowUp',
        l: 'ArrowRight',
      };
      const normalizedKey: string = vimToArrow[keyLower] ?? e.key;

      const isArrow = ['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(normalizedKey);
      const isEdit = normalizedKey === 'F2' || keyLower === 'e';
      const isDelete = keyLower === 'd' || e.key === 'Delete';
      const isInsertAfter = keyLower === 'o' && !e.shiftKey;
      const isInsertBefore = keyLower === 'o' && e.shiftKey;
      if (!isArrow && !isEdit && !isDelete && !isInsertAfter && !isInsertBefore) {
        return;
      }
      if (e.ctrlKey || e.metaKey) {
        return;
      }

      const activeEl = document.activeElement as HTMLElement | null;
      const isInBlocksArea =
        !!focusRef.current &&
        !!activeEl &&
        (activeEl === focusRef.current || focusRef.current.contains(activeEl));
      if (!isInBlocksArea) {
        return;
      }

      const target = e.target as HTMLElement | null;
      const isTypingTarget = !!target?.closest(
        'input, textarea, select, [contenteditable="true"], vscode-text-field, vscode-text-area, vscode-dropdown'
      );
      if (isTypingTarget) {
        return;
      }

      const scrollToCell = (rowIndex: number, key: BlockEditKey) => {
        window.setTimeout(() => {
          const row = document.querySelector(`tr[data-block-idx="${rowIndex}"]`);
          row?.scrollIntoView({ block: 'nearest' });
          const cell = row?.querySelector(`td[data-col-key="${key}"]`) as HTMLElement | null;
          cell?.scrollIntoView({ block: 'nearest', inline: 'nearest' });
        }, 0);
      };

      const focusEditor = (rowIndex: number, key: BlockEditKey) => {
        window.setTimeout(() => {
          const row = document.querySelector(`tr[data-block-idx="${rowIndex}"]`);
          const editor = row?.querySelector(`[data-edit-key="${key}"]`) as HTMLElement | null;
          editor?.focus?.();
        }, 0);
      };

      const currentRow =
        blockActiveCell.rowIndex >= 0
          ? blockActiveCell.rowIndex
          : selectedBlockIndex >= 0
            ? selectedBlockIndex
            : 0;
      const currentKey: BlockEditKey = BLOCK_COLUMN_ORDER.includes(blockActiveCell.key)
        ? blockActiveCell.key
        : 'name';

      if (isEdit) {
        if (currentRow < 0 || currentRow >= liveBlocks.length) {
          return;
        }
        e.preventDefault();
        e.stopPropagation();
        setSelectedBlockIndex(currentRow);
        setHoveredBlockIndex(currentRow);
        setBlockActiveCell({ rowIndex: currentRow, key: currentKey });
        focusEditor(currentRow, currentKey);
        return;
      }
      if (isInsertAfter || isInsertBefore) {
        e.preventDefault();
        e.stopPropagation();
        tryInsertBlock(isInsertAfter);
        return;
      }
      if (isDelete) {
        if (currentRow < 0 || currentRow >= liveBlocks.length) {
          return;
        }
        e.preventDefault();
        e.stopPropagation();
        const newBlocks = liveBlocks.filter((_: unknown, i: number) => i !== currentRow);
        onUpdate(['addressBlocks'], newBlocks);
        const nextRow = currentRow > 0 ? currentRow - 1 : newBlocks.length > 0 ? 0 : -1;
        setSelectedBlockIndex(nextRow);
        setHoveredBlockIndex(nextRow);
        setBlockActiveCell({ rowIndex: nextRow, key: currentKey });
        return;
      }

      e.preventDefault();
      e.stopPropagation();
      if (liveBlocks.length === 0) {
        return;
      }

      const isVertical = normalizedKey === 'ArrowUp' || normalizedKey === 'ArrowDown';
      const delta = normalizedKey === 'ArrowUp' || normalizedKey === 'ArrowLeft' ? -1 : 1;

      if (isVertical) {
        const nextRow = Math.max(0, Math.min(liveBlocks.length - 1, currentRow + delta));
        setSelectedBlockIndex(nextRow);
        setHoveredBlockIndex(nextRow);
        setBlockActiveCell({ rowIndex: nextRow, key: currentKey });
        scrollToCell(nextRow, currentKey);
        return;
      }

      const currentCol = Math.max(0, BLOCK_COLUMN_ORDER.indexOf(currentKey));
      const nextCol = Math.max(0, Math.min(BLOCK_COLUMN_ORDER.length - 1, currentCol + delta));
      const nextKey = BLOCK_COLUMN_ORDER[nextCol] ?? 'name';
      setSelectedBlockIndex(currentRow);
      setHoveredBlockIndex(currentRow);
      setBlockActiveCell({ rowIndex: currentRow, key: nextKey });
      scrollToCell(currentRow, nextKey);
    };

    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  }, [memoryMap, selectedBlockIndex, hoveredBlockIndex, blockActiveCell, onUpdate]);

  const toHex = (n: number) => `0x${Math.max(0, n).toString(16).toUpperCase()}`;
  const getBlockColor = (idx: number) => FIELD_COLOR_KEYS[idx % FIELD_COLOR_KEYS.length];

  return (
    <div className="flex flex-col w-full h-full min-h-0">
      {/* Header + AddressMapVisualizer */}
      <div className="vscode-surface border-b vscode-border p-8 flex flex-col gap-6 shrink-0 relative overflow-hidden">
        <div className="flex justify-between items-start relative z-10">
          <div>
            <h2 className="text-2xl font-bold font-mono tracking-tight">
              {memoryMap?.name || 'Memory Map'}
            </h2>
            <p className="vscode-muted text-sm mt-1 max-w-2xl">
              {memoryMap?.description || 'Address space layout'}
            </p>
          </div>
        </div>
        <div className="w-full relative z-10 mt-2 select-none">
          <AddressMapVisualizer
            blocks={blocks}
            hoveredBlockIndex={hoveredBlockIndex}
            setHoveredBlockIndex={setHoveredBlockIndex}
            onBlockClick={onNavigateToBlock}
          />
        </div>
      </div>

      {/* Blocks table */}
      <div className="flex-1 flex overflow-hidden min-h-0">
        <div className="flex-1 vscode-surface min-h-0 flex flex-col">
          <div
            ref={focusRef}
            tabIndex={0}
            data-blocks-table="true"
            className="flex-1 overflow-auto min-h-0 outline-none focus:outline-none"
          >
            {insertError ? (
              <div className="vscode-error px-4 py-2 text-xs">{insertError}</div>
            ) : null}
            <table className="w-full text-left border-collapse table-fixed">
              <colgroup>
                <col className="w-[25%] min-w-[200px]" />
                <col className="w-[20%] min-w-[120px]" />
                <col className="w-[15%] min-w-[100px]" />
                <col className="w-[15%] min-w-[100px]" />
                <col className="w-[25%]" />
              </colgroup>
              <thead className="vscode-surface-alt text-xs font-semibold vscode-muted uppercase tracking-wider sticky top-0 z-10 shadow-sm">
                <tr className="h-12">
                  <th className="px-6 py-3 border-b vscode-border align-middle">Name</th>
                  <th className="px-4 py-3 border-b vscode-border align-middle">Base Address</th>
                  <th className="px-4 py-3 border-b vscode-border align-middle">Size</th>
                  <th className="px-4 py-3 border-b vscode-border align-middle">Usage</th>
                  <th className="px-6 py-3 border-b vscode-border align-middle">Description</th>
                </tr>
              </thead>
              <tbody className="divide-y vscode-border text-sm">
                {blocks.map((block: MemoryMapBlockDef, idx: number) => {
                  const color = getBlockColor(idx);
                  const base = block.base_address ?? block.offset ?? 0;
                  const size = calculateBlockSize(block);

                  return (
                    <tr
                      key={idx}
                      data-block-idx={idx}
                      className={`group transition-colors border-l-4 border-transparent h-12 ${
                        idx === selectedBlockIndex
                          ? 'vscode-focus-border vscode-row-selected'
                          : idx === hoveredBlockIndex
                            ? 'vscode-focus-border vscode-row-hover'
                            : ''
                      }`}
                      onMouseEnter={() => setHoveredBlockIndex(idx)}
                      onMouseLeave={() => setHoveredBlockIndex(null)}
                      onClick={() => {
                        setSelectedBlockIndex(idx);
                        setHoveredBlockIndex(idx);
                        setBlockActiveCell((prev) => ({ rowIndex: idx, key: prev.key }));
                      }}
                    >
                      <td
                        data-col-key="name"
                        className={`px-6 py-2 font-medium align-middle ${
                          blockActiveCell.rowIndex === idx && blockActiveCell.key === 'name'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedBlockIndex(idx);
                          setHoveredBlockIndex(idx);
                          setBlockActiveCell({ rowIndex: idx, key: 'name' });
                        }}
                      >
                        <div className="flex items-center gap-2">
                          <div
                            className="w-2.5 h-2.5 rounded-sm"
                            style={{ backgroundColor: FIELD_COLORS[color] || color }}
                          />
                          <VSCodeTextField
                            data-edit-key="name"
                            className="flex-1"
                            value={block.name || ''}
                            onBlur={(e: Event | React.FocusEvent<HTMLElement>) =>
                              onUpdate(
                                ['addressBlocks', idx, 'name'],
                                (e.target as HTMLInputElement).value
                              )
                            }
                          />
                        </div>
                      </td>
                      <td
                        data-col-key="base"
                        className={`px-4 py-2 font-mono vscode-muted align-middle ${
                          blockActiveCell.rowIndex === idx && blockActiveCell.key === 'base'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedBlockIndex(idx);
                          setHoveredBlockIndex(idx);
                          setBlockActiveCell({ rowIndex: idx, key: 'base' });
                        }}
                      >
                        <VSCodeTextField
                          data-edit-key="base"
                          className="w-full font-mono"
                          value={toHex(base)}
                          onInput={(e: Event | React.FormEvent<HTMLElement>) => {
                            const val = Number.parseInt((e.target as HTMLInputElement).value, 0);
                            if (!Number.isNaN(val)) {
                              onUpdate(['addressBlocks', idx, 'offset'], val);
                            }
                          }}
                        />
                      </td>
                      <td
                        data-col-key="size"
                        className={`px-4 py-2 font-mono vscode-muted align-middle ${
                          blockActiveCell.rowIndex === idx && blockActiveCell.key === 'size'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedBlockIndex(idx);
                          setHoveredBlockIndex(idx);
                          setBlockActiveCell({ rowIndex: idx, key: 'size' });
                        }}
                      >
                        {size < 1024 ? `${size}B` : `${(size / 1024).toFixed(1)}KB`}
                      </td>
                      <td
                        data-col-key="usage"
                        className={`px-4 py-2 align-middle ${
                          blockActiveCell.rowIndex === idx && blockActiveCell.key === 'usage'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedBlockIndex(idx);
                          setHoveredBlockIndex(idx);
                          setBlockActiveCell({ rowIndex: idx, key: 'usage' });
                        }}
                      >
                        <span className="px-2 py-0.5 rounded text-xs font-medium vscode-badge whitespace-nowrap">
                          {block.usage || 'register'}
                        </span>
                      </td>
                      <td
                        data-col-key="description"
                        className={`px-6 py-2 vscode-muted align-middle ${
                          blockActiveCell.rowIndex === idx && blockActiveCell.key === 'description'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedBlockIndex(idx);
                          setHoveredBlockIndex(idx);
                          setBlockActiveCell({ rowIndex: idx, key: 'description' });
                        }}
                      >
                        <VSCodeTextArea
                          data-edit-key="description"
                          className="w-full"
                          rows={1}
                          value={block.description || ''}
                          onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                            onUpdate(
                              ['addressBlocks', idx, 'description'],
                              (e.target as HTMLInputElement).value
                            )
                          }
                        />
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <KeyboardShortcutsButton context="memoryMap" />
    </div>
  );
}

import React, { useState } from 'react';
import type { YamlUpdateHandler } from '../../types/editor';
import { VSCodeDropdown, VSCodeTextField } from '@vscode/webview-ui-toolkit/react';
import { KeyboardShortcutsButton } from '../../shared/components';
import RegisterMapVisualizer from '../RegisterMapVisualizer';

// ---------------------------------------------------------------------------
// Types
// ---------------------------------------------------------------------------

type RegEditKey = 'name' | 'offset' | 'access' | 'description';
type RegActiveCell = { rowIndex: number; key: RegEditKey };
const REG_COLUMN_ORDER: RegEditKey[] = ['name', 'offset', 'access', 'description'];

export interface RegisterModel {
  name?: string;
  offset?: number | string;
  address_offset?: number | string;
  __kind?: string;
  count?: number;
  stride?: number;
  access?: string;
  description?: string;
  registers?: RegisterModel[];
  fields?: unknown[];
  [key: string]: unknown;
}

export interface RegisterArrayEditorProps {
  /** The register array definition object. */
  registerArray: RegisterModel;
  onUpdate: YamlUpdateHandler;
}

// ---------------------------------------------------------------------------
// Component
// ---------------------------------------------------------------------------

/**
 * Renders and manages editing of a register array definition:
 * - Array name, base offset, count, stride
 * - Inline RegisterMapVisualizer for nested template registers
 * - Keyboard-navigable nested registers table
 */
export function RegisterArrayEditor({ registerArray, onUpdate }: RegisterArrayEditorProps) {
  const arr = registerArray;
  const nestedRegisters = arr?.registers || [];
  const baseOffset = arr?.address_offset ?? 0;

  const [selectedRegIndex, setSelectedRegIndex] = useState<number>(-1);
  const [hoveredRegIndex, setHoveredRegIndex] = useState<number | null>(null);
  const [regActiveCell, setRegActiveCell] = useState<RegActiveCell>({
    rowIndex: -1,
    key: 'name',
  });

  const toHex = (n: number) => `0x${Math.max(0, n).toString(16).toUpperCase()}`;

  const scrollToCell = (rowIndex: number, key: string) => {
    window.setTimeout(() => {
      const row = document.querySelector(`tr[data-reg-idx="${rowIndex}"]`);
      row?.scrollIntoView({ block: 'nearest' });
      const cell = row?.querySelector(`td[data-col-key="${key}"]`);
      cell?.scrollIntoView({ block: 'nearest', inline: 'nearest' });
    }, 0);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    const keyLower = e.key.toLowerCase();
    const vimToArrow: Record<string, string> = {
      h: 'ArrowLeft',
      j: 'ArrowDown',
      k: 'ArrowUp',
      l: 'ArrowRight',
    };
    const normalizedKey = vimToArrow[keyLower] ?? e.key;

    const isArrow = ['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(normalizedKey);
    const isEdit = normalizedKey === 'F2' || keyLower === 'e' || keyLower === 'enter';
    const isDelete = keyLower === 'd' || e.key === 'Delete';
    const isInsertAfter = keyLower === 'o' && !e.shiftKey;
    const isInsertBefore = keyLower === 'o' && e.shiftKey;

    if (!isArrow && !isEdit && !isDelete && !isInsertAfter && !isInsertBefore) {
      return;
    }
    if (e.ctrlKey || e.metaKey) {
      return;
    }

    const target = e.target as HTMLElement | null;
    const isTypingTarget = !!target?.closest(
      'input, textarea, select, [contenteditable="true"], vscode-text-field, vscode-text-area, vscode-dropdown'
    );
    if (isTypingTarget) {
      return;
    }

    const currentRow = selectedRegIndex >= 0 ? selectedRegIndex : 0;

    if (isInsertAfter || isInsertBefore) {
      e.preventDefault();
      e.stopPropagation();

      let maxN = 0;
      for (const r of nestedRegisters) {
        const match = r.name?.match(/^reg(\d+)$/i);
        if (match) {
          maxN = Math.max(maxN, parseInt(match[1], 10));
        }
      }
      const newName = `reg${maxN + 1}`;
      const selIdx = selectedRegIndex >= 0 ? selectedRegIndex : nestedRegisters.length - 1;
      const selected = nestedRegisters[selIdx];
      const selectedOffset = selected?.address_offset ?? selected?.offset ?? 0;
      const newOffset = isInsertAfter
        ? Number(selectedOffset) + 4
        : Math.max(0, Number(selectedOffset) - 4);

      const newReg = {
        name: newName,
        offset: newOffset,
        address_offset: newOffset,
        access: 'read-write',
        description: '',
        fields: [{ name: 'data', bits: '[31:0]', access: 'read-write', description: '' }],
      };

      let newRegs: RegisterModel[];
      let newIdx: number;
      if (isInsertAfter) {
        newRegs = [
          ...nestedRegisters.slice(0, selIdx + 1),
          newReg,
          ...nestedRegisters.slice(selIdx + 1),
        ];
        newIdx = selIdx + 1;
      } else {
        newRegs = [...nestedRegisters.slice(0, selIdx), newReg, ...nestedRegisters.slice(selIdx)];
        newIdx = selIdx;
      }

      onUpdate(['registers'], newRegs as unknown[]);
      setSelectedRegIndex(newIdx);
      setHoveredRegIndex(newIdx);
      setRegActiveCell({ rowIndex: newIdx, key: 'name' });
      scrollToCell(newIdx, 'name');
      return;
    }

    if (isDelete) {
      if (currentRow < 0 || currentRow >= nestedRegisters.length) {
        return;
      }
      e.preventDefault();
      e.stopPropagation();
      const newRegs = nestedRegisters.filter((_: RegisterModel, i: number) => i !== currentRow);
      onUpdate(['registers'], newRegs as unknown[]);
      const nextRow = currentRow > 0 ? currentRow - 1 : newRegs.length > 0 ? 0 : -1;
      setSelectedRegIndex(nextRow);
      setHoveredRegIndex(nextRow);
      return;
    }

    if (isArrow) {
      e.preventDefault();
      const isVertical = normalizedKey === 'ArrowUp' || normalizedKey === 'ArrowDown';
      const delta = normalizedKey === 'ArrowUp' || normalizedKey === 'ArrowLeft' ? -1 : 1;

      if (isVertical) {
        const next = Math.max(0, Math.min(nestedRegisters.length - 1, currentRow + delta));
        setSelectedRegIndex(next);
        setHoveredRegIndex(next);
        setRegActiveCell({ rowIndex: next, key: regActiveCell.key });
        scrollToCell(next, regActiveCell.key);
      } else {
        const currentKey = regActiveCell.key;
        const currentCol = Math.max(0, REG_COLUMN_ORDER.indexOf(currentKey));
        const nextCol = Math.max(0, Math.min(REG_COLUMN_ORDER.length - 1, currentCol + delta));
        const nextKey = REG_COLUMN_ORDER[nextCol];
        setRegActiveCell({ rowIndex: currentRow, key: nextKey });
        scrollToCell(currentRow, nextKey);
      }
    }

    if (isEdit) {
      e.preventDefault();
      e.stopPropagation();
      setRegActiveCell({ rowIndex: currentRow, key: regActiveCell.key });
    }
  };

  return (
    <div className="flex flex-col w-full h-full min-h-0">
      {/* Header */}
      <div className="vscode-surface border-b vscode-border p-8 flex flex-col gap-6 shrink-0">
        <div className="flex justify-between items-start">
          <div>
            <h2 className="text-2xl font-bold font-mono tracking-tight">
              {arr?.name || 'Register Array'}
            </h2>
            <p className="vscode-muted text-sm mt-1 max-w-2xl">
              {arr?.description || 'Register array'} • {arr?.count || 1} instances ×{' '}
              {arr?.stride || 4} bytes
            </p>
          </div>
        </div>

        {/* Array Properties */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 vscode-surface-alt p-4 rounded-lg">
          <div>
            <label className="text-xs vscode-muted block mb-1">Name</label>
            <VSCodeTextField
              value={arr?.name || ''}
              onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                onUpdate(['name'], (e.target as HTMLInputElement).value)
              }
              className="w-full"
            />
          </div>
          <div>
            <label className="text-xs vscode-muted block mb-1">Base Offset</label>
            <span className="font-mono text-sm">{toHex(Number(baseOffset))}</span>
          </div>
          <div>
            <label className="text-xs vscode-muted block mb-1">Count</label>
            <VSCodeTextField
              value={String(arr?.count || 1)}
              onInput={(e: Event | React.FormEvent<HTMLElement>) => {
                const val = parseInt((e.target as HTMLInputElement).value, 10);
                if (!isNaN(val) && val > 0) {
                  onUpdate(['count'], val);
                }
              }}
              className="w-24"
            />
          </div>
          <div>
            <label className="text-xs vscode-muted block mb-1">Stride (bytes)</label>
            <VSCodeTextField
              value={String(arr?.stride || 4)}
              onInput={(e: Event | React.FormEvent<HTMLElement>) => {
                const val = parseInt((e.target as HTMLInputElement).value, 10);
                if (!isNaN(val) && val > 0) {
                  onUpdate(['stride'], val);
                }
              }}
              className="w-24"
            />
          </div>
        </div>

        {/* Address summary */}
        <div className="text-sm vscode-muted">
          <span className="font-mono">
            {toHex(Number(baseOffset))} →{' '}
            {toHex(Number(baseOffset) + Number(arr?.count || 1) * Number(arr?.stride || 4) - 1)}
          </span>
          <span className="ml-2">({(arr?.count || 1) * (arr?.stride || 4)} bytes total)</span>
        </div>

        {/* RegisterMapVisualizer for nested registers */}
        <div className="w-full relative z-10 mt-4 select-none">
          <RegisterMapVisualizer
            registers={nestedRegisters}
            hoveredRegIndex={hoveredRegIndex}
            setHoveredRegIndex={setHoveredRegIndex}
            baseAddress={0}
            onReorderRegisters={(newRegs) => onUpdate(['registers'], newRegs)}
            onRegisterClick={(idx) => {
              setSelectedRegIndex(idx);
              setHoveredRegIndex(idx);
            }}
          />
        </div>
      </div>

      {/* Nested registers table */}
      <div className="flex-1 flex overflow-hidden min-h-0">
        <div className="flex-1 vscode-surface min-h-0 flex flex-col">
          <div
            tabIndex={0}
            data-registers-table="true"
            className="flex-1 overflow-auto min-h-0 outline-none focus:outline-none"
            onKeyDown={handleKeyDown}
          >
            <table className="w-full text-left border-collapse table-fixed">
              <colgroup>
                <col className="w-[30%] min-w-[200px]" />
                <col className="w-[20%] min-w-[120px]" />
                <col className="w-[15%] min-w-[100px]" />
                <col className="w-[35%]" />
              </colgroup>
              <thead className="vscode-surface-alt text-xs font-semibold vscode-muted uppercase tracking-wider sticky top-0 z-10 shadow-sm">
                <tr className="h-12">
                  <th className="px-6 py-3 border-b vscode-border align-middle">Name</th>
                  <th className="px-4 py-3 border-b vscode-border align-middle">Offset</th>
                  <th className="px-4 py-3 border-b vscode-border align-middle">Access</th>
                  <th className="px-6 py-3 border-b vscode-border align-middle">Description</th>
                </tr>
              </thead>
              <tbody className="divide-y vscode-border text-sm">
                {nestedRegisters.map((reg: RegisterModel, idx: number) => {
                  const regOffset = reg.address_offset ?? reg.offset ?? 0;
                  const isSelected = selectedRegIndex === idx;
                  const isHovered = hoveredRegIndex === idx;

                  return (
                    <tr
                      key={idx}
                      data-reg-idx={idx}
                      className={`group transition-colors border-l-4 border-transparent h-12 ${
                        isSelected
                          ? 'vscode-focus-border vscode-row-selected'
                          : isHovered
                            ? 'vscode-focus-border vscode-row-hover'
                            : ''
                      }`}
                      onMouseEnter={() => setHoveredRegIndex(idx)}
                      onMouseLeave={() => setHoveredRegIndex(null)}
                      onClick={() => {
                        setSelectedRegIndex(idx);
                        setHoveredRegIndex(idx);
                        setRegActiveCell((prev) => ({ rowIndex: idx, key: prev.key }));
                      }}
                    >
                      {/* NAME */}
                      <td
                        data-col-key="name"
                        className={`px-6 py-2 font-medium align-middle ${
                          regActiveCell.rowIndex === idx && regActiveCell.key === 'name'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedRegIndex(idx);
                          setRegActiveCell({ rowIndex: idx, key: 'name' });
                        }}
                      >
                        {regActiveCell.rowIndex === idx && regActiveCell.key === 'name' ? (
                          <VSCodeTextField
                            value={reg.name || ''}
                            onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                              onUpdate(
                                ['registers', idx, 'name'],
                                (e.target as HTMLInputElement).value
                              )
                            }
                            className="w-full font-mono"
                            autoFocus
                          />
                        ) : (
                          <span className="font-mono">{reg.name}</span>
                        )}
                      </td>

                      {/* OFFSET */}
                      <td
                        data-col-key="offset"
                        className={`px-4 py-2 font-mono text-xs align-middle ${
                          regActiveCell.rowIndex === idx && regActiveCell.key === 'offset'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedRegIndex(idx);
                          setRegActiveCell({ rowIndex: idx, key: 'offset' });
                        }}
                      >
                        {regActiveCell.rowIndex === idx && regActiveCell.key === 'offset' ? (
                          <VSCodeTextField
                            value={String(regOffset)}
                            onInput={(e: Event | React.FormEvent<HTMLElement>) => {
                              const val = parseInt((e.target as HTMLInputElement).value, 10);
                              if (!isNaN(val) && val >= 0) {
                                onUpdate(['registers', idx, 'offset'], val);
                                onUpdate(['registers', idx, 'address_offset'], val);
                              }
                            }}
                            className="w-full font-mono text-xs"
                            autoFocus
                          />
                        ) : (
                          <span>{`0x${Number(regOffset).toString(16).toUpperCase()}`}</span>
                        )}
                      </td>

                      {/* ACCESS */}
                      <td
                        data-col-key="access"
                        className={`px-4 py-2 text-xs align-middle ${
                          regActiveCell.rowIndex === idx && regActiveCell.key === 'access'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedRegIndex(idx);
                          setRegActiveCell({ rowIndex: idx, key: 'access' });
                        }}
                      >
                        {regActiveCell.rowIndex === idx && regActiveCell.key === 'access' ? (
                          <VSCodeDropdown
                            value={reg.access || 'read-write'}
                            onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                              onUpdate(
                                ['registers', idx, 'access'],
                                (e.target as HTMLInputElement).value
                              )
                            }
                            className="w-full"
                          >
                            {['read-write', 'read-only', 'write-only'].map((opt) => (
                              <option key={opt} value={opt}>
                                {opt}
                              </option>
                            ))}
                          </VSCodeDropdown>
                        ) : (
                          <span
                            className={`px-2 py-0.5 rounded-full text-[10px] uppercase font-semibold bg-opacity-20 ${
                              reg.access === 'read-only'
                                ? 'bg-blue-500 text-blue-500'
                                : reg.access === 'write-only'
                                  ? 'bg-orange-500 text-orange-500'
                                  : 'bg-green-500 text-green-500'
                            }`}
                          >
                            {reg.access || 'RW'}
                          </span>
                        )}
                      </td>

                      {/* DESCRIPTION */}
                      <td
                        data-col-key="description"
                        className={`px-6 py-2 align-middle ${
                          regActiveCell.rowIndex === idx && regActiveCell.key === 'description'
                            ? 'vscode-cell-active'
                            : ''
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedRegIndex(idx);
                          setRegActiveCell({ rowIndex: idx, key: 'description' });
                        }}
                      >
                        {regActiveCell.rowIndex === idx && regActiveCell.key === 'description' ? (
                          <VSCodeTextField
                            value={reg.description || ''}
                            onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                              onUpdate(
                                ['registers', idx, 'description'],
                                (e.target as HTMLInputElement).value
                              )
                            }
                            className="w-full"
                            autoFocus
                          />
                        ) : (
                          <span className="truncate block max-w-[300px] opacity-70">
                            {reg.description}
                          </span>
                        )}
                      </td>
                    </tr>
                  );
                })}
                {nestedRegisters.length === 0 && (
                  <tr>
                    <td colSpan={4} className="px-4 py-8 text-center vscode-muted">
                      No nested registers. Press{' '}
                      <kbd className="px-1 rounded vscode-surface-alt">o</kbd> to add one.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>

        <div className="flex-none p-4 bg-vscode-editor-background border-t vscode-border flex justify-between items-center">
          <p className="text-xs vscode-muted">
            These registers are replicated {arr?.count || 1} times at {arr?.stride || 4}-byte
            intervals.
          </p>
          <KeyboardShortcutsButton context="array" />
        </div>
      </div>
    </div>
  );
}

import React from 'react';
import type { YamlUpdateHandler } from '../../types/editor';
import {
  VSCodeDropdown,
  VSCodeOption,
  VSCodeTextField,
  VSCodeTextArea,
} from '@vscode/webview-ui-toolkit/react';
import { EditableTable } from '../../shared/components';
import { FIELD_COLORS, getFieldColor } from '../../shared/colors';
import { formatBits } from '../../algorithms/BitFieldRepacker';
import { fieldToBitsString } from '../../utils/BitFieldUtils';
import { validateVhdlIdentifier } from '../../shared/utils/validation';
import type { FieldEditorState } from '../../hooks/useFieldEditor';

export interface FieldDef {
  name?: string | null;
  bits?: string | null;
  bit_offset?: number | null;
  bit_width?: number | null;
  bit_range?: [number, number] | null;
  access?: string | null;
  reset_value?: number | null;
  description?: string | null;
  [key: string]: unknown;
}

// ---------------------------------------------------------------------------
// Constants
// ---------------------------------------------------------------------------

const ACCESS_OPTIONS = [
  'read-only',
  'write-only',
  'read-write',
  'write-1-to-clear',
  'read-write-1-to-clear',
];

const FIELD_TABLE_COLUMNS = [
  { key: 'name', header: 'Name' },
  { key: 'bits', header: 'Bit(s)' },
  { key: 'access', header: 'Access' },
  { key: 'reset', header: 'Reset' },
  { key: 'description', header: 'Description' },
];

// ---------------------------------------------------------------------------
// Validation helpers (register-editor utilities)
// ---------------------------------------------------------------------------

/** Returns the width represented by "[N:M]" or "[N]" strings. */
function parseBitsWidth(bits: string): number | null {
  const match = bits.trim().match(/^\[(\d+)(?::(\d+))?\]$/);
  if (!match) {
    return null;
  }
  const n = parseInt(match[1], 10);
  const m = match[2] ? parseInt(match[2], 10) : n;
  return Math.abs(n - m) + 1;
}

/** Returns an error message for an invalid bits string, or null if valid. */
function validateBitsString(bits: string): string | null {
  const trimmed = bits.trim();
  if (!/^\[\d+(?::\d+)?\]$/.test(trimmed)) {
    return 'Format must be [N:M] or [N]';
  }
  const match = trimmed.match(/\[(\d+)(?::(\d+))?\]/);
  if (!match) {
    return 'Invalid format';
  }
  const n = parseInt(match[1], 10);
  const m = match[2] ? parseInt(match[2], 10) : n;
  if (n < 0 || m < 0) {
    return 'Bit indices must be >= 0';
  }
  if (n < m) {
    return 'MSB must be >= LSB';
  }
  return null;
}

/** Parses a bit string like "[7:4]" or "[3]" into {bit_offset, bit_width, bit_range}. */
function parseBitsInput(text: string) {
  const trimmed = text.trim().replace(/[\[\]]/g, ''); // eslint-disable-line no-useless-escape
  if (!trimmed) {
    return null;
  }
  const parts = trimmed.split(':').map((p) => Number(p.trim()));
  if (parts.some((p) => Number.isNaN(p))) {
    return null;
  }
  let msb: number;
  let lsb: number;
  if (parts.length === 1) {
    msb = parts[0];
    lsb = parts[0];
  } else {
    [msb, lsb] = parts as [number, number];
  }
  if (!Number.isFinite(msb) || !Number.isFinite(lsb)) {
    return null;
  }
  if (msb < lsb) {
    const tmp = msb;
    msb = lsb;
    lsb = tmp;
  }
  return {
    bit_offset: lsb,
    bit_width: msb - lsb + 1,
    bit_range: [msb, lsb] as [number, number],
  };
}

/** Parses a hex or decimal string to a number, or returns null. */
function parseReset(text: string): number | null {
  const s = text.trim();
  if (!s) {
    return null;
  }
  const v = Number.parseInt(s, 0);
  return Number.isFinite(v) ? v : null;
}

function getFieldBitWidth(f: FieldDef): number {
  const w = Number(f?.bit_width);
  if (Number.isFinite(w) && w > 0) {
    return w;
  }
  const br = f?.bit_range;
  if (Array.isArray(br) && br.length === 2) {
    const msb = Number(br[0]);
    const lsb = Number(br[1]);
    if (Number.isFinite(msb) && Number.isFinite(lsb)) {
      return Math.abs(msb - lsb) + 1;
    }
  }
  return 1;
}

function validateResetForField(f: FieldDef, value: number | null): string | null {
  if (value === null) {
    return null;
  }
  if (!Number.isFinite(value)) {
    return 'Invalid number';
  }
  if (value < 0) {
    return 'Reset must be >= 0';
  }
  const width = getFieldBitWidth(f);
  const max = width >= 53 ? Number.MAX_SAFE_INTEGER : Math.pow(2, width) - 1;
  if (value > max) {
    return `Reset too large for ${width} bit(s)`;
  }
  return null;
}

// ---------------------------------------------------------------------------
// Props
// ---------------------------------------------------------------------------

export interface FieldsTableProps {
  /** Normalised bit fields for the current register. */
  fields: FieldDef[];
  /** Register width in bits (used for overflow validation). */
  registerSize: number;
  /** Callback to commit a YAML path + value change. */
  onUpdate: YamlUpdateHandler;
  /** All editing state from the useFieldEditor hook. */
  fieldEditor: FieldEditorState;
}

// ---------------------------------------------------------------------------
// Component
// ---------------------------------------------------------------------------

/**
 * Renders the editable bit fields table for a register, including the
 * move-up/move-down toolbar.
 * Delegates insertion/deletion/editing to the useFieldEditor hook state.
 */
export function FieldsTable({ fields, registerSize, onUpdate, fieldEditor }: FieldsTableProps) {
  const {
    selectedFieldIndex,
    setSelectedFieldIndex,
    hoveredFieldIndex,
    setHoveredFieldIndex,
    setSelectedEditKey,
    activeCell,
    setActiveCell,
    nameDrafts,
    setNameDrafts,
    nameErrors,
    setNameErrors,
    bitsDrafts,
    setBitsDrafts,
    bitsErrors,
    setBitsErrors,
    dragPreviewRanges,
    resetDrafts,
    setResetDrafts,
    resetErrors,
    setResetErrors,
    insertError,
    focusRef,
    errorRef,
    ensureDraftsInitialized,
    moveSelectedField,
  } = fieldEditor;

  return (
    <div className="flex-1 flex overflow-hidden min-h-0">
      <div className="flex-1 vscode-surface border-r vscode-border min-h-0 flex flex-col">
        {/* Toolbar: move up / move down */}
        <div className="shrink-0 px-4 py-2 border-b vscode-border vscode-surface flex items-center justify-end gap-1">
          <button
            className="p-2 rounded-md transition-colors disabled:opacity-40 vscode-icon-button"
            onClick={() => moveSelectedField(-1)}
            disabled={selectedFieldIndex <= 0}
            title="Move field up"
            type="button"
          >
            <span className="codicon codicon-chevron-up"></span>
          </button>
          <button
            className="p-2 rounded-md transition-colors disabled:opacity-40 vscode-icon-button"
            onClick={() => moveSelectedField(1)}
            disabled={selectedFieldIndex < 0 || selectedFieldIndex >= fields.length - 1}
            title="Move field down"
            type="button"
          >
            <span className="codicon codicon-chevron-down"></span>
          </button>
        </div>

        {/* Scrollable table */}
        <div
          ref={focusRef}
          tabIndex={0}
          data-fields-table="true"
          className="flex-1 overflow-auto min-h-0 outline-none focus:outline-none"
          style={{ overflowY: 'auto', overflowX: 'auto' }}
        >
          {insertError ? (
            <div ref={errorRef} className="vscode-error px-4 py-2 text-xs">
              {insertError}
            </div>
          ) : null}

          <EditableTable
            rows={fields}
            columns={FIELD_TABLE_COLUMNS}
            showHeaderSection={false}
            showTableBorder={false}
            containerClassName=""
            tableWrapperClassName=""
            tableClassName="w-full text-left border-collapse table-fixed"
            renderTableContent={() => (
              <>
                <colgroup>
                  <col className="w-[18%] min-w-[120px]" />
                  <col className="w-[14%] min-w-[100px]" />
                  <col className="w-[14%] min-w-[120px]" />
                  <col className="w-[14%] min-w-[110px]" />
                  <col className="w-[40%] min-w-[240px]" />
                </colgroup>
                <thead className="vscode-surface-alt text-xs font-semibold vscode-muted uppercase tracking-wider sticky top-0 z-10 shadow-sm">
                  <tr className="h-12">
                    <th className="px-6 py-3 border-b vscode-border align-middle">Name</th>
                    <th className="px-4 py-3 border-b vscode-border align-middle">Bit(s)</th>
                    <th className="px-4 py-3 border-b vscode-border align-middle">Access</th>
                    <th className="px-4 py-3 border-b vscode-border align-middle">Reset</th>
                    <th className="px-6 py-3 border-b vscode-border align-middle">Description</th>
                  </tr>
                </thead>
                <tbody className="divide-y vscode-border text-sm">
                  {fields.map((field, index) => {
                    const bits = fieldToBitsString(field);
                    const color = getFieldColor(
                      field.name || `field${index}`,
                      field.bit_offset ?? undefined
                    );
                    const resetDisplay =
                      field.reset_value !== null && field.reset_value !== undefined
                        ? `0x${Number(field.reset_value).toString(16).toUpperCase()}`
                        : '';

                    const fieldKey =
                      field && field.name ? `${String(field.name)}` : `index-${index}`;
                    const nameValue = nameDrafts[fieldKey] ?? String(field.name ?? '');
                    const nameErr = nameErrors[fieldKey] ?? null;

                    const previewRange = dragPreviewRanges[index];
                    const bitsValue = previewRange
                      ? `[${previewRange[0]}:${previewRange[1]}]`
                      : (bitsDrafts[index] ?? bits);
                    const bitsErr = bitsErrors[index] ?? null;
                    const resetValue = resetDrafts[index] ?? (resetDisplay || '0x0');
                    const resetErr = resetErrors[index] ?? null;

                    return (
                      <tr
                        key={index}
                        data-field-index={index}
                        className={`group transition-colors border-l-4 border-transparent h-12 ${
                          index === selectedFieldIndex
                            ? 'vscode-focus-border vscode-row-selected'
                            : index === hoveredFieldIndex
                              ? 'vscode-focus-border vscode-row-hover'
                              : ''
                        }`}
                        style={{ position: 'relative' }}
                        onMouseEnter={() => setHoveredFieldIndex(index)}
                        onMouseLeave={() => setHoveredFieldIndex(null)}
                        onClick={() => {
                          setSelectedFieldIndex(index);
                          setHoveredFieldIndex(index);
                          setActiveCell((prev) => ({ rowIndex: index, key: prev.key }));
                          ensureDraftsInitialized(index);
                        }}
                        id={`row-${String(field.name || '')
                          .toLowerCase()
                          .replace(/[^a-z0-9_]/g, '-')}`}
                      >
                        <>
                          {/* NAME */}
                          <td
                            data-col-key="name"
                            className={`px-6 py-2 font-medium align-middle ${
                              activeCell.rowIndex === index && activeCell.key === 'name'
                                ? 'vscode-cell-active'
                                : ''
                            }`}
                            onClick={(e) => {
                              e.stopPropagation();
                              ensureDraftsInitialized(index);
                              setSelectedFieldIndex(index);
                              setHoveredFieldIndex(index);
                              setSelectedEditKey('name');
                              setActiveCell({ rowIndex: index, key: 'name' });
                            }}
                          >
                            <div className="flex flex-col justify-center">
                              <div className="flex items-center gap-2 h-10">
                                <div
                                  className="w-2.5 h-2.5 rounded-sm"
                                  style={{
                                    backgroundColor:
                                      color === 'gray'
                                        ? '#e5e7eb'
                                        : (FIELD_COLORS && FIELD_COLORS[color]) || color,
                                  }}
                                />
                                <VSCodeTextField
                                  data-edit-key="name"
                                  className="flex-1"
                                  value={nameValue}
                                  onFocus={() => {
                                    ensureDraftsInitialized(index);
                                    setSelectedFieldIndex(index);
                                    setHoveredFieldIndex(index);
                                    setSelectedEditKey('name');
                                    setActiveCell({ rowIndex: index, key: 'name' });
                                  }}
                                  onInput={(e: Event | React.FormEvent<HTMLElement>) => {
                                    const next = String((e.target as HTMLInputElement).value ?? '');
                                    setNameDrafts((prev) => ({
                                      ...prev,
                                      [fieldKey]: next,
                                    }));
                                    const err = validateVhdlIdentifier(next);
                                    setNameErrors((prev) => ({
                                      ...prev,
                                      [fieldKey]: err,
                                    }));
                                  }}
                                  onBlur={(e: Event | React.FocusEvent<HTMLElement>) => {
                                    const next = String((e.target as HTMLInputElement).value ?? '');
                                    const err = validateVhdlIdentifier(next);
                                    if (!err) {
                                      onUpdate(['fields', index, 'name'], next.trim());
                                    }
                                  }}
                                />
                              </div>
                              {nameErr ? (
                                <div className="text-xs vscode-error mt-1">{nameErr}</div>
                              ) : null}
                            </div>
                          </td>

                          {/* BITS */}
                          <td
                            data-col-key="bits"
                            className={`px-4 py-2 font-mono vscode-muted align-middle ${
                              activeCell.rowIndex === index && activeCell.key === 'bits'
                                ? 'vscode-cell-active'
                                : ''
                            }`}
                            onClick={(e) => {
                              e.stopPropagation();
                              ensureDraftsInitialized(index);
                              setSelectedFieldIndex(index);
                              setHoveredFieldIndex(index);
                              setSelectedEditKey('bits');
                              setActiveCell({ rowIndex: index, key: 'bits' });
                            }}
                          >
                            <div className="flex items-center h-10">
                              <div className="flex flex-col w-full">
                                <VSCodeTextField
                                  data-edit-key="bits"
                                  className="w-full font-mono"
                                  value={bitsValue}
                                  onFocus={() => {
                                    ensureDraftsInitialized(index);
                                    setSelectedFieldIndex(index);
                                    setHoveredFieldIndex(index);
                                    setSelectedEditKey('bits');
                                    setActiveCell({ rowIndex: index, key: 'bits' });
                                  }}
                                  onInput={(e: Event | React.FormEvent<HTMLElement>) => {
                                    const next = String((e.target as HTMLInputElement).value ?? '');
                                    setBitsDrafts((prev) => ({
                                      ...prev,
                                      [index]: next,
                                    }));
                                    let err = validateBitsString(next);
                                    if (!err) {
                                      const thisWidth = parseBitsWidth(next);
                                      if (thisWidth !== null) {
                                        let total = 0;
                                        for (let i = 0; i < fields.length; ++i) {
                                          if (i === index) {
                                            total += thisWidth;
                                          } else {
                                            const b = bitsDrafts[i] ?? fieldToBitsString(fields[i]);
                                            const w = parseBitsWidth(b);
                                            if (w) {
                                              total += w;
                                            }
                                          }
                                        }
                                        if (total > registerSize) {
                                          err = `Bit fields overflow register (${total} > ${registerSize})`;
                                        }
                                      }
                                    }
                                    setBitsErrors((prev) => ({
                                      ...prev,
                                      [index]: err,
                                    }));
                                    if (!err) {
                                      const updatedFields = fields.map((f, i) => {
                                        if (i !== index) {
                                          return { ...f };
                                        }
                                        const parsed = parseBitsInput(next);
                                        if (parsed) {
                                          return {
                                            ...f,
                                            bits: next,
                                            bit_offset: parsed.bit_offset,
                                            bit_width: parsed.bit_width,
                                            bit_range: parsed.bit_range,
                                          };
                                        } else {
                                          return { ...f, bits: next };
                                        }
                                      });
                                      const curr = updatedFields[index];
                                      const currMSB = curr.bit_range
                                        ? curr.bit_range[0]
                                        : Number(curr.bit_offset) + Number(curr.bit_width) - 1;
                                      let prevMSB = currMSB;
                                      for (let i = index + 1; i < updatedFields.length; ++i) {
                                        const f = updatedFields[i];
                                        const width = Number(f.bit_width) || 1;
                                        const lsb = Number(prevMSB) + 1;
                                        const msb = Number(lsb) + width - 1;
                                        updatedFields[i] = {
                                          ...f,
                                          bit_offset: lsb,
                                          bit_width: width,
                                          bit_range: [msb, lsb],
                                          bits: formatBits(msb, lsb),
                                        };
                                        prevMSB = msb;
                                      }
                                      onUpdate(['fields'], updatedFields);
                                    }
                                  }}
                                />
                                {bitsErr ? (
                                  <div className="text-xs vscode-error mt-1">{bitsErr}</div>
                                ) : null}
                              </div>
                            </div>
                          </td>

                          {/* ACCESS */}
                          <td
                            data-col-key="access"
                            className={`px-4 py-2 align-middle ${
                              activeCell.rowIndex === index && activeCell.key === 'access'
                                ? 'vscode-cell-active'
                                : ''
                            }`}
                            style={{ overflow: 'visible', position: 'relative' }}
                            onClick={(e) => {
                              e.stopPropagation();
                              ensureDraftsInitialized(index);
                              setSelectedFieldIndex(index);
                              setHoveredFieldIndex(index);
                              setSelectedEditKey('access');
                              setActiveCell({ rowIndex: index, key: 'access' });
                            }}
                          >
                            <div className="flex items-center h-10">
                              <VSCodeDropdown
                                data-edit-key="access"
                                value={field.access ?? 'read-write'}
                                className="w-full"
                                position="below"
                                onFocus={() => {
                                  setSelectedFieldIndex(index);
                                  setHoveredFieldIndex(index);
                                  setSelectedEditKey('access');
                                  setActiveCell({ rowIndex: index, key: 'access' });
                                }}
                                onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                                  onUpdate(
                                    ['fields', index, 'access'],
                                    (e.target as HTMLInputElement).value
                                  )
                                }
                              >
                                {ACCESS_OPTIONS.map((opt) => (
                                  <VSCodeOption key={opt} value={opt}>
                                    {opt}
                                  </VSCodeOption>
                                ))}
                              </VSCodeDropdown>
                            </div>
                          </td>

                          {/* RESET */}
                          <td
                            data-col-key="reset"
                            className={`px-4 py-2 font-mono vscode-muted align-middle ${
                              activeCell.rowIndex === index && activeCell.key === 'reset'
                                ? 'vscode-cell-active'
                                : ''
                            }`}
                            onClick={(e) => {
                              e.stopPropagation();
                              ensureDraftsInitialized(index);
                              setSelectedFieldIndex(index);
                              setHoveredFieldIndex(index);
                              setSelectedEditKey('reset');
                              setActiveCell({ rowIndex: index, key: 'reset' });
                            }}
                          >
                            <div className="flex flex-col justify-center h-10">
                              <VSCodeTextField
                                data-edit-key="reset"
                                className="w-full font-mono"
                                value={resetValue}
                                onFocus={() => {
                                  ensureDraftsInitialized(index);
                                  setSelectedFieldIndex(index);
                                  setHoveredFieldIndex(index);
                                  setSelectedEditKey('reset');
                                  setActiveCell({ rowIndex: index, key: 'reset' });
                                }}
                                onInput={(e: Event | React.FormEvent<HTMLElement>) => {
                                  const raw = String((e.target as HTMLInputElement).value ?? '');
                                  setResetDrafts((prev) => ({
                                    ...prev,
                                    [index]: raw,
                                  }));
                                  const trimmed = raw.trim();
                                  if (!trimmed) {
                                    setResetErrors((prev) => ({
                                      ...prev,
                                      [index]: null,
                                    }));
                                    onUpdate(['fields', index, 'reset_value'], null);
                                    return;
                                  }
                                  const parsed = parseReset(raw);
                                  const err = validateResetForField(field, parsed);
                                  setResetErrors((prev) => ({
                                    ...prev,
                                    [index]: err,
                                  }));
                                  if (!err && parsed !== null) {
                                    onUpdate(['fields', index, 'reset_value'], parsed);
                                  }
                                }}
                              />
                              {resetErr ? (
                                <div className="text-xs vscode-error mt-1">{resetErr}</div>
                              ) : null}
                            </div>
                          </td>

                          {/* DESCRIPTION */}
                          <td
                            data-col-key="description"
                            className={`px-6 py-2 vscode-muted align-middle ${
                              activeCell.rowIndex === index && activeCell.key === 'description'
                                ? 'vscode-cell-active'
                                : ''
                            }`}
                            style={{ width: '40%', minWidth: '240px' }}
                            onClick={(e) => {
                              e.stopPropagation();
                              ensureDraftsInitialized(index);
                              setSelectedFieldIndex(index);
                              setHoveredFieldIndex(index);
                              setSelectedEditKey('description');
                              setActiveCell({ rowIndex: index, key: 'description' });
                            }}
                          >
                            <div className="flex items-center h-10">
                              <VSCodeTextArea
                                data-edit-key="description"
                                className="w-full"
                                style={{
                                  height: '40px',
                                  minHeight: '40px',
                                  resize: 'none',
                                }}
                                rows={1}
                                value={field.description ?? ''}
                                onFocus={() => {
                                  setSelectedFieldIndex(index);
                                  setHoveredFieldIndex(index);
                                  setSelectedEditKey('description');
                                  setActiveCell({
                                    rowIndex: index,
                                    key: 'description',
                                  });
                                }}
                                onInput={(e: Event | React.FormEvent<HTMLElement>) =>
                                  onUpdate(
                                    ['fields', index, 'description'],
                                    (e.target as HTMLInputElement).value
                                  )
                                }
                              />
                            </div>
                          </td>
                        </>
                      </tr>
                    );
                  })}
                </tbody>
              </>
            )}
          />
        </div>
      </div>
    </div>
  );
}

import React, { useEffect, useImperativeHandle, useMemo } from 'react';
import type { YamlUpdateHandler, BitFieldRecord } from '../../types/editor';
import BitFieldVisualizer from '../BitFieldVisualizer';
import { KeyboardShortcutsButton } from '../../shared/components';
import { FieldsTable } from './FieldsTable';
import { useFieldEditor } from '../../hooks/useFieldEditor';
import type { Register } from '../../types/memoryMap';

// ---------------------------------------------------------------------------
// Types
// ---------------------------------------------------------------------------

export interface RegisterEditorProps {
  /** The register to display and edit. */
  register: Register;
  /** Normalised bit fields (with bit_range / bit_offset / bit_width). */
  fields: BitFieldRecord[];
  selectionMeta?: {
    absoluteAddress?: number;
    relativeOffset?: number;
    focusDetails?: boolean;
  };
  onUpdate: YamlUpdateHandler;
}

export type RegisterEditorHandle = {
  focus: () => void;
};

// ---------------------------------------------------------------------------
// Component
// ---------------------------------------------------------------------------

/**
 * Renders and manages editing of a single register's properties, including:
 * - Register name / description header
 * - Interactive BitFieldVisualizer
 * - Editable FieldsTable with keyboard navigation
 *
 * Exposes a `focus()` method via ref.
 */
export const RegisterEditor = React.forwardRef<RegisterEditorHandle, RegisterEditorProps>(
  ({ register, fields, selectionMeta, onUpdate }, ref) => {
    const registerSize = register?.size ?? 32;

    const fieldEditor = useFieldEditor(fields, registerSize, onUpdate, true);

    const { hoveredFieldIndex, setHoveredFieldIndex, setDragPreviewRanges, focusRef } = fieldEditor;

    // Expose focus() to parent (e.g. DetailsPanel's useImperativeHandle).
    useImperativeHandle(
      ref,
      () => ({
        focus: () => {
          focusRef.current?.focus();
        },
      }),
      []
    );

    // Auto-focus when selectionMeta.focusDetails is set.
    useEffect(() => {
      if (!selectionMeta?.focusDetails) {
        return;
      }
      const id = window.setTimeout(() => {
        focusRef.current?.focus();
      }, 0);
      return () => window.clearTimeout(id);
    }, [selectionMeta?.focusDetails, register?.name]);

    // Normalise fields for BitFieldVisualizer (provide bit_range).
    const normalisedFields = useMemo(() => {
      if (!register?.fields) {
        return [];
      }
      return register.fields.map((f: BitFieldRecord) => {
        const mappedF = {
          ...f,
          name: f.name ?? undefined,
          bits: f.bits ?? undefined,
          bit_offset: f.bit_offset ?? undefined,
          bit_width: f.bit_width ?? undefined,
          access: f.access ?? undefined,
          reset_value: f.reset_value ?? undefined,
          description: f.description ?? undefined,
        };
        if (mappedF.bit_range) {
          const validRange: [number, number] = [mappedF.bit_range[0], mappedF.bit_range[1]];
          return { ...mappedF, bit_range: validRange };
        }
        if (mappedF.bit_offset !== undefined && mappedF.bit_width !== undefined) {
          const lo = Number(mappedF.bit_offset);
          const width = Number(mappedF.bit_width);
          const hi = lo + width - 1;
          return { ...mappedF, bit_range: [hi, lo] as [number, number] };
        }
        return mappedF;
      });
    }, [register?.fields]);

    return (
      <div className="flex flex-col w-full h-full min-h-0">
        {/* Register Header + BitFieldVisualizer */}
        <div className="vscode-surface border-b vscode-border p-8 flex flex-col gap-6 shrink-0 relative overflow-hidden">
          <div className="flex justify-between items-start relative z-10">
            <div>
              <h2 className="text-2xl font-bold font-mono tracking-tight">{register.name}</h2>
              <p className="vscode-muted text-sm mt-1 max-w-2xl">{register.description}</p>
            </div>
          </div>
          <div className="w-full relative z-10 mt-2 select-none">
            <BitFieldVisualizer
              fields={normalisedFields}
              hoveredFieldIndex={hoveredFieldIndex}
              setHoveredFieldIndex={setHoveredFieldIndex}
              registerSize={32}
              layout="pro"
              onUpdateFieldReset={(fieldIndex, resetValue) => {
                onUpdate(['fields', fieldIndex, 'reset_value'], resetValue);
              }}
              onUpdateFieldRange={(fieldIndex, newRange) => {
                const [hi, lo] = newRange;
                const field = fields[fieldIndex];
                const updatedField = {
                  ...field,
                  bit_range: newRange,
                  bit_offset: lo,
                  bit_width: hi - lo + 1,
                };
                const newFields = [...fields];
                newFields[fieldIndex] = updatedField;
                onUpdate(['fields'], newFields);
              }}
              onBatchUpdateFields={(updates) => {
                const newFields = [...fields];
                updates.forEach(({ idx, range }) => {
                  const [hi, lo] = range;
                  const field = newFields[idx];
                  if (field) {
                    newFields[idx] = {
                      ...field,
                      bit_range: range,
                      bit_offset: lo,
                      bit_width: hi - lo + 1,
                    };
                  }
                });
                newFields.sort((a, b) => {
                  const aLo = a.bit_range ? a.bit_range[1] : (a.bit_offset ?? 0);
                  const bLo = b.bit_range ? b.bit_range[1] : (b.bit_offset ?? 0);
                  return aLo - bLo;
                });
                onUpdate(['fields'], newFields);
              }}
              onCreateField={(newField) => {
                let maxN = 0;
                for (const f of fields) {
                  const m = String(f.name || '').match(/^field(\d+)$/);
                  if (m) {
                    maxN = Math.max(maxN, parseInt(m[1], 10));
                  }
                }
                const name = `field${maxN + 1}`;
                const [hi, lo] = newField.bit_range;
                const field = {
                  name,
                  bit_range: newField.bit_range,
                  bit_offset: lo,
                  bit_width: hi - lo + 1,
                  access: 'read-write',
                  reset_value: 0,
                  description: '',
                };
                const newFields = [...fields, field].sort((a, b) => {
                  const aLo = a.bit_range ? a.bit_range[1] : (a.bit_offset ?? 0);
                  const bLo = b.bit_range ? b.bit_range[1] : (b.bit_offset ?? 0);
                  return aLo - bLo;
                });
                onUpdate(['fields'], newFields);
              }}
              onDragPreview={(preview) => {
                if (preview === null) {
                  setDragPreviewRanges({});
                } else {
                  const newRanges: Record<number, [number, number]> = {};
                  preview.forEach(({ idx, range }) => {
                    newRanges[idx] = range;
                  });
                  setDragPreviewRanges(newRanges);
                }
              }}
            />
          </div>
        </div>

        {/* Editable fields table */}
        <FieldsTable
          fields={fields}
          registerSize={registerSize}
          onUpdate={onUpdate}
          fieldEditor={fieldEditor}
        />

        <KeyboardShortcutsButton context="register" />
      </div>
    );
  }
);

RegisterEditor.displayName = 'RegisterEditor';

import { useCallback, useEffect, useRef, useState } from 'react';
import { SpatialInsertionService } from '../services/SpatialInsertionService';
import type { BitFieldRuntimeDef } from '../services/SpatialInsertionService';
import { fieldToBitsString } from '../utils/BitFieldUtils';
import type { BitFieldRecord, YamlUpdateHandler } from '../types/editor';

// ---------------------------------------------------------------------------
// Types
// ---------------------------------------------------------------------------

export type EditKey = 'name' | 'bits' | 'access' | 'reset' | 'description';
export type ActiveCell = { rowIndex: number; key: EditKey };

export const COLUMN_ORDER: EditKey[] = ['name', 'bits', 'access', 'reset', 'description'];

// ---------------------------------------------------------------------------
// Hook
// ---------------------------------------------------------------------------

/**
 * Manages editing state and operations for bit fields within a register.
 * Handles draft values, validation errors, active cell tracking,
 * and spatial insertion logic.
 *
 * @param fields   Normalised bit field array for the current register.
 * @param registerSize  Register width in bits (e.g. 32).
 * @param onUpdate Callback to commit a YAML path + value change.
 * @param isActive When false the keyboard handler is not installed.
 */
export function useFieldEditor(
  fields: BitFieldRecord[],
  registerSize: number,
  onUpdate: YamlUpdateHandler,
  isActive = true
) {
  // ---- selection / hover ----
  const [selectedFieldIndex, setSelectedFieldIndex] = useState<number>(-1);
  const [hoveredFieldIndex, setHoveredFieldIndex] = useState<number | null>(null);
  const [selectedEditKey, setSelectedEditKey] = useState<EditKey>('name');
  const [activeCell, setActiveCell] = useState<ActiveCell>({
    rowIndex: -1,
    key: 'name',
  });

  // ---- drafts ----
  const [nameDrafts, setNameDrafts] = useState<Record<string, string>>({});
  const [nameErrors, setNameErrors] = useState<Record<string, string | null>>({});
  const [bitsDrafts, setBitsDrafts] = useState<Record<number, string>>({});
  const [bitsErrors, setBitsErrors] = useState<Record<number, string | null>>({});
  const [dragPreviewRanges, setDragPreviewRanges] = useState<Record<number, [number, number]>>({});
  const [resetDrafts, setResetDrafts] = useState<Record<number, string>>({});
  const [resetErrors, setResetErrors] = useState<Record<number, string | null>>({});

  // ---- insert error ----
  const [insertError, setInsertError] = useState<string | null>(null);

  // ---- DOM refs ----
  const focusRef = useRef<HTMLDivElement | null>(null);
  const errorRef = useRef<HTMLDivElement | null>(null);

  // ---------------------------------------------------------------------------
  // Internal helpers
  // ---------------------------------------------------------------------------

  const refocusTableSoon = useCallback(() => {
    window.setTimeout(() => {
      focusRef.current?.focus();
    }, 0);
  }, []);

  const focusFieldEditor = useCallback((rowIndex: number, key: EditKey) => {
    window.setTimeout(() => {
      const row = document.querySelector(`tr[data-field-index="${rowIndex}"]`);
      const el = row?.querySelector(`[data-edit-key="${key}"]`) as HTMLElement | null;
      try {
        el?.focus();
      } catch {
        // ignore
      }
    }, 0);
  }, []);

  // ---------------------------------------------------------------------------
  // Public helpers returned to consumers
  // ---------------------------------------------------------------------------

  /** Initialises drafts for row `index` if they haven't been set yet. */
  const ensureDraftsInitialized = useCallback(
    (index: number) => {
      const field = fields[index];
      if (!field) {
        return;
      }
      const key = field.name ? `${field.name}` : `idx-${index}`;
      setNameDrafts((prev) =>
        prev[key] !== undefined ? prev : { ...prev, [key]: String(field.name ?? '') }
      );
      setBitsDrafts((prev) =>
        prev[index] !== undefined ? prev : { ...prev, [index]: fieldToBitsString(field) }
      );
      setResetDrafts((prev) => {
        if (prev[index] !== undefined) {
          return prev;
        }
        const v = field?.reset_value;
        const display =
          v !== null && v !== undefined ? `0x${Number(v).toString(16).toUpperCase()}` : '0x0';
        return { ...prev, [index]: display };
      });
    },
    [fields]
  );

  /** Moves the currently selected field up (-1) or down (+1). */
  const moveSelectedField = useCallback(
    (delta: -1 | 1) => {
      const index = selectedFieldIndex;
      if (index < 0) {
        return;
      }
      const next = index + delta;
      if (next < 0 || next >= fields.length) {
        return;
      }
      onUpdate(['__op', 'field-move'], { index, delta });
      setBitsDrafts({});
      setBitsErrors({});
      setNameDrafts({});
      setNameErrors({});
      setSelectedFieldIndex(next);
      setHoveredFieldIndex(next);
    },
    [selectedFieldIndex, fields.length, onUpdate]
  );

  // ---------------------------------------------------------------------------
  // Clamp selection when the register changes
  // ---------------------------------------------------------------------------
  useEffect(() => {
    if (!isActive) {
      setSelectedFieldIndex(-1);
      setActiveCell({ rowIndex: -1, key: 'name' });
      return;
    }
    if (!fields.length) {
      setSelectedFieldIndex(-1);
      setActiveCell({ rowIndex: -1, key: 'name' });
      return;
    }
    setSelectedFieldIndex((prev) => {
      if (prev < 0) {
        return 0;
      }
      if (prev >= fields.length) {
        return fields.length - 1;
      }
      return prev;
    });
    setActiveCell((prev) => {
      const rowIndex = prev.rowIndex < 0 ? 0 : Math.min(fields.length - 1, prev.rowIndex);
      const key = COLUMN_ORDER.includes(prev.key) ? prev.key : 'name';
      return { rowIndex, key };
    });
  }, [isActive, fields.length]);

  // ---------------------------------------------------------------------------
  // Keyboard shortcuts (o/O insert, arrow nav, F2/e edit, d delete)
  // ---------------------------------------------------------------------------
  useEffect(() => {
    if (!isActive) {
      return;
    }

    const tryInsertField = (after: boolean) => {
      setInsertError(null);
      // BitFieldRecord is structurally compatible with BitFieldRuntimeDef at runtime
      const typedFields = fields as unknown as BitFieldRuntimeDef[];
      const result = after
        ? SpatialInsertionService.insertFieldAfter(typedFields, selectedFieldIndex, registerSize)
        : SpatialInsertionService.insertFieldBefore(typedFields, selectedFieldIndex, registerSize);

      if (result.error) {
        setInsertError(result.error);
        window.setTimeout(
          () =>
            errorRef.current?.scrollIntoView({
              block: 'nearest',
              behavior: 'smooth',
            }),
          0
        );
        return;
      }

      const newIndex = result.newIndex;
      onUpdate(['fields'], result.items);
      setSelectedFieldIndex(newIndex);
      setHoveredFieldIndex(newIndex);
      setActiveCell({ rowIndex: newIndex, key: 'name' });
      setBitsDrafts({});
      setBitsErrors({});
      setNameDrafts({});
      setNameErrors({});
      window.setTimeout(() => {
        document
          .querySelector(`tr[data-field-index="${newIndex}"]`)
          ?.scrollIntoView({ block: 'center' });
      }, 100);
    };

    const onKeyDown = (e: KeyboardEvent) => {
      let keyLower = (e.key || '').toLowerCase();
      if (e.altKey && e.code) {
        if (e.code === 'KeyH') {
          keyLower = 'h';
        }
        if (e.code === 'KeyJ') {
          keyLower = 'j';
        }
        if (e.code === 'KeyK') {
          keyLower = 'k';
        }
        if (e.code === 'KeyL') {
          keyLower = 'l';
        }
      }
      const vimToArrow: Record<string, 'ArrowLeft' | 'ArrowDown' | 'ArrowUp' | 'ArrowRight'> = {
        h: 'ArrowLeft',
        j: 'ArrowDown',
        k: 'ArrowUp',
        l: 'ArrowRight',
      };
      const mappedArrow = vimToArrow[keyLower];
      const normalizedKey: string = mappedArrow ?? e.key;

      const isArrow =
        normalizedKey === 'ArrowUp' ||
        normalizedKey === 'ArrowDown' ||
        normalizedKey === 'ArrowLeft' ||
        normalizedKey === 'ArrowRight';
      const isEdit = normalizedKey === 'F2' || keyLower === 'e';
      const isDelete = keyLower === 'd' || e.key === 'Delete';
      const isInsertAfter = keyLower === 'o' && !e.shiftKey;
      const isInsertBefore = keyLower === 'o' && e.shiftKey;

      if (!isArrow && !isEdit && !isDelete && !isInsertAfter && !isInsertBefore) {
        return;
      }
      if (e.ctrlKey || e.metaKey) {
        return;
      }

      const activeEl = document.activeElement as HTMLElement | null;
      const isInFieldsArea =
        !!focusRef.current &&
        !!activeEl &&
        (activeEl === focusRef.current || focusRef.current.contains(activeEl));
      if (!isInFieldsArea) {
        return;
      }

      const target = e.target as HTMLElement | null;
      const isInDropdown = !!target?.closest('vscode-dropdown');
      const isTypingTarget = !!target?.closest(
        'input, textarea, select, [contenteditable="true"], vscode-text-field, vscode-text-area'
      );
      if (isTypingTarget) {
        return;
      }
      if (isInDropdown && !keyLower.match(/^[hjkl]$/)) {
        return;
      }

      const scrollToCell = (rowIndex: number, key: EditKey) => {
        window.setTimeout(() => {
          const row = document.querySelector(`tr[data-field-index="${rowIndex}"]`);
          row?.scrollIntoView({
            block: rowIndex === 0 ? 'center' : 'nearest',
          });
          const cell = row?.querySelector(`td[data-col-key="${key}"]`) as HTMLElement | null;
          cell?.scrollIntoView({ block: 'nearest', inline: 'nearest' });
        }, 0);
      };

      const currentRow =
        activeCell.rowIndex >= 0
          ? activeCell.rowIndex
          : selectedFieldIndex >= 0
            ? selectedFieldIndex
            : 0;
      const currentKey: EditKey = COLUMN_ORDER.includes(activeCell.key)
        ? activeCell.key
        : selectedEditKey;

      if (isEdit) {
        if (currentRow < 0 || currentRow >= fields.length) {
          return;
        }
        e.preventDefault();
        e.stopPropagation();
        setSelectedFieldIndex(currentRow);
        setHoveredFieldIndex(currentRow);
        setSelectedEditKey(currentKey);
        setActiveCell({ rowIndex: currentRow, key: currentKey });
        focusFieldEditor(currentRow, currentKey);
        return;
      }

      if (isInsertAfter || isInsertBefore) {
        e.preventDefault();
        e.stopPropagation();
        tryInsertField(isInsertAfter);
        return;
      }

      if (isDelete) {
        if (currentRow < 0 || currentRow >= fields.length) {
          return;
        }
        e.preventDefault();
        e.stopPropagation();
        const newFields = fields.filter((_, index) => index !== currentRow);
        onUpdate(['fields'], newFields);
        const nextRow = currentRow > 0 ? currentRow - 1 : newFields.length > 0 ? 0 : -1;
        setSelectedFieldIndex(nextRow);
        setHoveredFieldIndex(nextRow);
        setActiveCell({ rowIndex: nextRow, key: currentKey });
        setBitsDrafts({});
        setBitsErrors({});
        setNameDrafts({});
        setNameErrors({});
        return;
      }

      const isVertical = normalizedKey === 'ArrowUp' || normalizedKey === 'ArrowDown';
      const delta = normalizedKey === 'ArrowUp' || normalizedKey === 'ArrowLeft' ? -1 : 1;

      if (e.altKey && isVertical) {
        if (selectedFieldIndex < 0) {
          return;
        }
        const next = selectedFieldIndex + delta;
        if (next < 0 || next >= fields.length) {
          return;
        }
        e.preventDefault();
        e.stopPropagation();
        onUpdate(['__op', 'field-move'], { index: selectedFieldIndex, delta });
        setBitsDrafts({});
        setBitsErrors({});
        setNameDrafts({});
        setNameErrors({});
        setSelectedFieldIndex(next);
        setHoveredFieldIndex(next);
        setActiveCell((prev) => ({ rowIndex: next, key: prev.key }));
        scrollToCell(next, currentKey);
        return;
      }

      e.preventDefault();
      e.stopPropagation();

      if (isVertical) {
        const nextRow = Math.max(0, Math.min(fields.length - 1, currentRow + delta));
        setSelectedFieldIndex(nextRow);
        setHoveredFieldIndex(nextRow);
        setSelectedEditKey(currentKey);
        setActiveCell({ rowIndex: nextRow, key: currentKey });
        scrollToCell(nextRow, currentKey);
        return;
      }

      const currentCol = Math.max(0, COLUMN_ORDER.indexOf(currentKey));
      const nextCol = Math.max(0, Math.min(COLUMN_ORDER.length - 1, currentCol + delta));
      const nextKey = COLUMN_ORDER[nextCol] ?? 'name';
      setSelectedFieldIndex(currentRow);
      setHoveredFieldIndex(currentRow);
      setSelectedEditKey(nextKey);
      setActiveCell({ rowIndex: currentRow, key: nextKey });
      scrollToCell(currentRow, nextKey);
    };

    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  }, [
    isActive,
    fields.length,
    selectedFieldIndex,
    selectedEditKey,
    activeCell,
    onUpdate,
    registerSize,
  ]);

  // ---------------------------------------------------------------------------
  // Escape: return focus from inline editor back to the table container
  // ---------------------------------------------------------------------------
  useEffect(() => {
    if (!isActive) {
      return;
    }
    const onKeyDown = (e: KeyboardEvent) => {
      if (e.key !== 'Escape') {
        return;
      }
      const activeEl = document.activeElement as HTMLElement | null;
      if (!activeEl) {
        return;
      }
      const inFields =
        !!focusRef.current && focusRef.current.contains(activeEl) && activeEl !== focusRef.current;
      if (!inFields) {
        return;
      }
      e.preventDefault();
      e.stopPropagation();
      try {
        (activeEl as any).blur?.();
      } catch {
        // ignore
      }
      refocusTableSoon();
    };
    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  }, [isActive, refocusTableSoon]);

  return {
    // selection / hover
    selectedFieldIndex,
    setSelectedFieldIndex,
    hoveredFieldIndex,
    setHoveredFieldIndex,
    selectedEditKey,
    setSelectedEditKey,
    activeCell,
    setActiveCell,
    // drafts
    nameDrafts,
    setNameDrafts,
    nameErrors,
    setNameErrors,
    bitsDrafts,
    setBitsDrafts,
    bitsErrors,
    setBitsErrors,
    dragPreviewRanges,
    setDragPreviewRanges,
    resetDrafts,
    setResetDrafts,
    resetErrors,
    setResetErrors,
    // insert error
    insertError,
    setInsertError,
    // refs
    focusRef,
    errorRef,
    // helpers
    ensureDraftsInitialized,
    moveSelectedField,
    focusFieldEditor,
    refocusTableSoon,
  };
}

/** Return type of {@link useFieldEditor}. */
export type FieldEditorState = ReturnType<typeof useFieldEditor>;

import React, { useEffect, useRef, useState, useCallback } from 'react';
import type { ReactNode, ErrorInfo } from 'react';
import { createRoot } from 'react-dom/client';
import Outline, { type OutlineHandle } from './components/Outline';
import DetailsPanel, { type DetailsPanelHandle } from './components/DetailsPanel';
import { vscode } from './vscode';
import { useMemoryMapState } from './hooks/useMemoryMapState';
import { useSelection, type Selection } from './hooks/useSelection';
import { useYamlSync } from './hooks/useYamlSync';
import { YamlPathResolver, type YamlPath } from './services/YamlPathResolver';
import { YamlService } from './services/YamlService';
import type { NormalizedRegister, NormalizedRegisterArray } from './services/DataNormalizer';
import { BitFieldUtils } from './utils/BitFieldUtils';
import './index.css';

/**
 * Main application component
 */
const App = () => {
  // Sidebar toggle state for mobile
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // State management hooks
  const { memoryMap, rawText, rawTextRef, parseError, fileName, updateFromYaml, updateRawText } =
    useMemoryMapState();
  const {
    selectedId,
    selectedType,
    selectedObject,
    breadcrumbs,
    selectionMeta,
    selectionRef,
    handleSelect,
    goBack,
  } = useSelection();

  // VSCode sync hook
  const { sendUpdate, sendCommand } = useYamlSync(vscode, updateFromYaml);

  useEffect(() => {
    vscode?.postMessage({ type: 'ready' });
  }, []);

  // Local state
  const [activeTab] = useState<'properties' | 'yaml'>('properties');
  const didInitSelectionRef = useRef(false);
  const outlineRef = useRef<OutlineHandle | null>(null);
  const detailsRef = useRef<DetailsPanelHandle | null>(null);

  // Mouse back button listener for navigation history
  useEffect(() => {
    const handleMouseDown = (e: MouseEvent) => {
      // Mouse button 3 = back button, button 4 = forward button
      if (e.button === 3) {
        e.preventDefault();
        goBack();
      }
    };

    window.addEventListener('mousedown', handleMouseDown);
    return () => window.removeEventListener('mousedown', handleMouseDown);
  }, [goBack]);

  /**
   * Resolve selection from the current memory map after updates
   */
  const resolveFromSelection = useCallback(
    (
      selection: Selection | null
    ): {
      type: Selection['type'];
      object: unknown;
      breadcrumbs: string[];
    } | null => {
      if (!selection || !memoryMap) {
        return null;
      }

      if (selection.type === 'memoryMap') {
        return {
          type: 'memoryMap',
          object: memoryMap,
          breadcrumbs: [memoryMap.name || 'Memory Map'],
        };
      }

      // Selection paths are YAML-style: ['addressBlocks', blockIndex, ...]
      const blockIndex = typeof selection.path[1] === 'number' ? selection.path[1] : null;
      if (blockIndex === null) {
        return null;
      }
      const block = memoryMap.address_blocks?.[blockIndex];
      if (!block) {
        return null;
      }

      if (selection.type === 'block') {
        return {
          type: 'block',
          object: block,
          breadcrumbs: [memoryMap.name || 'Memory Map', block.name],
        };
      }

      const blockRegs = ((block as any).registers ?? []) as Array<
        NormalizedRegister | NormalizedRegisterArray
      >;

      if (selection.type === 'array') {
        const regIndex = typeof selection.path[3] === 'number' ? selection.path[3] : null;
        if (regIndex === null) {
          return null;
        }
        const node = blockRegs[regIndex];
        if (node && (node as any).__kind === 'array') {
          const registerArray = node as NormalizedRegisterArray;
          return {
            type: 'array',
            object: registerArray,
            breadcrumbs: [memoryMap.name || 'Memory Map', block.name, registerArray.name],
          };
        }
        return null;
      }

      if (selection.type === 'register') {
        // Direct register: ['addressBlocks', b, 'registers', r]
        if (selection.path.length === 4) {
          const regIndex = typeof selection.path[3] === 'number' ? selection.path[3] : null;
          if (regIndex === null) {
            return null;
          }
          const node = blockRegs[regIndex];
          if (!node || (node as any).__kind === 'array') {
            return null;
          }
          const reg = node as NormalizedRegister;
          return {
            type: 'register',
            object: reg,
            breadcrumbs: [memoryMap.name || 'Memory Map', block.name, reg.name],
          };
        }

        // Nested register inside array: ['addressBlocks', b, 'registers', r, 'registers', rr]
        if (selection.path.length === 6) {
          const arrayIndex = typeof selection.path[3] === 'number' ? selection.path[3] : null;
          const nestedIndex = typeof selection.path[5] === 'number' ? selection.path[5] : null;
          if (arrayIndex === null || nestedIndex === null) {
            return null;
          }
          const node = blockRegs[arrayIndex];
          if (!node || (node as any).__kind !== 'array') {
            return null;
          }
          const registerArray = node as NormalizedRegisterArray;
          const reg = registerArray.registers?.[nestedIndex];
          if (!reg) {
            return null;
          }
          return {
            type: 'register',
            object: reg,
            breadcrumbs: [memoryMap.name || 'Memory Map', block.name, registerArray.name, reg.name],
          };
        }

        return null;
      }

      return null;
    },
    [memoryMap]
  );

  /**
   * Handle updates from DetailsPanel
   */
  const handleUpdate = useCallback(
    (path: YamlPath, value: unknown) => {
      const selection = selectionRef.current;
      if (!selection) {
        return;
      }

      const currentText = rawTextRef.current;
      const rootObj = YamlService.safeParse(currentText);
      if (!rootObj) {
        console.warn('Cannot apply update: YAML parse failed');
        return;
      }

      const { root, selectionRootPath } = YamlPathResolver.getMapRootInfo(rootObj);

      // Handle field operations
      if (path[0] === '__op' && selection.type === 'register') {
        handleFieldOperations(path, value, root, selectionRootPath, selection);
        const newText = YamlService.dump(root);
        updateRawText(newText);
        sendUpdate(newText);
        return;
      }

      // Handle regular property updates
      const fullPath: YamlPath = [...selectionRootPath, ...selection.path, ...path];
      try {
        YamlPathResolver.setAtPath(root, fullPath, value);
        const newText = YamlService.dump(root);
        updateRawText(newText);
        sendUpdate(newText);
      } catch (err) {
        console.warn('Failed to apply update:', err);
      }
    },
    [sendUpdate, updateRawText]
  );

  /**
   * Handle field add/delete/move operations
   * NOTE: This is a simplified version - full implementation would be in a separate service
   */
  const handleFieldOperations = (
    path: YamlPath,
    value: unknown,
    root: unknown,
    selectionRootPath: YamlPath,
    selection: Selection
  ) => {
    const operationType = String(path[1] ?? '');
    const payload = (value ?? {}) as Record<string, unknown>;
    const registerYamlPath: YamlPath = [...selectionRootPath, ...selection.path];
    const fieldsPath: YamlPath = [...registerYamlPath, 'fields'];
    const current = YamlPathResolver.getAtPath(root, fieldsPath);
    if (!Array.isArray(current)) {
      YamlPathResolver.setAtPath(root, fieldsPath, []);
    }
    const fieldsArr = (YamlPathResolver.getAtPath(root, fieldsPath) ?? []) as Record<
      string,
      unknown
    >[];
    if (!Array.isArray(fieldsArr)) {
      return;
    }

    if (operationType === 'field-add') {
      const afterIndex = typeof payload.afterIndex === 'number' ? payload.afterIndex : -1;
      const insertIndex = Math.max(0, Math.min(fieldsArr.length, afterIndex + 1));
      const currentFields =
        ((selection.object as Record<string, unknown>)?.fields as unknown[]) ?? [];
      const used = new Set<number>();
      for (const field of currentFields) {
        const o = Number((field as Record<string, unknown>)?.bit_offset ?? 0);
        const w = Number((field as Record<string, unknown>)?.bit_width ?? 1);
        for (let b = o; b < o + w; b++) {
          used.add(b);
        }
      }
      let lsb = 0;
      while (used.has(lsb) && lsb < 32) {
        lsb++;
      }
      const bits = `[${lsb}:${lsb}]`;

      fieldsArr.splice(insertIndex, 0, {
        name: payload.name ?? 'NEW_FIELD',
        bits,
        access: payload.access ?? 'read-write',
        description: payload.description ?? '',
      });
    }

    if (operationType === 'field-delete') {
      const index = typeof payload.index === 'number' ? payload.index : -1;
      if (index >= 0 && index < fieldsArr.length) {
        fieldsArr.splice(index, 1);
      }
    }

    if (operationType === 'field-move') {
      const index = typeof payload.index === 'number' ? payload.index : -1;
      const delta = typeof payload.delta === 'number' ? payload.delta : 0;
      const next = index + delta;
      if (index >= 0 && next >= 0 && index < fieldsArr.length && next < fieldsArr.length) {
        // Swap fields in array
        const tmp = fieldsArr[index];
        fieldsArr[index] = fieldsArr[next];
        fieldsArr[next] = tmp;

        // Recalculate bit offsets for ALL fields after swapping
        // Important: Create clean field objects to avoid modifying shared references
        let offset = 0;
        for (let i = 0; i < fieldsArr.length; i++) {
          const field = fieldsArr[i];

          // Parse width from bits string (primary source of truth in YAML)
          let width = 1; // default
          if (typeof field?.bits === 'string') {
            const parsed = BitFieldUtils.parseBitsLike(field.bits);
            if (parsed && parsed.bit_width > 0) {
              width = parsed.bit_width;
            }
          }
          // Fall back to bit_width property if bits is not available
          else if (Number.isFinite(field?.bit_width as number) && (field.bit_width as number) > 0) {
            width = Number(field.bit_width);
          }
          width = Math.max(1, Math.min(32, Math.trunc(width)));

          // Replace field with clean object containing only YAML-persisted properties
          fieldsArr[i] = {
            name: field.name,
            bits: BitFieldUtils.formatBitsLike(offset, width),
            access: field.access,
            reset_value: field.reset_value,
            description: field.description,
            enumerated_values: field.enumerated_values,
          };

          offset += width;
        }
      }
    }
  };

  /**
   * Auto-select root on first load and maintain selection after updates
   */
  useEffect(() => {
    if (!memoryMap) {
      return;
    }

    if (!didInitSelectionRef.current) {
      handleSelect({
        id: 'root',
        type: 'memoryMap',
        object: memoryMap,
        breadcrumbs: [memoryMap.name || 'Memory Map'],
        path: [],
      });
      didInitSelectionRef.current = true;
    } else {
      const resolved = resolveFromSelection(selectionRef.current);
      if (resolved) {
        handleSelect({
          ...selectionRef.current!,
          type: resolved.type,
          object: resolved.object,
          breadcrumbs: resolved.breadcrumbs,
        });
      }
    }
  }, [memoryMap, handleSelect, resolveFromSelection]);

  /**
   * Keyboard navigation (Ctrl+H for outline, Ctrl+L for details)
   */
  useEffect(() => {
    const onKeyDown = (e: KeyboardEvent) => {
      const keyLower = (e.key || '').toLowerCase();
      if (!e.ctrlKey || e.metaKey || e.altKey) {
        return;
      }
      if (keyLower !== 'h' && keyLower !== 'l') {
        return;
      }

      e.preventDefault();
      e.stopPropagation();

      if (keyLower === 'h') {
        outlineRef.current?.focus();
        return;
      }
      if (keyLower === 'l') {
        detailsRef.current?.focus();
        return;
      }
    };
    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  }, []);

  /**
   * Render error state
   */
  if (parseError) {
    return (
      <div className="flex items-center justify-center h-screen vscode-surface">
        <div className="text-center p-8">
          <span className="codicon codicon-error text-6xl mb-4 block opacity-50"></span>
          <h2 className="text-xl font-semibold mb-2">Parse Error</h2>
          <p className="text-sm opacity-75">{parseError}</p>
        </div>
      </div>
    );
  }

  /**
   * Render loading state
   */
  if (!memoryMap) {
    return (
      <div className="flex items-center justify-center h-screen vscode-surface">
        <div className="text-center">
          <span className="codicon codicon-loading codicon-modifier-spin text-4xl opacity-50"></span>
          <p className="mt-4 text-sm opacity-75">Loading memory map...</p>
        </div>
      </div>
    );
  }

  /**
   * Main UI
   */
  return (
    <>
      <header
        className="flex items-center justify-between px-6 py-3 shrink-0"
        style={{ borderBottom: '1px solid var(--vscode-panel-border)' }}
      >
        <div className="flex items-center gap-4 flex-1 overflow-hidden">
          {/* Mobile sidebar toggle */}
          <button
            className="sidebar-toggle-btn p-2 rounded-md transition-colors vscode-icon-button"
            onClick={() => setSidebarOpen(!sidebarOpen)}
            title="Toggle sidebar"
          >
            <span className="codicon codicon-menu"></span>
          </button>
          <h1 className="text-lg font-semibold shrink-0">FPGA Memory Map Editor</h1>
          <div className="flex items-center gap-1 text-sm opacity-75 overflow-hidden">
            <span className="codicon codicon-file text-[16px]"></span>
            <span className="truncate">{fileName || 'Untitled'}</span>
            {breadcrumbs.length > 1 && (
              <>
                <span className="codicon codicon-chevron-right text-[16px]"></span>
                <span
                  className="font-medium px-2 py-0.5 rounded vscode-surface-alt"
                  style={{ border: '1px solid var(--vscode-panel-border)' }}
                >
                  {breadcrumbs[breadcrumbs.length - 1]}
                </span>
              </>
            )}
          </div>
        </div>
        <div className="flex items-center gap-2">
          <button
            className="p-2 rounded-md transition-colors vscode-icon-button"
            onClick={() => sendCommand('save')}
            title="Save"
          >
            <span className="codicon codicon-save"></span>
          </button>
          <button
            className="p-2 rounded-md transition-colors vscode-icon-button"
            onClick={() => sendCommand('validate')}
            title="Validate"
          >
            <span className="codicon codicon-check"></span>
          </button>
          <div className="h-6 w-px mx-1" style={{ background: 'var(--vscode-panel-border)' }}></div>
          <button
            className="p-2 rounded-md transition-colors vscode-icon-button"
            title="Export Header"
          >
            <span className="codicon codicon-code"></span>
          </button>
          <button
            className="p-2 rounded-md transition-colors vscode-icon-button"
            title="Documentation"
          >
            <span className="codicon codicon-book"></span>
          </button>
        </div>
      </header>
      <main className="flex-1 flex overflow-hidden">
        {/* Sidebar backdrop for mobile */}
        {sidebarOpen && (
          <div className="sidebar-backdrop active" onClick={() => setSidebarOpen(false)} />
        )}
        <aside
          className={`sidebar flex flex-col shrink-0 overflow-y-auto ${sidebarOpen ? 'sidebar-open' : ''}`}
        >
          <Outline
            ref={outlineRef}
            memoryMap={memoryMap}
            selectedId={selectedId}
            onSelect={handleSelect}
            onRename={(path, newName) => {
              // path is already the full path to the 'name' property
              // e.g., ['addressBlocks', 0, 'name'] for a block
              const currentText = rawTextRef.current;
              const rootObj = YamlService.safeParse(currentText);
              if (!rootObj) {
                console.warn('Cannot apply rename: YAML parse failed');
                return;
              }
              const { root, selectionRootPath } = YamlPathResolver.getMapRootInfo(rootObj);
              const fullPath = [...selectionRootPath, ...path];
              try {
                YamlPathResolver.setAtPath(root, fullPath, newName);
                const newText = YamlService.dump(root);
                updateRawText(newText);
                sendUpdate(newText);
              } catch (err) {
                console.warn('Failed to apply rename:', err);
              }
            }}
          />
        </aside>
        {activeTab === 'yaml' ? (
          <section className="flex-1 vscode-surface overflow-auto min-w-0">
            <div className="p-6">
              <pre className="font-mono text-sm">{rawText}</pre>
            </div>
          </section>
        ) : (
          <section className="flex-1 overflow-hidden min-w-0">
            <DetailsPanel
              ref={detailsRef}
              selectedType={selectedType}
              selectedObject={selectedObject}
              selectionMeta={selectionMeta}
              onUpdate={handleUpdate}
              onNavigateToRegister={(regIndex) => {
                // Navigate to register in Outline when clicking on visualizer
                if (!memoryMap || !selectionRef.current) {
                  return;
                }

                // CASE 1: Block View (Direct registers/arrays)
                if (selectionRef.current.type === 'block') {
                  const currentPath = selectionRef.current.path || [];
                  const block = selectedObject as Record<string, unknown>;
                  const registers = (block?.registers as unknown[]) || [];
                  const reg = registers[regIndex] as Record<string, unknown>;
                  if (!reg) {
                    return;
                  }

                  // Determine if it's an array or register
                  const isArray = reg.__kind === 'array';
                  const newPath = [...currentPath, 'registers', regIndex];

                  // Outline uses different ID schemes for arrays vs registers
                  const idSuffix = isArray ? `-arrreg-${regIndex}` : `-reg-${regIndex}`;

                  handleSelect({
                    id: `${selectionRef.current.id}${idSuffix}`,
                    type: isArray ? 'array' : 'register',
                    object: reg,
                    breadcrumbs: [
                      ...(selectionRef.current.breadcrumbs || []),
                      String(reg.name ?? `Register ${regIndex}`),
                    ],
                    path: newPath,
                    // Block children (top-level) absolute address is calculated in Outline, but we can approximate or omit if Outline recalculates?
                    // Usually safe to omit if DetailsPanel Recalcs or if Outline pass provides it.
                    // For consistency, let's leave meta undefined (or copy from existing logic which didn't set it? Existing logic DID NOT set meta).
                  });
                  return;
                }

                // CASE 2: Array Element View (Nested registers)
                // When masquerading as a Block for an Array Element, clicking a register should select the template register in the Outline under that element.
                if (selectionRef.current.type === 'array') {
                  const arr = selectedObject as Record<string, unknown>; // NormalizedRegisterArray with meta
                  const registers = (arr.registers as unknown[]) || [];
                  const reg = registers[regIndex] as Record<string, unknown>;
                  if (!reg) {
                    return;
                  }

                  // Path logic verified from Outline.tsx: [...arrayPath, 'registers', childIndex]
                  const newPath = [...(selectionRef.current.path || []), 'registers', regIndex];

                  // ID suffix logic verified from Outline.tsx: `${elementId}-reg-${childIndex}`
                  const id = `${selectionRef.current.id}-reg-${regIndex}`;

                  // Calculate absolute address for the specific child
                  const elementBase = (arr.__element_base as number) ?? 0;
                  const absoluteAddr = elementBase + ((reg.address_offset as number) ?? 0);

                  handleSelect({
                    id,
                    type: 'register',
                    object: reg,
                    breadcrumbs: [
                      ...(selectionRef.current.breadcrumbs || []),
                      String(reg.name ?? `Register ${regIndex}`),
                    ],
                    path: newPath,
                    meta: {
                      absoluteAddress: absoluteAddr,
                      relativeOffset: (reg.address_offset as number) ?? 0,
                    },
                  });
                  return;
                }
              }}
              onNavigateToBlock={(blockIndex) => {
                // Navigate to address block in Outline
                if (!memoryMap || !memoryMap.address_blocks) {
                  return;
                }
                const block = memoryMap.address_blocks[blockIndex];
                if (!block) {
                  return;
                }

                handleSelect({
                  id: `block-${blockIndex}`,
                  type: 'block',
                  object: block,
                  breadcrumbs: [
                    memoryMap.name || 'Memory Map',
                    block.name || `Block ${blockIndex}`,
                  ],
                  path: ['addressBlocks', blockIndex],
                });
              }}
            />
          </section>
        )}
      </main>
    </>
  );
};

/**
 * Error boundary for catching React errors
 */
class ErrorBoundary extends React.Component<
  { children: ReactNode },
  { error: unknown; info: unknown }
> {
  constructor(props: { children: ReactNode }) {
    super(props);
    this.state = { error: null, info: null };
  }
  static getDerivedStateFromError(error: unknown) {
    return { error, info: null };
  }
  componentDidCatch(error: unknown, info: ErrorInfo) {
    this.setState({ error, info });
  }
  render() {
    if (this.state.error) {
      return (
        <div
          style={{
            background: '#fff0f0',
            color: '#b91c1c',
            padding: 32,
            fontFamily: 'monospace',
          }}
        >
          <h2 style={{ fontWeight: 'bold' }}>UI Error</h2>
          <div>{(this.state.error as Error)?.message || String(this.state.error)}</div>
          {!!this.state.info && (
            <pre style={{ marginTop: 16, fontSize: 12 }}>
              {(this.state.info as { componentStack?: string })?.componentStack}
            </pre>
          )}
        </div>
      );
    }
    return this.props.children;
  }
}

/**
 * Application bootstrap
 */
const rootElement = document.getElementById('root');
if (rootElement) {
  const root = createRoot(rootElement);
  root.render(
    <ErrorBoundary>
      <App />
    </ErrorBoundary>
  );
}

import React, { useEffect, useState, useRef } from 'react';
import { createRoot } from 'react-dom/client';
import { NavigationSidebar } from './components/layout/NavigationSidebar';
import { EditorPanel } from './components/layout/EditorPanel';
import { useIpCoreState } from './hooks/useIpCoreState';
import { useNavigation } from './hooks/useNavigation';
import { useIpCoreSync } from './hooks/useIpCoreSync';
import { vscode } from '../vscode';

export type FocusedPanel = 'left' | 'right';

/**
 * Main IP Core Visual Editor application
 */
const IpCoreApp: React.FC = () => {
  const {
    ipCore,
    rawYaml,
    parseError,
    fileName,
    imports,
    updateFromYaml,
    updateIpCore,
    getValidationErrors,
  } = useIpCoreState();
  const { selectedSection, navigate } = useNavigation();
  useIpCoreSync(rawYaml);

  // Sidebar toggle state for mobile
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Panel focus state for Ctrl+H/L navigation
  const [focusedPanel, setFocusedPanel] = useState<FocusedPanel>('left');
  const leftPanelRef = useRef<HTMLDivElement>(null);
  const rightPanelRef = useRef<HTMLDivElement>(null);

  // Highlight state for validation errors
  const [highlight, setHighlight] = useState<{ entityName: string; field: string } | undefined>(
    undefined
  );

  const validationErrors = getValidationErrors();

  // Clear highlight if the error is no longer in the validation list (e.g., fixed by undo or edit)
  useEffect(() => {
    if (highlight) {
      const errorStillExists = validationErrors.some(
        (e) => e.entityName === highlight.entityName && e.field === highlight.field
      );
      if (!errorStillExists) {
        setHighlight(undefined);
      }
    }
  }, [validationErrors, highlight]);

  // Handle global keyboard shortcuts for panel switching
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Ctrl+H: Focus left panel
      if (e.ctrlKey && e.key.toLowerCase() === 'h') {
        e.preventDefault();
        setFocusedPanel('left');
        leftPanelRef.current?.focus();
      }
      // Ctrl+L: Focus right panel (EditorPanel's useEffect will auto-focus the table)
      else if (e.ctrlKey && e.key.toLowerCase() === 'l') {
        e.preventDefault();
        setFocusedPanel('right');
        // The EditorPanel will auto-focus the inner table container via its useEffect
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Notify extension that webview is ready
  useEffect(() => {
    if (vscode) {
      vscode.postMessage({ type: 'ready' });
    }
  }, []);

  // Listen for messages from extension
  useEffect(() => {
    const handleMessage = (event: MessageEvent) => {
      const message = event.data;

      switch (message.type) {
        case 'update':
          updateFromYaml(message.text, message.fileName, message.imports);
          break;
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, [updateFromYaml]);

  const typedIpCore = ipCore as unknown as Parameters<typeof EditorPanel>[0]['ipCore'];

  return (
    <div
      className="h-screen flex flex-col"
      style={{
        background: 'var(--vscode-editor-background)',
        color: 'var(--vscode-editor-foreground)',
      }}
    >
      {/* Header */}
      <div
        className="px-4 py-2"
        style={{
          borderBottom: '1px solid var(--vscode-panel-border)',
          background: 'var(--vscode-sideBar-background)',
        }}
      >
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            {/* Mobile sidebar toggle */}
            <button
              className="sidebar-toggle-btn p-2 rounded-md transition-colors vscode-icon-button"
              onClick={() => setSidebarOpen(!sidebarOpen)}
              title="Toggle navigation"
              type="button"
            >
              <span className="codicon codicon-menu"></span>
            </button>
            <h1 className="text-sm font-semibold">{fileName || 'IP Core Editor'}</h1>
            {typedIpCore?.vlnv && (
              <span className="text-xs" style={{ opacity: 0.7 }}>
                {typedIpCore.vlnv.vendor} / {typedIpCore.vlnv.library} / {typedIpCore.vlnv.name}
              </span>
            )}
          </div>
          {validationErrors.length > 0 && (
            <div className="text-sm" style={{ color: 'var(--vscode-errorForeground)' }}>
              {validationErrors.length} validation error(s)
            </div>
          )}
        </div>
      </div>

      {/* Main content */}
      <div className="flex-1 flex overflow-hidden">
        {parseError ? (
          <div className="flex-1 flex items-center justify-center">
            <div
              className="px-4 py-3 rounded max-w-2xl"
              style={{
                background: 'var(--vscode-inputValidation-errorBackground)',
                border: '1px solid var(--vscode-inputValidation-errorBorder)',
                color: 'var(--vscode-errorForeground)',
              }}
            >
              <p className="font-semibold mb-2">Parse Error</p>
              <p className="text-sm">{parseError}</p>
            </div>
          </div>
        ) : !ipCore ? (
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center">
              <p style={{ color: 'var(--vscode-descriptionForeground)' }}>No IP core loaded</p>
              <p
                className="text-xs mt-2"
                style={{
                  color: 'var(--vscode-descriptionForeground)',
                  opacity: 0.6,
                }}
              >
                Waiting for data from extension...
              </p>
            </div>
          </div>
        ) : (
          <>
            {/* Sidebar backdrop for mobile */}
            {sidebarOpen && (
              <div className="sidebar-backdrop active" onClick={() => setSidebarOpen(false)} />
            )}
            <NavigationSidebar
              selectedSection={selectedSection}
              onNavigate={navigate}
              ipCore={{ ...typedIpCore, imports }}
              isFocused={focusedPanel === 'left'}
              onFocus={() => setFocusedPanel('left')}
              panelRef={leftPanelRef}
              className={sidebarOpen ? 'sidebar-open' : ''}
            />
            <EditorPanel
              selectedSection={selectedSection}
              ipCore={typedIpCore}
              imports={imports}
              onUpdate={updateIpCore}
              isFocused={focusedPanel === 'right'}
              onFocus={() => setFocusedPanel('right')}
              panelRef={rightPanelRef}
              highlight={highlight}
            />
          </>
        )}
      </div>

      {/* Validation errors panel */}
      {validationErrors.length > 0 && (
        <div
          className="p-2"
          style={{
            borderTop: '1px solid var(--vscode-panel-border)',
            background: 'var(--vscode-inputValidation-warningBackground)',
          }}
        >
          <p className="text-sm font-semibold mb-1">Reference Validation Errors:</p>
          <ul className="text-xs list-disc list-inside">
            {validationErrors.map((error, idx) => (
              <li
                key={idx}
                className="cursor-pointer hover:underline"
                onClick={() => {
                  navigate(error.section);
                  setHighlight({
                    entityName: error.entityName,
                    field: error.field,
                  });
                  setFocusedPanel('right');
                }}
              >
                {error.message}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

