import React from 'react';
import type { YamlUpdateHandler } from '../../../types/editor';
import { EditableTable, FormField, SelectField } from '../../../shared/components';
import { validateVhdlIdentifier, validateUniqueName } from '../../../shared/utils/validation';
import { useVimTableNavigation } from '../../hooks/useVimTableNavigation';

interface Clock {
  name: string; // Physical port name
  logicalName?: string; // Standard logical name (CLK)
  frequency?: string;
  direction?: string;
}

interface BusInterface {
  name: string;
  associatedClock?: string;
}

interface ClocksTableProps {
  clocks: unknown[];
  busInterfaces?: unknown[];
  onUpdate: YamlUpdateHandler;
}

const createEmptyClock = (): Clock => ({
  name: '',
  logicalName: 'CLK',
  frequency: '',
  direction: 'input',
});

// Normalize direction from in/out to input/output
const normalizeClock = (clock: Clock): Clock => {
  const dirMap: { [key: string]: string } = {
    in: 'input',
    out: 'output',
    input: 'input',
    output: 'output',
  };
  return { ...clock, direction: dirMap[clock.direction || 'input'] || 'input' };
};

// Helper to display normalized direction
const displayDirection = (dir?: string): string => {
  const dirMap: { [key: string]: string } = {
    in: 'input',
    out: 'output',
    input: 'input',
    output: 'output',
  };
  return dirMap[dir || 'input'] || 'input';
};

const COLUMN_KEYS = ['name', 'logicalName', 'frequency', 'direction', 'usedBy'];
const TABLE_COLUMNS = [
  { key: 'name', header: 'Physical Name' },
  { key: 'logicalName', header: 'Logical Name' },
  { key: 'frequency', header: 'Frequency' },
  { key: 'direction', header: 'Direction' },
  { key: 'usedBy', header: 'Used By' },
  { key: 'actions', header: 'Actions', align: 'right' as const },
];
const KEYBOARD_HINT = '• h/j/k/l: navigate • e: edit • d: delete • o: add';

// Helper to find which interfaces use a clock
const getUsedByInterfaces = (clockName: string, busInterfaces: BusInterface[]): string[] => {
  return busInterfaces.filter((bus) => bus.associatedClock === clockName).map((bus) => bus.name);
};

/**
 * Editable table for IP Core clocks
 * Supports vim-style keyboard navigation:
 * - j/k or Arrow Up/Down: Navigate rows
 * - h/l or Arrow Left/Right: Navigate columns
 * - Enter or 'e': Edit selected cell
 * - 'd' or Delete: Delete selected row
 * - 'o': Add new row
 * - Escape: Cancel editing
 */
export const ClocksTable: React.FC<ClocksTableProps> = ({
  clocks: rawClocks,
  busInterfaces: rawBusInterfaces = [],
  onUpdate,
}) => {
  const clocks = rawClocks as Clock[];
  const busInterfaces = rawBusInterfaces as BusInterface[];
  const {
    editingIndex,
    isAdding,
    draft,
    setDraft,
    handleEdit,
    handleAdd,
    handleSave,
    handleCancel,
    handleDelete,
    containerRef,
    getRowProps,
    getCellProps,
  } = useVimTableNavigation<Clock>({
    items: clocks,
    onUpdate,
    dataKey: 'clocks',
    createEmptyItem: createEmptyClock,
    normalizeItem: normalizeClock,
    columnKeys: COLUMN_KEYS,
  });

  const existingNames = clocks.map((c) => c.name).filter((_, i) => i !== editingIndex);
  const nameError =
    validateVhdlIdentifier(draft.name) || validateUniqueName(draft.name, existingNames);
  const canSave = !nameError;

  const renderEditRow = (isNew: boolean) => (
    <tr
      style={{
        background: 'var(--vscode-list-activeSelectionBackground)',
        borderBottom: '1px solid var(--vscode-panel-border)',
      }}
      data-row-idx={editingIndex ?? clocks.length}
    >
      <td className="px-4 py-3">
        <FormField
          label=""
          value={draft.name}
          onChange={(v: string) => setDraft({ ...draft, name: v })}
          error={nameError || undefined}
          placeholder="i_clk_sys"
          required
          data-edit-key="name"
          onSave={canSave ? handleSave : undefined}
          onCancel={handleCancel}
        />
      </td>
      <td className="px-4 py-3">
        <FormField
          label=""
          value={draft.logicalName || 'CLK'}
          onChange={(v: string) => setDraft({ ...draft, logicalName: v })}
          placeholder="CLK"
          data-edit-key="logicalName"
          onSave={canSave ? handleSave : undefined}
          onCancel={handleCancel}
        />
      </td>
      <td className="px-4 py-3">
        <FormField
          label=""
          value={draft.frequency || ''}
          onChange={(v: string) => setDraft({ ...draft, frequency: v })}
          placeholder="100 MHz"
          data-edit-key="frequency"
          onSave={canSave ? handleSave : undefined}
          onCancel={handleCancel}
        />
      </td>
      <td className="px-4 py-3">
        <SelectField
          label=""
          value={draft.direction || 'input'}
          options={[
            { value: 'input', label: 'input' },
            { value: 'output', label: 'output' },
          ]}
          onChange={(v: string) => setDraft({ ...draft, direction: v })}
          data-edit-key="direction"
          onSave={canSave ? handleSave : undefined}
          onCancel={handleCancel}
        />
      </td>
      <td className="px-4 py-3 text-right">
        <div className="flex items-center justify-end gap-2">
          <button
            onClick={handleSave}
            disabled={!canSave}
            className="px-3 py-1 rounded text-xs font-medium"
            style={{
              background: canSave
                ? 'var(--vscode-button-background)'
                : 'var(--vscode-button-secondaryBackground)',
              color: 'var(--vscode-button-foreground)',
              opacity: canSave ? 1 : 0.5,
            }}
          >
            {isNew ? 'Add' : 'Save'}
          </button>
          <button
            onClick={handleCancel}
            className="px-3 py-1 rounded text-xs font-medium"
            style={{
              background: 'var(--vscode-button-secondaryBackground)',
              color: 'var(--vscode-button-foreground)',
            }}
          >
            Cancel
          </button>
        </div>
      </td>
    </tr>
  );

  return (
    <EditableTable
      title="Clocks"
      rows={clocks}
      rowLabelSingular="clock"
      keyboardHint={KEYBOARD_HINT}
      addButtonLabel="Add Clock"
      onAdd={handleAdd}
      disableAdd={isAdding || editingIndex !== null}
      columns={TABLE_COLUMNS}
      editingIndex={editingIndex}
      isAdding={isAdding}
      renderEditRow={renderEditRow}
      renderDisplayRow={(clock, index) => {
        const rowProps = getRowProps(index);
        const usedBy = getUsedByInterfaces(clock.name, busInterfaces);
        return (
          <tr key={index} {...rowProps} onDoubleClick={() => handleEdit(index)}>
            <td className="px-4 py-3 text-sm font-mono" {...getCellProps(index, 'name')}>
              {clock.name}
            </td>
            <td className="px-4 py-3 text-sm font-mono" {...getCellProps(index, 'logicalName')}>
              {clock.logicalName || 'CLK'}
            </td>
            <td className="px-4 py-3 text-sm" {...getCellProps(index, 'frequency')}>
              {clock.frequency || '—'}
            </td>
            <td className="px-4 py-3 text-sm" {...getCellProps(index, 'direction')}>
              {displayDirection(clock.direction)}
            </td>
            <td className="px-4 py-3 text-sm" {...getCellProps(index, 'usedBy')}>
              {usedBy.length > 0 ? (
                <div className="flex flex-wrap gap-1">
                  {usedBy.map((name, i) => (
                    <span
                      key={i}
                      className="px-2 py-0.5 rounded text-xs font-mono"
                      style={{
                        background: 'var(--vscode-badge-background)',
                        color: 'var(--vscode-badge-foreground)',
                      }}
                    >
                      {name}
                    </span>
                  ))}
                </div>
              ) : (
                <span style={{ opacity: 0.5 }}>—</span>
              )}
            </td>
            <td className="px-4 py-3 text-right">
              <div className="flex items-center justify-end gap-2">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleEdit(index);
                  }}
                  disabled={isAdding || editingIndex !== null}
                  className="p-1 rounded"
                  style={{
                    opacity: isAdding || editingIndex !== null ? 0.3 : 1,
                  }}
                  title="Edit (e)"
                >
                  <span className="codicon codicon-edit"></span>
                </button>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDelete(index);
                  }}
                  disabled={isAdding || editingIndex !== null}
                  className="p-1 rounded"
                  style={{
                    color: 'var(--vscode-errorForeground)',
                    opacity: isAdding || editingIndex !== null ? 0.3 : 1,
                  }}
                  title="Delete (d)"
                >
                  <span className="codicon codicon-trash"></span>
                </button>
              </div>
            </td>
          </tr>
        );
      }}
      emptyMessage={'No clocks defined. Press \'o\' or click "Add Clock" to create one.'}
      emptyColSpan={6}
      containerRef={containerRef}
    />
  );
};

import React, { useState, useEffect } from "react";
import type { YamlUpdateHandler } from "../../../types/editor";
import {
  FormField,
  TextAreaField,
  SelectField,
} from "../../../shared/components";
import { validateRequired } from "../../../shared/utils/validation";
import { vscode } from "../../../vscode";

interface FileEntry {
  path: string;
  type: string;
}

interface FileSet {
  name: string;
  description?: string;
  files: FileEntry[];
  import?: string;
}

interface FileSetsEditorProps {
  fileSets: unknown[];
  onUpdate: YamlUpdateHandler;
}

/**
 * Editor for IP Core file sets
 * Supports adding/editing file sets and files within them
 */
export const FileSetsEditor: React.FC<FileSetsEditorProps> = ({
  fileSets: rawFileSets,
  onUpdate,
}) => {
  const fileSets = rawFileSets as FileSet[];
  const [expandedSets, setExpandedSets] = useState<Set<number>>(new Set([0]));
  const [editingSet, setEditingSet] = useState<number | null>(null);
  const [editingFile, setEditingFile] = useState<{
    setIdx: number;
    fileIdx: number;
  } | null>(null);
  const [isAddingSet, setIsAddingSet] = useState(false);
  const [isAddingFile, setIsAddingFile] = useState<number | null>(null);
  const [fileExistence, setFileExistence] = useState<{
    [key: string]: boolean;
  }>({});

  const [setDraft, setSetDraft] = useState({ name: "", description: "" });
  const [fileDraft, setFileDraft] = useState({ path: "", type: "verilog" });

  const fileTypeOptions = [
    { value: "verilog", label: "Verilog" },
    { value: "vhdl", label: "VHDL" },
    { value: "systemverilog", label: "SystemVerilog" },
    { value: "c", label: "C" },
    { value: "cpp", label: "C++" },
    { value: "tcl", label: "TCL" },
    { value: "python", label: "Python" },
    { value: "pdf", label: "PDF" },
    { value: "text", label: "Text" },
  ];

  const toggleSet = (idx: number) => {
    const newExpanded = new Set(expandedSets);
    if (newExpanded.has(idx)) {
      newExpanded.delete(idx);
    } else {
      newExpanded.add(idx);
    }
    setExpandedSets(newExpanded);
  };

  // Check file existence when fileSets change
  useEffect(() => {
    const allPaths = fileSets.flatMap((fs) => {
      const paths = (fs.files || []).map((f) => f.path);
      if (fs.import) {paths.push(fs.import);}
      return paths;
    });
    if (allPaths.length === 0) {return;}

    // Send message to extension to check file existence
    vscode?.postMessage({
      type: "checkFilesExist",
      paths: allPaths,
    });

    // Listen for response
    const handler = (event: MessageEvent) => {
      const message = event.data;
      if (message.type === "filesExistResult" && message.results) {
        setFileExistence((prev) => ({ ...prev, ...message.results }));
        window.removeEventListener("message", handler);
      }
    };
    window.addEventListener("message", handler);

    return () => window.removeEventListener("message", handler);
  }, [fileSets]);

  // Open file in editor
  const handleOpenFile = (filePath: string) => {
    const exists = fileExistence[filePath];
    if (exists === false) {
      // File doesn't exist - don't try to open
      return;
    }
    vscode?.postMessage({
      type: "command",
      command: "openFile",
      path: filePath,
    });
  };

  const handleAddSet = () => {
    setIsAddingSet(true);
    setSetDraft({ name: "", description: "" });
  };

  const handleSaveSet = () => {
    if (isAddingSet) {
      const newSet: FileSet = { ...setDraft, files: [] };
      onUpdate(["fileSets"], [...fileSets, newSet]);
    } else if (editingSet !== null) {
      const updated = [...fileSets];
      updated[editingSet] = { ...updated[editingSet], ...setDraft };
      onUpdate(["fileSets"], updated);
    }
    setIsAddingSet(false);
    setEditingSet(null);
  };

  const handleDeleteSet = (idx: number) => {
    // Remove confirmation - webview confirm() may not work properly
    onUpdate(
      ["fileSets"],
      fileSets.filter((_, i) => i !== idx),
    );
  };

  const handleBrowseFile = (setIdx: number) => {
    // Send message to extension to open file picker
    vscode?.postMessage({
      type: "selectFiles",
      multi: true,
    });

    // Listen for response
    const handler = (event: MessageEvent) => {
      const message = event.data;
      if (
        message.type === "filesSelected" &&
        message.files &&
        message.files.length > 0
      ) {
        // Auto-detect file type based on extension
        const detectFileType = (filePath: string): string => {
          const ext = filePath.split(".").pop()?.toLowerCase();
          const typeMap: { [key: string]: string } = {
            v: "verilog",
            vh: "verilog",
            sv: "systemverilog",
            vhd: "vhdl",
            vhdl: "vhdl",
            c: "c",
            h: "c",
            cpp: "cpp",
            hpp: "cpp",
            cc: "cpp",
            tcl: "tcl",
            py: "python",
            pdf: "pdf",
            txt: "text",
            md: "text",
          };
          return typeMap[ext || ""] || "text";
        };

        // Add all selected files
        const fileSet = fileSets[setIdx];
        const newFiles = message.files.map((filePath: string) => ({
          path: filePath,
          type: detectFileType(filePath),
        }));

        const updatedFiles = [...(fileSet.files || []), ...newFiles];
        const updated = [...fileSets];
        updated[setIdx] = { ...fileSet, files: updatedFiles };
        onUpdate(["fileSets"], updated);

        // Close the add file form
        setIsAddingFile(null);

        window.removeEventListener("message", handler);
      }
    };
    window.addEventListener("message", handler);
  };

  const handleAddFile = (setIdx: number) => {
    setIsAddingFile(setIdx);
    setFileDraft({ path: "", type: "verilog" });
  };

  const handleSaveFile = (setIdx: number) => {
    const fileSet = fileSets[setIdx];
    if (isAddingFile === setIdx) {
      const updatedFiles = [...(fileSet.files || []), fileDraft];
      const updated = [...fileSets];
      updated[setIdx] = { ...fileSet, files: updatedFiles };
      onUpdate(["fileSets"], updated);
    } else if (editingFile && editingFile.setIdx === setIdx) {
      const updatedFiles = [...(fileSet.files || [])];
      updatedFiles[editingFile.fileIdx] = fileDraft;
      const updated = [...fileSets];
      updated[setIdx] = { ...fileSet, files: updatedFiles };
      onUpdate(["fileSets"], updated);
    }
    setIsAddingFile(null);
    setEditingFile(null);
  };

  const handleDeleteFile = (setIdx: number, fileIdx: number) => {
    const fileSet = fileSets[setIdx];
    // Remove confirmation - webview confirm() may not work properly
    const updatedFiles = (fileSet.files || []).filter((_, i) => i !== fileIdx);
    const updated = [...fileSets];
    updated[setIdx] = { ...fileSet, files: updatedFiles };
    onUpdate(["fileSets"], updated);
  };

  return (
    <div className="p-6 space-y-4">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-medium">File Sets</h2>
          <p className="text-sm mt-1" style={{ opacity: 0.7 }}>
            {fileSets.length} file set{fileSets.length !== 1 ? "s" : ""}
          </p>
        </div>
        <button
          onClick={handleAddSet}
          disabled={isAddingSet || editingSet !== null}
          className="px-4 py-2 rounded text-sm flex items-center gap-2"
          style={{
            background:
              isAddingSet || editingSet !== null
                ? "var(--vscode-button-secondaryBackground)"
                : "var(--vscode-button-background)",
            color: "var(--vscode-button-foreground)",
            opacity: isAddingSet || editingSet !== null ? 0.5 : 1,
          }}
        >
          <span className="codicon codicon-add"></span>
          Add File Set
        </button>
      </div>

      <div className="space-y-3">
        {fileSets.map((fileSet, setIdx) => (
          <div
            key={setIdx}
            className="rounded overflow-hidden"
            style={{
              border: "1px solid var(--vscode-panel-border)",
              background: "var(--vscode-editor-background)",
            }}
          >
            {/* File Set Header */}
            <div
              className="px-4 py-3 flex items-center justify-between cursor-pointer"
              style={{
                background: "var(--vscode-sideBar-background)",
                borderBottom: expandedSets.has(setIdx)
                  ? "1px solid var(--vscode-panel-border)"
                  : "none",
              }}
              onClick={() => toggleSet(setIdx)}
            >
              <div className="flex items-center gap-3 flex-1">
                <span
                  className={`codicon codicon-chevron-${expandedSets.has(setIdx) ? "down" : "right"}`}
                ></span>
                <div>
                  <p className="font-medium text-sm">
                    {fileSet.name || "(imported)"}
                  </p>
                  {fileSet.description && (
                    <p className="text-xs mt-1" style={{ opacity: 0.7 }}>
                      {fileSet.description}
                    </p>
                  )}
                  {fileSet.import && (
                    <p
                      className="text-xs mt-1 font-mono"
                      style={{ opacity: 0.7 }}
                    >
                      Import: {fileSet.import}
                    </p>
                  )}
                </div>
              </div>
              <div
                className="flex items-center gap-2"
                onClick={(e) => e.stopPropagation()}
              >
                <span
                  className="text-xs px-2 py-1 rounded"
                  style={{
                    background: "var(--vscode-badge-background)",
                    color: "var(--vscode-badge-foreground)",
                  }}
                >
                  {fileSet.files?.length || 0} files
                </span>
                {!fileSet.import && (
                  <>
                    <button
                      onClick={() => {
                        setEditingSet(setIdx);
                        setSetDraft({
                          name: fileSet.name,
                          description: fileSet.description || "",
                        });
                      }}
                      className="p-1"
                      title="Edit"
                    >
                      <span className="codicon codicon-edit"></span>
                    </button>
                    <button
                      onClick={() => handleDeleteSet(setIdx)}
                      className="p-1"
                      style={{ color: "var(--vscode-errorForeground)" }}
                      title="Delete"
                    >
                      <span className="codicon codicon-trash"></span>
                    </button>
                  </>
                )}
              </div>
            </div>

            {/* Expanded Content */}
            {expandedSets.has(setIdx) && (
              <div className="p-4 space-y-3">
                {fileSet.import ? (
                  <div
                    className="p-4 rounded border-l-4"
                    style={{
                      background:
                        fileExistence[fileSet.import] === false
                          ? "rgba(255, 0, 0, 0.1)"
                          : "var(--vscode-editor-background)",
                      border: "1px solid var(--vscode-panel-border)",
                      borderLeftColor:
                        fileExistence[fileSet.import] === false
                          ? "var(--vscode-errorForeground)"
                          : "var(--vscode-textLink-foreground)",
                    }}
                  >
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center gap-2">
                        <h3 className="font-semibold">External File Set</h3>
                        {fileExistence[fileSet.import] === false && (
                          <span
                            className="text-xs px-1.5 py-0.5 rounded"
                            style={{
                              background: "var(--vscode-errorForeground)",
                              color: "white",
                            }}
                          >
                            Missing
                          </span>
                        )}
                      </div>
                    </div>

                    <p className="text-sm mb-4" style={{ opacity: 0.8 }}>
                      Linked file:{" "}
                      <code
                        className="px-1 py-0.5 rounded"
                        style={{
                          background: "var(--vscode-textBlockQuote-background)",
                        }}
                      >
                        {fileSet.import}
                      </code>
                    </p>

                    <button
                      onClick={() => handleOpenFile(fileSet.import!)}
                      className="px-4 py-2 rounded text-sm flex items-center gap-2"
                      style={{
                        background: "var(--vscode-button-background)",
                        color: "var(--vscode-button-foreground)",
                      }}
                    >
                      <span className="codicon codicon-go-to-file"></span>
