                                      ? "var(--vscode-errorForeground)"
                                      : undefined,
                                }}
                              ></span>
                              <span
                                className="text-sm font-mono truncate"
                                style={{
                                  cursor:
                                    fileExistence[file.path] !== false
                                      ? "pointer"
                                      : "not-allowed",
                                  color:
                                    fileExistence[file.path] === false
                                      ? "var(--vscode-errorForeground)"
                                      : "var(--vscode-textLink-foreground)",
                                  textDecoration:
                                    fileExistence[file.path] !== false
                                      ? "underline"
                                      : "line-through",
                                  opacity:
                                    fileExistence[file.path] === false
                                      ? 0.8
                                      : 1,
                                }}
                                onClick={() => handleOpenFile(file.path)}
                                title={
                                  fileExistence[file.path] === false
                                    ? `File not found: ${file.path}`
                                    : `Click to open: ${file.path}`
                                }
                              >
                                {file.path}
                              </span>
                              {fileExistence[file.path] === false && (
                                <span
                                  className="text-xs px-1.5 py-0.5 rounded"
                                  style={{
                                    background: "var(--vscode-errorForeground)",
                                    color: "white",
                                  }}
                                >
                                  Missing
                                </span>
                              )}
                              <span
                                className="text-xs px-2 py-0.5 rounded"
                                style={{
                                  background: "var(--vscode-badge-background)",
                                  color: "var(--vscode-badge-foreground)",
                                }}
                              >
                                {file.type}
                              </span>
                            </div>
                            <div className="flex items-center gap-2">
                              <button
                                onClick={() => {
                                  setEditingFile({ setIdx, fileIdx });
                                  setFileDraft(file);
                                }}
                                className="p-1"
                                title="Edit"
                              >
                                <span className="codicon codicon-edit"></span>
                              </button>
                              <button
                                onClick={() =>
                                  handleDeleteFile(setIdx, fileIdx)
                                }
                                className="p-1"
                                style={{
                                  color: "var(--vscode-errorForeground)",
                                }}
                                title="Delete"
                              >
                                <span className="codicon codicon-trash"></span>
                              </button>
                            </div>
                          </div>
                        );
                      })}

                      {/* Add File Row - Just browse button since it handles multiple files */}
                      {isAddingFile === setIdx && (
                        <div
                          className="flex items-center gap-2 p-3 rounded"
                          style={{
                            background:
                              "var(--vscode-list-activeSelectionBackground)",
                          }}
                        >
                          <button
                            onClick={() => handleBrowseFile(setIdx)}
                            className="flex-1 px-4 py-2 rounded text-sm flex items-center justify-center gap-2"
                            style={{
                              background: "var(--vscode-button-background)",
                              color: "var(--vscode-button-foreground)",
                            }}
                          >
                            <span className="codicon codicon-folder-opened"></span>
                            Browse and Add Files...
                          </button>
                          <button
                            onClick={() => setIsAddingFile(null)}
                            className="px-3 py-2 rounded text-sm"
                            style={{
                              background:
                                "var(--vscode-button-secondaryBackground)",
                              color: "var(--vscode-button-foreground)",
                            }}
                          >
                            Cancel
                          </button>
                        </div>
                      )}
                    </div>

                    {/* Add File Button */}
                    {isAddingFile !== setIdx && (
                      <button
                        onClick={() => handleAddFile(setIdx)}
                        disabled={editingFile !== null || isAddingFile !== null}
                        className="px-3 py-1.5 rounded text-sm flex items-center gap-2"
                        style={{
                          background:
                            editingFile !== null || isAddingFile !== null
                              ? "var(--vscode-button-secondaryBackground)"
                              : "var(--vscode-button-background)",
                          color: "var(--vscode-button-foreground)",
                          opacity:
                            editingFile !== null || isAddingFile !== null
                              ? 0.5
                              : 1,
                        }}
                      >
                        <span className="codicon codicon-add"></span>
                        Add File
                      </button>
                    )}
                  </>
                )}
              </div>
            )}
          </div>
        ))}

        {/* Add New File Set */}
        {isAddingSet && (
          <div
            className="p-4 rounded space-y-3"
            style={{
              border: "1px solid var(--vscode-panel-border)",
              background: "var(--vscode-list-activeSelectionBackground)",
            }}
          >
            <FormField
              label="Name"
              value={setDraft.name}
              onChange={(v: string) => setSetDraft({ ...setDraft, name: v })}
              placeholder="RTL_Sources"
              required
              validator={validateRequired}
              onSave={setDraft.name ? handleSaveSet : undefined}
              onCancel={() => setIsAddingSet(false)}
            />
            <TextAreaField
              label="Description"
              value={setDraft.description || ""}
              onChange={(v: string) =>
                setSetDraft({ ...setDraft, description: v })
              }
              placeholder="Optional description"
              rows={2}
              onSave={setDraft.name ? handleSaveSet : undefined}
              onCancel={() => setIsAddingSet(false)}
            />
            <div className="flex items-center gap-2">
              <button
                onClick={handleSaveSet}
                disabled={!setDraft.name}
                className="px-4 py-2 rounded text-sm"
                style={{
                  background: setDraft.name
                    ? "var(--vscode-button-background)"
                    : "var(--vscode-button-secondaryBackground)",
                  color: "var(--vscode-button-foreground)",
                  opacity: setDraft.name ? 1 : 0.5,
                }}
              >
                Create File Set
              </button>
              <button
                onClick={() => setIsAddingSet(false)}
                className="px-4 py-2 rounded text-sm"
                style={{
                  background: "var(--vscode-button-secondaryBackground)",
                  color: "var(--vscode-button-foreground)",
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        )}

        {/* Edit File Set */}
        {editingSet !== null && !isAddingSet && (
          <div
            className="p-4 rounded space-y-3"
            style={{
              border: "1px solid var(--vscode-panel-border)",
              background: "var(--vscode-list-activeSelectionBackground)",
            }}
          >
            <h3 className="font-semibold">Edit File Set</h3>
            <FormField
              label="Name"
              value={setDraft.name}
              onChange={(v: string) => setSetDraft({ ...setDraft, name: v })}
              placeholder="RTL_Sources"
              required
              validator={validateRequired}
              onSave={setDraft.name ? handleSaveSet : undefined}
              onCancel={() => setEditingSet(null)}
            />
            <TextAreaField
              label="Description"
              value={setDraft.description || ""}
              onChange={(v: string) =>
                setSetDraft({ ...setDraft, description: v })
              }
              placeholder="Optional description"
              rows={2}
              onSave={setDraft.name ? handleSaveSet : undefined}
              onCancel={() => setEditingSet(null)}
            />
            <div className="flex items-center gap-2">
              <button
                onClick={handleSaveSet}
                disabled={!setDraft.name}
                className="px-4 py-2 rounded text-sm"
                style={{
                  background: setDraft.name
                    ? "var(--vscode-button-background)"
                    : "var(--vscode-button-secondaryBackground)",
                  color: "var(--vscode-button-foreground)",
                  opacity: setDraft.name ? 1 : 0.5,
                }}
              >
                Save Changes
              </button>
              <button
                onClick={() => setEditingSet(null)}
                className="px-4 py-2 rounded text-sm"
                style={{
                  background: "var(--vscode-button-secondaryBackground)",
                  color: "var(--vscode-button-foreground)",
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        )}

        {fileSets.length === 0 && !isAddingSet && (
          <div className="p-8 text-center text-sm" style={{ opacity: 0.6 }}>
            No file sets defined. Click "Add File Set" to create one.
          </div>
        )}
      </div>
    </div>
  );
};

/**
 * GeneratorPanel - VHDL Code Generation UI
 *
 * Provides interface for generating VHDL files, vendor integration files,
 * and testbenches from IP Core definitions.
 */

import React, { useState, useCallback, useMemo, useRef, useEffect } from 'react';
import { IpCore, BusInterface } from '../../../types/ipCore';
import { vscode } from '../../../vscode';

interface GeneratorPanelProps {
  ipCore: IpCore;
}

type BusType = 'axil' | 'avmm';
type VendorType = 'none' | 'intel' | 'xilinx' | 'both';

interface GenerationOptions {
  busType: BusType;
  includeVhdl: boolean;
  includeRegfile: boolean;
  vendorFiles: VendorType;
  includeTestbench: boolean;
}

interface GenerationStatus {
  status: 'idle' | 'generating' | 'success' | 'error';
  message?: string;
  files?: string[];
}

interface DetectedBusInfo {
  busInterface: BusInterface;
  busType: BusType;
  memoryMapRef: string;
}

/**
 * Map bus interface type to generator bus type
 */
function mapBusType(interfaceType: string): BusType | null {
  if (!interfaceType) {
    return null;
  }
  const type = interfaceType.toLowerCase();

  // Check Avalon first (prioritize specific matches)
  if (type.includes('avalon') || type === 'avmm' || type.includes('avalon-mm')) {
    return 'avmm';
  }

  // Check AXI
  if (type.includes('axi') || type === 'axi4l' || type === 'axi4lite') {
    return 'axil';
  }

  return null;
}

/**
 * Detect bus interface with memory map reference
 */
function detectBusWithMemoryMap(ipCore: IpCore): DetectedBusInfo | null {
  const busInterfaces = ipCore.busInterfaces || [];

  for (const bus of busInterfaces) {
    // Check both camelCase (runtime) and snake_case (type def)
    const memMapRef = bus.memoryMapRef || bus.memory_map_ref;
    if (memMapRef) {
      const busType = mapBusType(bus.type);
      if (busType) {
        return {
          busInterface: bus,
          busType,
          memoryMapRef: memMapRef,
        };
      }
    }
  }

  return null;
}

export const GeneratorPanel: React.FC<GeneratorPanelProps> = ({ ipCore }) => {
  // Auto-detect bus type from memory map reference
  const detectedBus = useMemo(() => detectBusWithMemoryMap(ipCore), [ipCore]);

  // Get IP name for preview
  const ipName = ipCore.vlnv?.name?.toLowerCase() || 'ip_core';

  const [options, setOptions] = useState<GenerationOptions>(() => {
    const savedState = (vscode?.getState() as Record<string, unknown>) || {};
    return Object.keys(savedState).length > 0 && savedState.generationOptions
      ? (savedState.generationOptions as GenerationOptions)
      : {
          busType: detectedBus?.busType || 'axil',
          includeVhdl: true,
          includeRegfile: true,
          vendorFiles: 'none',
          includeTestbench: false,
        };
  });

  // Persist options state
  useEffect(() => {
    const currentState = (vscode?.getState() as Record<string, unknown>) || {};
    vscode?.setState({ ...currentState, generationOptions: options });
  }, [options]);

  const [status, setStatus] = useState<GenerationStatus>({ status: 'idle' });

  // Responsive layout - detect container width
  const containerRef = useRef<HTMLDivElement>(null);
  const [isWideLayout, setIsWideLayout] = useState(false);

  useEffect(() => {
    const checkWidth = () => {
      if (containerRef.current) {
        setIsWideLayout(containerRef.current.offsetWidth > 700);
      }
    };
    checkWidth();
    const resizeObserver = new ResizeObserver(checkWidth);
    if (containerRef.current) {
      resizeObserver.observe(containerRef.current);
    }
    return () => resizeObserver.disconnect();
  }, []);

  const handleGenerate = useCallback(() => {
    setStatus({ status: 'generating', message: 'Generating files...' });

    // Send message to extension to trigger generation
    vscode?.postMessage({
      type: 'generate',
      options: {
        busType: detectedBus?.busType || options.busType,
        includeVhdl: options.includeVhdl,
        includeRegfile: options.includeRegfile,
        vendorFiles: options.vendorFiles,
        includeTestbench: options.includeTestbench,
      },
    });
  }, [options, detectedBus]);

  // Listen for generation result messages
  React.useEffect(() => {
    const handleMessage = (event: MessageEvent) => {
      const message = event.data;
      if (message.type === 'generateResult') {
        if (message.success) {
          setStatus({
            status: 'success',
            message: `Generated ${String(message.files?.length || 0)} files`,
            files: message.files,
          });
        } else {
          setStatus({
            status: 'error',
            message: message.error || 'Generation failed',
          });
        }
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, []);

  const labelStyle: React.CSSProperties = {
    fontSize: '12px',
    fontWeight: 500,
    marginBottom: '4px',
    display: 'block',
  };

  const sectionStyle: React.CSSProperties = {
    marginBottom: '20px',
    padding: '12px',
    background: 'var(--vscode-input-background)',
    borderRadius: '4px',
    border: '1px solid var(--vscode-input-border)',
  };

  const checkboxRowStyle: React.CSSProperties = {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
    marginBottom: '8px',
    fontSize: '12px',
  };

  const infoStyle: React.CSSProperties = {
    fontSize: '11px',
    opacity: 0.8,
    marginTop: '4px',
  };

  return (
    <div ref={containerRef} style={{ padding: '16px' }}>
      <h2 style={{ fontSize: '16px', fontWeight: 600, marginBottom: '16px' }}>Project Scaffold</h2>

      <p style={{ fontSize: '12px', opacity: 0.8, marginBottom: '16px' }}>
        Generate VHDL files, vendor integration files, and testbenches for{' '}
        <strong>{ipCore.vlnv?.name}</strong>.
      </p>

      {/* Responsive layout wrapper */}
      <div
        style={{
          display: 'flex',
          flexDirection: isWideLayout ? 'row' : 'column',
          gap: '16px',
        }}
      >
        {/* Left column: Options */}
        <div style={{ flex: isWideLayout ? '1 1 50%' : '1 1 auto', minWidth: 0 }}>
          {/* Detected Bus Interface Section */}
          <div style={sectionStyle}>
            <label style={labelStyle}>Bus Interface</label>
            {detectedBus ? (
              <div>
                <div
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                    padding: '8px 12px',
                    background: 'var(--vscode-inputValidation-infoBackground)',
                    borderRadius: '4px',
                    fontSize: '12px',
                  }}
                >
                  <span style={{ fontWeight: 500 }}>
                    {detectedBus.busType === 'axil' ? '🔵 AXI-Lite' : '🟢 Avalon-MM'}
                  </span>
                  <span style={{ opacity: 0.7 }}>
                    (detected from {detectedBus.busInterface.name})
                  </span>
                </div>
                <p style={infoStyle}>
                  Memory Map: <code>{detectedBus.memoryMapRef}</code>
                </p>
              </div>
            ) : (
              <div
                style={{
                  padding: '8px 12px',
                  background: 'var(--vscode-inputValidation-warningBackground)',
                  borderRadius: '4px',
                  fontSize: '12px',
                }}
              >
                ⚠️ No bus interface with memory map detected. Using default: AXI-Lite
              </div>
            )}
          </div>

          {/* VHDL Files Section */}
          <div style={sectionStyle}>
            <label style={labelStyle}>VHDL Files</label>
            <label style={checkboxRowStyle}>
              <input
                type="checkbox"
                checked={options.includeVhdl}
                onChange={(e) =>
                  setOptions({
                    ...options,
                    includeVhdl: e.target.checked,
                    includeRegfile: e.target.checked,
                  })
                }
              />
              Package, Top, Core, Bus Wrapper, Register Bank
            </label>
          </div>

          {/* Vendor Integration Section */}
          <div style={sectionStyle}>
            <label style={labelStyle}>Vendor Integration</label>
            <select
              value={options.vendorFiles}
              onChange={(e) =>
                setOptions({
                  ...options,
                  vendorFiles: e.target.value as VendorType,
                })
              }
              style={{
                width: '100%',
                padding: '6px 8px',
                fontSize: '12px',
                background: 'var(--vscode-dropdown-background)',
                color: 'var(--vscode-dropdown-foreground)',
                border: '1px solid var(--vscode-dropdown-border)',
                borderRadius: '2px',
              }}
            >
              <option value="none">None</option>
              <option value="intel">Intel Platform Designer (_hw.tcl)</option>
              <option value="xilinx">Xilinx Vivado (component.xml)</option>
              <option value="both">Both Intel and Xilinx</option>
            </select>
          </div>

          {/* Testbench Section */}
          <div style={sectionStyle}>
            <label style={labelStyle}>Testbench</label>
            <label style={checkboxRowStyle}>
              <input
                type="checkbox"
                checked={options.includeTestbench}
                onChange={(e) => setOptions({ ...options, includeTestbench: e.target.checked })}
              />
              cocotb Python tests + Makefile (GHDL)
            </label>
          </div>
        </div>

        {/* Right column: Preview */}
        <div style={{ flex: isWideLayout ? '1 1 50%' : '1 1 auto', minWidth: 0 }}>
          {/* Preview Section */}
          <div
            style={{
              ...sectionStyle,
              background: 'var(--vscode-textBlockQuote-background)',
              fontFamily: 'monospace',
              fontSize: '11px',
            }}
          >
            <label style={labelStyle}>📁 Preview: Output Structure</label>
            <div style={{ marginTop: '8px', lineHeight: '1.6' }}>
              <div style={{ color: 'var(--vscode-textPreformat-foreground)' }}>{ipName}/</div>
              {options.includeVhdl && (
                <div style={{ paddingLeft: '16px' }}>
                  <div>├── rtl/</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>├── {ipName}_pkg.vhd</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>├── {ipName}.vhd</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>├── {ipName}_core.vhd</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>
                    ├── {ipName}_{detectedBus?.busType || 'axil'}.vhd
                  </div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>└── {ipName}_regs.vhd</div>
                </div>
              )}
              {options.includeTestbench && (
                <div style={{ paddingLeft: '16px' }}>
                  <div>├── tb/</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>├── {ipName}_test.py</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>└── Makefile</div>
                </div>
              )}
              {(options.vendorFiles === 'intel' || options.vendorFiles === 'both') && (
                <div style={{ paddingLeft: '16px' }}>
                  <div>├── intel/</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>└── {ipName}_hw.tcl</div>
                </div>
              )}
              {(options.vendorFiles === 'xilinx' || options.vendorFiles === 'both') && (
                <div style={{ paddingLeft: '16px' }}>
                  <div>└── xilinx/</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>├── component.xml</div>
                  <div style={{ paddingLeft: '24px', opacity: 0.8 }}>└── xgui/{ipName}_v*.tcl</div>
                </div>
              )}
            </div>
            <div style={{ marginTop: '8px', fontSize: '10px', opacity: 0.7 }}>
              {(() => {
                let count = 0;
                if (options.includeVhdl) {
                  count += 5;
                } // pkg, top, core, bus, regs
                if (options.vendorFiles === 'intel') {
                  count += 1;
                }
                if (options.vendorFiles === 'xilinx') {
                  count += 2;
                } // component.xml + xgui
                if (options.vendorFiles === 'both') {
                  count += 3;
                } // intel hw.tcl + xilinx 2 files
                if (options.includeTestbench) {
                  count += 2;
                }
                return `${count} file(s) will be generated`;
              })()}
            </div>
          </div>

          {/* Generate Button */}
          <button
            onClick={handleGenerate}
            disabled={status.status === 'generating'}
            style={{
              width: '100%',
              padding: '10px 16px',
              fontSize: '13px',
              fontWeight: 500,
              background: 'var(--vscode-button-background)',
              color: 'var(--vscode-button-foreground)',
              border: 'none',
              borderRadius: '4px',
              cursor: status.status === 'generating' ? 'wait' : 'pointer',
              opacity: status.status === 'generating' ? 0.7 : 1,
            }}
          >
            {status.status === 'generating' ? '⏳ Generating...' : '🔧 Generate Files'}
          </button>

          {/* Status Display */}
          {status.status === 'success' && (
            <div
              style={{
                marginTop: '16px',
                padding: '12px',
                background: 'var(--vscode-inputValidation-infoBackground)',
                borderRadius: '4px',
                fontSize: '12px',
              }}
            >
              <p style={{ fontWeight: 500, marginBottom: '8px' }}>✅ {status.message}</p>
              {status.files && (
                <ul style={{ margin: 0, paddingLeft: '16px' }}>
                  {status.files.map((file, idx) => (
                    <li key={idx}>{file}</li>
                  ))}
                </ul>
              )}
            </div>
          )}

          {status.status === 'error' && (
            <div
              style={{
                marginTop: '16px',
                padding: '12px',
                background: 'var(--vscode-inputValidation-errorBackground)',
                border: '1px solid var(--vscode-inputValidation-errorBorder)',
                borderRadius: '4px',
                fontSize: '12px',
                color: 'var(--vscode-errorForeground)',
              }}
            >
              ❌ {status.message}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

import React, { useCallback } from 'react';
import type { YamlUpdateHandler } from '../../../types/editor';
import { vscode } from '../../../vscode';

interface MemoryMapsEditorProps {
  memoryMaps: unknown;
  imports?: { memoryMaps?: unknown[] };
  onUpdate: YamlUpdateHandler;
}

export const MemoryMapsEditor: React.FC<MemoryMapsEditorProps> = ({
  memoryMaps,
  imports,
  onUpdate,
}) => {
  // Cast to a typed record for internal use
  const memMaps = memoryMaps as Record<string, unknown> | null;
  // Check if it's an import object (as per schema using 'import' keyword)
  // The memoryMaps field in the yaml is usually:
  // memoryMaps:
  //   import: "path/to/file"
  const importFile = memMaps?.import as string | undefined;
  const detectedMaps = (imports?.memoryMaps || []) as Record<string, unknown>[];

  const handleLinkFile = useCallback(() => {
    // Send message to extension to open file picker with filter
    vscode?.postMessage({
      type: 'selectFiles',
      multi: false, // Single file selection
      filters: { 'Memory Map': ['memmap.yml', 'yml'] },
    });

    // Listen for response
    const handler = (event: MessageEvent) => {
      const message = event.data;
      if (message.type === 'filesSelected' && message.files && message.files.length > 0) {
        // Update the memory map import
        // We update the whole memoryMaps object to be { import: "path" }
        // Use onUpdate(['memoryMaps'], { import: filePath })
        const filePath = message.files[0];
        onUpdate(['memoryMaps'], { import: filePath });

        window.removeEventListener('message', handler);
      }
    };
    window.addEventListener('message', handler);
  }, [onUpdate]);

  const handleUnlink = useCallback(() => {
    // Clear the memoryMaps object or set it to undefined/empty
    onUpdate(['memoryMaps'], undefined);
  }, [onUpdate]);

  return (
    <div className="p-6 space-y-4">
      <h2 className="text-xl font-medium">Memory Maps</h2>

      {importFile ? (
        <div className="space-y-4">
          <div
            className="p-4 rounded border-l-4"
            style={{
              background: 'var(--vscode-editor-background)',
              border: '1px solid var(--vscode-panel-border)',
              borderLeftColor: 'var(--vscode-textLink-foreground)',
            }}
          >
            <div className="flex items-center justify-between mb-4">
              <h3 className="font-semibold">External Memory Map</h3>
              <div className="flex items-center gap-2">
                <button
                  onClick={handleLinkFile}
                  className="p-1.5 rounded opacity-70 hover:opacity-100"
                  title="Change Linked File"
                >
                  <span className="codicon codicon-edit"></span>
                </button>
                <button
                  onClick={handleUnlink}
                  className="p-1.5 rounded opacity-70 hover:opacity-100"
                  style={{ color: 'var(--vscode-errorForeground)' }}
                  title="Unlink File"
                >
                  <span className="codicon codicon-close"></span>
                </button>
              </div>
            </div>

            <p className="text-sm mb-4" style={{ opacity: 0.8 }}>
              Linked file:{' '}
              <code
                className="px-1 py-0.5 rounded"
                style={{
                  background: 'var(--vscode-textBlockQuote-background)',
                }}
              >
                {importFile}
              </code>
            </p>

            <button
              onClick={() =>
                vscode?.postMessage({
                  type: 'command',
                  command: 'openFile',
                  path: importFile,
                })
              }
              className="px-4 py-2 rounded text-sm flex items-center gap-2"
              style={{
                background: 'var(--vscode-button-background)',
                color: 'var(--vscode-button-foreground)',
              }}
            >
              <span className="codicon codicon-go-to-file"></span>
              Open Memory Map Editor
            </button>
          </div>

          {/* Detected Memory Maps List */}
          <div>
            <h3 className="text-sm font-semibold mb-3">Detected Memory Maps</h3>
            {detectedMaps.length > 0 ? (
              <div className="space-y-3">
                {detectedMaps.map((map, idx: number) => (
                  <div
                    key={idx}
                    className="p-4 rounded border"
                    style={{
                      background: 'var(--vscode-editor-background)',
                      borderColor: 'var(--vscode-panel-border)',
                    }}
                  >
                    <div className="flex items-start justify-between">
                      <div>
                        <div className="flex items-center gap-2">
                          <span className="font-medium text-sm">{map.name as string}</span>
                          <span
                            className="text-xs px-2 py-0.5 rounded"
                            style={{
                              background: 'var(--vscode-badge-background)',
                              color: 'var(--vscode-badge-foreground)',
                            }}
                          >
                            {(map.addressBlocks as unknown[])?.length || 0} Block
                            {(map.addressBlocks as unknown[])?.length !== 1 ? 's' : ''}
                          </span>
                        </div>
                        {!!map.description && (
                          <p className="text-sm mt-1" style={{ opacity: 0.8 }}>
